import { defineComponentStyle } from "@yamada-ui/react"

export const badgeStyle = defineComponentStyle({
  base: {
    alignItems: "center",
    display: "inline-flex",
    fontWeight: "medium",
    rounded: "l1",
    textTransform: "uppercase",
    verticalAlign: "middle",
    whiteSpace: "nowrap",
  },

  props: {
    /**
     * If `true`, the button is full rounded. Else, it'll be slightly round.
     *
     * @default false
     */
    fullRounded: {
      true: {
        rounded: "full",
      },
    },
  },

  variants: {
    outline: {
      layerStyle: "outline",
    },
    solid: {
      layerStyle: "solid",
    },
    subtle: {
      layerStyle: "subtle",
    },
    surface: {
      layerStyle: "surface",
    },
  },

  sizes: {
    sm: {
      fontSize: "xs",
      lineHeight: "{sizes.5}",
      minH: "5",
      px: "1.5",
    },
    md: {
      fontSize: "sm",
      lineHeight: "{sizes.6}",
      minH: "6",
      px: "2",
    },
    lg: {
      fontSize: "md",
      lineHeight: "{sizes.7}",
      minH: "7",
      px: "2.5",
    },
  },

  defaultProps: {
    size: "sm",
    variant: "outline",
  },
})

export type BadgeStyle = typeof badgeStyle

"use client"

import type { HTMLStyledProps, ThemeProps } from "@yamada-ui/react"
import type { BadgeStyle } from "./badge.style"
import { createComponent } from "@yamada-ui/react"
import { badgeStyle } from "./badge.style"

export interface BadgeProps
  extends HTMLStyledProps<"span">,
    ThemeProps<BadgeStyle> {}

const {
  PropsContext: BadgePropsContext,
  usePropsContext: useBadgePropsContext,
  withContext,
} = createComponent<BadgeProps, BadgeStyle>("badge", badgeStyle)

export { BadgePropsContext, useBadgePropsContext }

/**
 * `Badge` is a component that emphasizes the status of an item to make it immediately recognizable.
 *
 * @see https://yamada-ui.com/docs/components/badge
 */
export const Badge = withContext("span")()
