import { defineComponentSlotStyle } from "@yamada-ui/react"

export const cardStyle = defineComponentSlotStyle({
  base: {
    body: {
      alignItems: "start",
      display: "flex",
      flex: "1",
      flexDirection: "column",
    },
    footer: {
      alignItems: "center",
      display: "flex",
      justifyContent: "start",
    },
    header: {
      alignItems: "center",
      display: "flex",
      justifyContent: "start",
    },
    root: {
      display: "flex",
      flexDirection: "column",
      overflowWrap: "break-word",
      rounded: "l2",
      w: "full",
    },
  },

  variants: {
    elevated: {
      root: {
        bg: "bg.panel",
        boxShadow: "md",
      },
    },
    outline: {
      root: {
        layerStyle: "outline",
      },
    },
    panel: {
      root: {
        layerStyle: "panel",
      },
    },
    solid: {
      root: {
        layerStyle: "solid",
      },
    },
    subtle: {
      root: {
        layerStyle: "subtle",
      },
    },
    surface: {
      root: {
        layerStyle: "surface",
      },
    },
  },

  sizes: {
    sm: {
      body: { gap: "3", px: "3", py: "2" },
      footer: { gap: "3", pb: "2", px: "3" },
      header: { fontSize: "lg", gap: "3", pt: "2", px: "3" },
    },
    md: {
      body: { gap: "4", px: "4", py: "3" },
      footer: { gap: "4", pb: "3", px: "4" },
      header: { fontSize: "xl", gap: "4", pt: "3", px: "4" },
    },
    lg: {
      body: { gap: "6", px: "6", py: "5" },
      footer: { gap: "6", pb: "5", px: "6" },
      header: { fontSize: "2xl", gap: "6", pt: "5", px: "6" },
    },
    xl: {
      body: { gap: "8", px: "8", py: "7" },
      footer: { gap: "8", pb: "7", px: "8" },
      header: { fontSize: "2xl", gap: "8", pt: "7", px: "8" },
    },
  },

  defaultProps: {
    size: "md",
    variant: "panel",
  },
})

export type CardStyle = typeof cardStyle

"use client"

import type { HTMLStyledProps, ThemeProps } from "@yamada-ui/react"
import type { CardStyle } from "./card.style"
import { createSlotComponent } from "@yamada-ui/react"
import { cardStyle } from "./card.style"

export interface CardRootProps
  extends HTMLStyledProps<"article">,
    ThemeProps<CardStyle> {}

const {
  PropsContext: CardPropsContext,
  usePropsContext: useCardPropsContext,
  withContext,
  withProvider,
} = createSlotComponent<CardRootProps, CardStyle>("card", cardStyle)

export { CardPropsContext, useCardPropsContext }

/**
 * `Card` is a component that groups and displays related information. By default, it renders a `article` element.
 *
 * @see https://yamada-ui.com/components/card
 */
export const CardRoot = withProvider("article", "root")()

export interface CardHeaderProps extends HTMLStyledProps<"header"> {}

export const CardHeader = withContext("header", "header")()

export interface CardBodyProps extends HTMLStyledProps {}

export const CardBody = withContext("div", "body")()

export interface CardFooterProps extends HTMLStyledProps<"footer"> {}

export const CardFooter = withContext("footer", "footer")()
