export * from "./circle-progress.style"
export {
  CircleProgressLabel,
  CircleProgressPropsContext,
  CircleProgressRoot,
  useCircleProgressPropsContext,
} from "./circle-progress"
export type {
  CircleProgressLabelProps,
  CircleProgressRootProps,
} from "./circle-progress"
export * as CircleProgress from "./namespace"

export {
  CircleProgressLabel as Label,
  CircleProgressPropsContext as PropsContext,
  CircleProgressRoot as Root,
  useCircleProgressPropsContext as usePropsContext,
} from "./circle-progress"
export type {
  CircleProgressLabelProps as LabelProps,
  CircleProgressRootProps as RootProps,
} from "./circle-progress"

import { defineComponentSlotStyle } from "@yamada-ui/react"

export const circleProgressStyle = defineComponentSlotStyle({
  base: {
    circle: {
      "--c": "calc(2 * 3.14159 * {r})",
      "--cx": "calc({size} / 2)",
      "--cy": "calc({size} / 2)",
      "--r": "calc({size} / 2 - {thickness} / 2)",
      boxSize: "{size}",
      _indeterminate: {
        animationDuration: "2s",
        animationIterationCount: "infinite",
        animationName: "spin",
        animationTimingFunction: "linear",
      },
    },
    label: {
      fontWeight: "medium",
      left: "50%",
      letterSpacing: "tight",
      lineHeight: "1",
      position: "absolute",
      textAlign: "center",
      top: "50%",
      transform: "translate(-50%, -50%)",
    },
    range: {
      cx: "{cx}",
      cy: "{cy}",
      fill: "transparent",
      r: "{r}",
      strokeDasharray: "{c}",
      strokeDashoffset: "calc({c} * ((100 - {percent}) / 100))",
      strokeWidth: "{thickness}",
      transform: "rotate(-90deg)",
      transformOrigin: "center center",
      transitionDuration: "slow",
      transitionProperty: "stroke-dashoffset, stroke-dasharray",
      _indeterminate: {
        animationDuration: "{duration, 1.5s}",
        animationIterationCount: "infinite",
        animationTimingFunction: "linear",
        keyframes: {
          "0%": {
            strokeDasharray: "1, 400",
            strokeDashoffset: "0",
          },
          "50%": {
            strokeDasharray: "400, 400",
            strokeDashoffset: "-100%",
          },
          "100%": {
            strokeDasharray: "400, 400",
            strokeDashoffset: "-260%",
          },
        },
      },
    },
    root: {
      display: "inline-flex",
      position: "relative",
    },
    track: {
      cx: "{cx}",
      cy: "{cy}",
      fill: "transparent",
      r: "{r}",
      strokeWidth: "{thickness}",
    },
  },

  props: {
    /**
     * The shape of the progress bar.
     *
     * @default rounded
     */
    shape: {
      rounded: {
        range: {
          strokeLinecap: "round",
          _indeterminate: { strokeLinecap: "butt" },
        },
      },
      square: {
        range: { strokeLinecap: "butt" },
      },
    },
  },

  variants: {
    outline: {
      range: {
        stroke: "colorScheme.solid",
      },
      track: {
        stroke: "bg.subtle",
      },
    },
    subtle: {
      range: {
        stroke: "colorScheme.solid/80",
      },
      track: {
        stroke: ["colorScheme.muted", "colorScheme.subtle"],
      },
    },
  },

  sizes: {
    xs: {
      label: { fontSize: "2xs" },
      root: {
        "--size": "{sizes.6}",
        "--thickness": "{sizes.1}",
      },
    },
    sm: {
      label: { fontSize: "2xs" },
      root: {
        "--size": "{sizes.8}",
        "--thickness": "{sizes.1.5}",
      },
    },
    md: {
      label: { fontSize: "2xs" },
      root: {
        "--size": "{sizes.10}",
        "--thickness": "{sizes.1.5}",
      },
    },
    lg: {
      label: { fontSize: "xs" },
      root: {
        "--size": "{sizes.12}",
        "--thickness": "{sizes.2}",
      },
    },
    xl: {
      label: { fontSize: "sm" },
      root: {
        "--size": "{sizes.14}",
        "--thickness": "{sizes.2}",
      },
    },
  },

  defaultProps: {
    size: "md",
    variant: "outline",
    shape: "rounded",
  },
})

export type CircleProgressStyle = typeof circleProgressStyle

"use client"

import type { CSSProps, HTMLStyledProps, ThemeProps } from "@yamada-ui/react"
import type { UseProgressProps } from "../progress"
import type { CircleProgressStyle } from "./circle-progress.style"
import {
  createSlotComponent,
  mergeCSS,
  styled,
  varAttr,
  isNumber,
} from "@yamada-ui/react"
import { useProgress } from "../progress"
import { circleProgressStyle } from "./circle-progress.style"

export interface CircleProgressRootProps
  extends HTMLStyledProps,
    UseProgressProps,
    ThemeProps<CircleProgressStyle> {
  /**
   * The animation duration in seconds.
   */
  duration?: number | string
  /**
   * The color of the progress range.
   */
  rangeColor?: CSSProps["color"]
  /**
   * The thickness of the progress bar.
   */
  thickness?: CSSProps["width"]
  /**
   * The color of the progress track.
   */
  trackColor?: CSSProps["color"]
  /**
   * Props for the progress circle element.
   */
  circleProps?: CircleProgressCircleProps
  /**
   * Props for the progress range element.
   */
  rangeProps?: CircleProgressRangeProps
  /**
   * Props for the progress track element.
   */
  trackProps?: CircleProgressTrackProps
}

const {
  PropsContext: CircleProgressPropsContext,
  usePropsContext: useCircleProgressPropsContext,
  withContext,
  withProvider,
} = createSlotComponent<CircleProgressRootProps, CircleProgressStyle>(
  "circle-progress",
  circleProgressStyle,
)

export { CircleProgressPropsContext, useCircleProgressPropsContext }

/**
 * `CircleProgress` is a component that displays progress in a circular progress bar.
 *
 * @see https://yamada-ui.com/components/circle-progress
 */
export const CircleProgressRoot = withProvider<"div", CircleProgressRootProps>(
  ({
    css,
    children,
    max,
    min,
    rangeColor,
    trackColor,
    value,
    circleProps,
    rangeProps,
    trackProps,
    ...rest
  }) => {
    const { percent, getRangeProps, getRootProps, getTrackProps } =
      useProgress<"svg">({ max, min, value })

    return (
      <styled.div css={mergeCSS(css, { "--percent": percent })} {...rest}>
        <CircleProgressCircle {...getRootProps(circleProps)}>
          <CircleProgressTrack
            stroke={trackColor}
            {...getTrackProps(trackProps)}
          />
          <CircleProgressRange
            stroke={rangeColor}
            {...getRangeProps(rangeProps)}
          />
        </CircleProgressCircle>

        {children}
      </styled.div>
    )
  },
  "root",
)(({ boxSize, duration, thickness, ...rest }) => ({
  "--duration": isNumber(duration) ? `${duration}s` : duration,
  "--size": varAttr(boxSize, "sizes"),
  "--thickness": varAttr(thickness, "sizes"),
  ...rest,
}))

interface CircleProgressCircleProps extends HTMLStyledProps<"svg"> {}

const CircleProgressCircle = withContext<"svg", CircleProgressCircleProps>(
  "svg",
  "circle",
)()

interface CircleProgressTrackProps extends HTMLStyledProps<"circle"> {}

const CircleProgressTrack = withContext<"circle", CircleProgressTrackProps>(
  "circle",
  "track",
)()

interface CircleProgressRangeProps extends HTMLStyledProps<"circle"> {}

const CircleProgressRange = withContext<"circle", CircleProgressRangeProps>(
  "circle",
  "range",
)()

export interface CircleProgressLabelProps extends HTMLStyledProps<"span"> {}

export const CircleProgressLabel = withContext<
  "span",
  CircleProgressLabelProps
>("span", "label")()
