import type { Component, CSSProps } from "@yamada-ui/react"
import type { ColorSwatchProps } from "./color-swatch"
import { Grid } from "../grid"
import { ColorSwatch } from "./color-swatch"

export interface ColorSwatchGroupProps extends Omit<ColorSwatchProps, "color"> {
  /**
   * The color used for the swatch element.
   *
   * @default "#ffffff00"
   */
  items?: CSSProps["color"][]
  /**
   * The props for the `ColorSwatch` component.
   */
  itemProps?: ColorSwatchProps
}

/**
 * `ColorSwatchGroup` is a component that displays color samples.
 *
 * @see https://yamada-ui.com/components/color-swatch
 */
export const ColorSwatchGroup = (({
  items = [],
  layers,
  withShadow = true,
  itemProps,
  ...rest
}: ColorSwatchGroupProps) => {
  if (items.length > 4)
    console.warn("ColorSwatchMix: doesn't support more than 4 items")

  const empty = items.length === 0
  const threeColors = items.length === 3

  if (empty) {
    return (
      <ColorSwatch
        aria-label="color swatch group"
        layers={layers}
        overflow="hidden"
        withShadow={withShadow}
        {...rest}
      />
    )
  } else {
    return (
      <ColorSwatch
        aria-label="color swatch group"
        layers={withShadow ? [{ boxShadow: "inner" }] : []}
        {...rest}
      >
        <Grid templateColumns="repeat(2, 1fr)">
          {items.map((color, index) => (
            <ColorSwatch
              key={index}
              boxSize="inherit"
              color={color}
              gridColumn={threeColors && !index ? "1 / 3" : undefined}
              rounded="0"
              w={threeColors && !index ? "unset" : undefined}
              withShadow={false}
              {...itemProps}
            />
          ))}
        </Grid>
      </ColorSwatch>
    )
  }
}) as Component<"div", ColorSwatchGroupProps>

import { defineComponentSlotStyle } from "@yamada-ui/react"

export const colorSwatchStyle = defineComponentSlotStyle({
  base: {
    overlay: {
      boxSize: "full",
      inset: 0,
      position: "absolute",
    },
    root: {
      "& > *": {
        alignItems: "center",
        boxSize: "full",
        display: "flex",
        inset: 0,
        justifyContent: "center",
        overflow: "hidden",
        position: "absolute",
      },
      "--checkers-bg": ["colors.whiteAlpha.500", "colors.blackAlpha.500"],
      "--checkers-fg": ["colors.blackAlpha.300", "colors.whiteAlpha.300"],
      overflow: "hidden",
      position: "relative",
    },
  },

  sizes: {
    "2xs": {
      root: { boxSize: "6" },
    },
    xs: {
      root: { boxSize: "8" },
    },
    sm: {
      root: { boxSize: "9" },
    },
    md: {
      root: { boxSize: "10" },
    },
    lg: {
      root: { boxSize: "11" },
    },
    xl: {
      root: { boxSize: "12" },
    },
    "2xl": {
      root: { boxSize: "14" },
    },
  },

  variants: {
    circle: {
      root: { rounded: "full" },
    },
    plain: {
      root: { rounded: "l2" },
    },
    square: {
      root: { rounded: "0" },
    },
  },

  defaultProps: {
    size: "xs",
    variant: "plain",
  },
})

export type ColorSwatchStyle = typeof colorSwatchStyle

"use client"

import type { CSSProps, HTMLStyledProps, ThemeProps } from "@yamada-ui/react"
import type { ColorSwatchStyle } from "./color-swatch.style"
import { createSlotComponent, styled, isString } from "@yamada-ui/react"
import { colorSwatchStyle } from "./color-swatch.style"

export const checkersProps: HTMLStyledProps = {
  bgImage: [
    "linear-gradient(45deg, {checkers-fg} 25%, transparent 25%)",
    "linear-gradient(-45deg, {checkers-fg} 25%, transparent 25%)",
    "linear-gradient(45deg, transparent 75%, {checkers-fg} 75%)",
    "linear-gradient(-45deg, {checkers-bg} 75%, {checkers-fg} 75%)",
  ].join(", "),
  bgPosition: `0 0, 0 4px, 4px -4px, -4px 0`,
  bgSize: `8px 8px`,
}

const defaultLayers = (
  background: CSSProps["color"],
  withShadow: boolean,
): HTMLStyledProps[] => {
  const layers: HTMLStyledProps[] = [checkersProps, { background }]

  if (withShadow) layers.push({ boxShadow: "inner" })

  return layers
}

export interface ColorSwatchProps
  extends Omit<HTMLStyledProps, "color">,
    ThemeProps<ColorSwatchStyle> {
  /**
   * The color used for the swatch element.
   *
   * @default "#ffffff00"
   */
  color?: CSSProps["color"]
  /**
   * The overlay used for the swatch element.
   */
  layers?: HTMLStyledProps[]
  /**
   * If `true`, the swatch element has an inner `box-shadow`.
   *
   * @default true
   */
  withShadow?: boolean
}

const {
  PropsContext: ColorSwatchPropsContext,
  usePropsContext: useColorSwatchPropsContext,
  withContext,
  withProvider,
} = createSlotComponent<ColorSwatchProps, ColorSwatchStyle>(
  "color-swatch",
  colorSwatchStyle,
)

export { ColorSwatchPropsContext, useColorSwatchPropsContext }

/**
 * `ColorSwatch` is a component that displays color samples.
 *
 * @see https://yamada-ui.com/components/color-swatch
 */
export const ColorSwatch = withProvider<"div", ColorSwatchProps>(
  ({
    children,
    color = "#ffffff00",
    withShadow = true,
    layers = defaultLayers(color, withShadow),
    ...rest
  }) => {
    return (
      <styled.div
        aria-label={isString(color) ? color : undefined}
        aria-roledescription="color swatch"
        role="img"
        {...rest}
      >
        {children}
        {layers.map((props, index) => (
          <ColorSwatchOverlay key={index} {...props} />
        ))}
      </styled.div>
    )
  },
  "root",
)()

export interface ColorSwatchOverlayProps extends HTMLStyledProps {}

export const ColorSwatchOverlay = withContext<"div", ColorSwatchOverlayProps>(
  "div",
  "overlay",
)()
