import { defineComponentStyle } from "@yamada-ui/react"
import { buttonStyle } from "../button"

export const fileButtonStyle = defineComponentStyle({
  base: {
    "--error-border-color": "colors.border.error",
  },

  variants: {
    ghost: {
      ...buttonStyle.variants?.ghost,
      _invalid: {
        borderColor: "{error-border-color}",
        borderWidth: "1px",
      },
    },
    outline: {
      ...buttonStyle.variants?.outline,
      _invalid: {
        borderColor: "{error-border-color}",
      },
    },
    solid: {
      ...buttonStyle.variants?.solid,
      _invalid: {
        borderColor: "{error-border-color}",
        borderWidth: "1px",
      },
    },
    subtle: {
      ...buttonStyle.variants?.subtle,
      _invalid: {
        borderColor: "{error-border-color}",
        borderWidth: "1px",
      },
    },
    surface: {
      ...buttonStyle.variants?.surface,
      _invalid: {
        borderColor: "{error-border-color}",
      },
    },
  },

  defaultProps: {
    variant: "solid",
  },
})

export type FileButtonStyle = typeof fileButtonStyle

"use client"

import type { ThemeProps } from "@yamada-ui/react"
import type { ButtonProps } from "../button"
import type { UseInputBorderProps } from "../input"
import type { FileButtonStyle } from "./file-button.style"
import type { UseFileButtonProps } from "./use-file-button"
import { createComponent, styled } from "@yamada-ui/react"
import { Button } from "../button"
import { useInputBorder } from "../input"
import { Portal } from "../portal"
import { fileButtonStyle } from "./file-button.style"
import { useFileButton } from "./use-file-button"

export interface FileButtonProps
  extends Omit<ButtonProps, "defaultValue" | "onChange" | "ref" | "value">,
    UseFileButtonProps,
    Pick<UseInputBorderProps, "errorBorderColor">,
    ThemeProps<FileButtonStyle> {}

const {
  PropsContext: FileButtonPropsContext,
  usePropsContext: useFileButtonPropsContext,
  withContext,
} = createComponent<FileButtonProps, FileButtonStyle>(
  "file-button",
  fileButtonStyle,
)

export { FileButtonPropsContext, useFileButtonPropsContext }

/**
 * `FileButton` is a button component used for users to select files.
 *
 * @see https://yamada-ui.com/docs/components/file-button
 */
export const FileButton = withContext<"input", FileButtonProps>(
  ({ as: As = Button, children, errorBorderColor, ...rest }) => {
    const { getButtonProps, getInputProps } = useFileButton(rest)
    const varProps = useInputBorder({ errorBorderColor })

    return (
      <>
        <Portal>
          <styled.input {...getInputProps()} />
        </Portal>

        <As {...varProps} {...getButtonProps()}>
          {children}
        </As>
      </>
    )
  },
)()

"use client"

import type { PropGetter } from "@yamada-ui/react"
import type { FieldProps } from "../field"
import type { UseFileInputProps } from "../file-input"
import { useCallback } from "react"
import { ariaAttr } from "@yamada-ui/react"
import { useFileInput } from "../file-input"

export interface UseFileButtonProps
  extends UseFileInputProps<"button">,
    FieldProps {}

export const useFileButton = (props: UseFileButtonProps = {}) => {
  const { interactive, clickableProps, getInputProps } =
    useFileInput<"button">(props)

  const getButtonProps: PropGetter<"button"> = useCallback(
    (props) => ({
      ...clickableProps,
      "aria-disabled": ariaAttr(!interactive),
      tabIndex: interactive ? clickableProps.tabIndex : -1,
      ...props,
    }),
    [clickableProps, interactive],
  )

  return {
    clickableProps,
    getButtonProps,
    getInputProps,
  }
}

export type UseFileButtonReturn = ReturnType<typeof useFileButton>
