import { defineComponentSlotStyle } from "@yamada-ui/react"
import { getInputPaddingResetStyle, inputStyle } from "../input"

export const fileInputStyle = defineComponentSlotStyle({
  base: {
    root: {
      ...inputStyle.base,
      alignItems: "center",
      cursor: "pointer",
      display: "flex",
      flexWrap: "wrap",
      gapX: "1",
      lineHeight: "1",
      _readOnly: {
        layerStyle: "readOnly",
      },
    },
    tag: {
      display: "inline-block",
      overflow: "hidden",
      whiteSpace: "nowrap",
    },
  },

  variants: {
    filled: {
      root: inputStyle.variants?.filled,
    },
    flushed: {
      root: {
        ...inputStyle.variants?.flushed,
        ...getInputPaddingResetStyle(),
      },
    },
    outline: {
      root: inputStyle.variants?.outline,
    },
    plain: {
      root: inputStyle.variants?.plain,
    },
  },

  sizes: {
    xs: {
      root: { py: "{--input-space-y}", ...inputStyle.sizes?.xs },
    },
    sm: {
      root: { py: "{--input-space-y}", ...inputStyle.sizes?.sm },
    },
    md: {
      root: { py: "{--input-space-y}", ...inputStyle.sizes?.md },
    },
    lg: {
      root: { py: "{--input-space-y}", ...inputStyle.sizes?.lg },
    },
    xl: {
      root: { py: "{--input-space-y}", ...inputStyle.sizes?.xl },
    },
    "2xl": {
      root: { py: "{--input-space-y}", ...inputStyle.sizes?.["2xl"] },
    },
  },

  defaultProps: {
    size: "md",
    variant: "outline",
  },
})

export type FileInputStyle = typeof fileInputStyle

"use client"

import type { ReactNode } from "react"
import type { HTMLStyledProps, ThemeProps } from "@yamada-ui/react"
import type { FieldProps } from "../field"
import type { UseInputBorderProps } from "../input"
import type { FileInputStyle } from "./file-input.style"
import type { UseFileInputProps } from "./use-file-input"
import { cloneElement, isValidElement, useMemo } from "react"
import { createSlotComponent, styled } from "@yamada-ui/react"
import { useInputBorder, useInputPropsContext } from "../input"
import { Portal } from "../portal"
import { fileInputStyle } from "./file-input.style"
import { useFileInput } from "./use-file-input"

const defaultFormat: (value: File, index: number) => string = ({ name }) => name

export interface FileInputProps
  extends Omit<
      HTMLStyledProps<"input">,
      "children" | "defaultValue" | "onChange" | "value"
    >,
    ThemeProps<FileInputStyle>,
    UseInputBorderProps,
    FieldProps,
    Omit<UseFileInputProps, "children"> {
  /**
   * A callback that returns a React node.
   */
  children?: (files: File[] | undefined) => ReactNode
  /**
   * The component that displays uploaded files.
   */
  component?: (props: { index: number; value: File }) => ReactNode
  /**
   * A callback that formats the name of the uploaded file.
   */
  format?: (value: File, index: number) => string
  /**
   * The string to separate uploaded files.
   *
   * @default ','
   */
  separator?: string
}

const {
  PropsContext: FileInputPropsContext,
  usePropsContext: useFileInputPropsContext,
  withContext,
  withProvider,
} = createSlotComponent<FileInputProps, FileInputStyle>(
  "file-input",
  fileInputStyle,
)

export { FileInputPropsContext, useFileInputPropsContext }

/**
 * `FileInput` is a component used for users to select files.
 *
 * @see https://yamada-ui.com/components/file-input
 */
export const FileInput = withProvider<"input", FileInputProps>(
  ({
    children,
    component,
    errorBorderColor,
    focusBorderColor,
    format = defaultFormat,
    placeholder,
    separator = ",",
    ...props
  }) => {
    const varProps = useInputBorder({ errorBorderColor, focusBorderColor })
    const { values, getFieldProps, getInputProps } = useFileInput(props)

    const cloneChildren = useMemo(() => {
      if (!values?.length)
        return (
          <styled.span overflow="hidden" whiteSpace="nowrap">
            {placeholder}
          </styled.span>
        )

      if (children) return children(values)

      return values.map((value, index) => {
        const last = values.length === index + 1

        if (component) {
          const node = component({ index, value })

          if (isValidElement(node)) {
            return cloneElement(node, { key: index })
          } else {
            return node
          }
        } else {
          return (
            <FileInputTag key={index}>
              {format(value, index)}
              {!last ? separator : null}
            </FileInputTag>
          )
        }
      })
    }, [children, format, placeholder, separator, component, values])

    return (
      <>
        <Portal>
          <styled.input {...getInputProps()} />
        </Portal>

        <styled.div {...varProps} {...getFieldProps()}>
          {cloneChildren}
        </styled.div>
      </>
    )
  },
  "root",
)((props) => {
  const context = useInputPropsContext()

  return { ...context, ...props }
})

interface FileInputTagProps extends HTMLStyledProps {}

const FileInputTag = withContext<"span", FileInputTagProps>("span", "tag")()
