import { defineComponentStyle } from "@yamada-ui/react"

export const groupStyle = defineComponentStyle({
  base: {
    "& > *": {
      _focusVisible: {
        zIndex: "yamcha",
      },
    },
    display: "inline-flex",
    gap: "sm",
    isolation: "isolate",
    justifyContent: "start",
    position: "relative",
  },

  props: {
    /**
     * If `true`, the borderRadius of button that are direct children will be altered to look flushed together.
     *
     * @default false
     */
    attached: {
      true: {
        gap: "0px !important",
      },
    },
    /**
     * If `true`, the children will grow to fill the available space.
     *
     * @default false
     */
    grow: {
      true: {
        "& > *": {
          flex: 1,
        },
        display: "flex",
      },
    },
    /**
     * The orientation of the group.
     *
     * @default "horizontal"
     */
    orientation: {
      horizontal: {
        flexDirection: "row",
      },
      vertical: {
        flexDirection: "column",
      },
    },
    /**
     * The stacking order of the group.
     */
    stacking: {
      "first-on-top": {
        "& > *": {
          zIndex: "calc({--group-count} - {--group-index})",
        },
      },
      "last-on-top": {
        "& > *": {
          zIndex: "{--group-index}",
        },
      },
    },
  },

  compounds: [
    {
      css: {
        "& > *[data-group-between]": {
          "& > *[data-group-propagate]": {
            borderRightWidth: "0px !important",
            rounded: "0px !important",
          },
          borderRightWidth: "0px !important",
          rounded: "0px !important",
        },
        "& > *[data-group-end]:not([data-group-start])": {
          "& > *[data-group-propagate]": {
            roundedLeft: "0px !important",
          },
          roundedLeft: "0px !important",
        },
        "& > *[data-group-start]:not([data-group-end])": {
          "& > *[data-group-propagate]": {
            borderRightWidth: "0px !important",
            roundedRight: "0px !important",
          },
          borderRightWidth: "0px !important",
          roundedRight: "0px !important",
        },
      },
      attached: true,
      orientation: "horizontal",
    },
    {
      css: {
        "& > *[data-group-between]": {
          borderTopWidth: "0px !important",
          rounded: "0px !important",
        },
        "& > *[data-group-end]:not([data-group-start])": {
          borderTopWidth: "0px !important",
          roundedTop: "0px !important",
        },
        "& > *[data-group-start]:not([data-group-end])": {
          roundedBottom: "0px !important",
        },
      },
      attached: true,
      orientation: "vertical",
    },
  ],

  defaultProps: {
    orientation: "horizontal",
  },
})

export type GroupStyle = typeof groupStyle

"use client"

import type { ThemeProps } from "@yamada-ui/react"
import type { FlexProps } from "../flex"
import type { GroupStyle } from "./group.style"
import { createComponent, dataAttr } from "@yamada-ui/react"
import { useValue } from "../../hooks/use-value"
import { Flex } from "../flex"
import { groupStyle } from "./group.style"
import { useGroup } from "./use-group"

export interface GroupProps
  extends Omit<FlexProps, "grow">,
    ThemeProps<GroupStyle> {}

const {
  PropsContext: GroupPropsContext,
  usePropsContext: useGroupPropsContext,
  withContext,
} = createComponent<GroupProps, GroupStyle>("group", groupStyle)

export { GroupPropsContext, useGroupPropsContext }

/**
 * `Group` is a component that groups and attaches multiple elements together.
 *
 * @see https://yamada-ui.com/components/group
 */
export const Group = withContext<"div", GroupProps, "attached" | "orientation">(
  Flex,
  { transferProps: ["attached", "orientation"] },
)(
  undefined,
  ({
    attached: attachedProp,
    children,
    orientation: orientationProp,
    ...rest
  }) => {
    const orientation = useValue(orientationProp)
    const attached = useValue(attachedProp)
    const cloneChildren = useGroup(children)

    return {
      "data-attached": dataAttr(attached),
      "data-orientation": orientation,
      children: cloneChildren,
      role: "group",
      ...rest,
    }
  },
)

import type { CSSProperties, DataAttributes, ReactNode } from "react"
import { cloneElement, useMemo } from "react"
import {
  dataAttr,
  isNull,
  isUndefined,
  useValidChildren,
} from "@yamada-ui/react"

export const useGroup = (children?: ReactNode) => {
  const validChildren = useValidChildren(children)
  const omittedChildren = useMemo(
    () =>
      validChildren.filter((child) => {
        const ungrouped = child.props["data-ungrouped"]

        return isUndefined(ungrouped) || isNull(ungrouped)
      }),
    [validChildren],
  )
  const count = omittedChildren.length
  const cloneChildren = useMemo(
    () =>
      validChildren.map((child) => {
        const index = omittedChildren.indexOf(child)
        const ungrouped = index === -1

        if (ungrouped) return child

        return cloneElement(child, {
          ...child.props,
          style: {
            "--group-count": count,
            "--group-index": index,
            ...(child.props?.style ?? {}),
          },
          "data-group-between": dataAttr(index > 0 && index < count - 1),
          "data-group-end": dataAttr(index === count - 1),
          "data-group-start": dataAttr(index === 0),
          "data-peer": "",
        })
      }),
    [validChildren, omittedChildren, count],
  )

  return cloneChildren
}

export type UseGroupReturn = ReturnType<typeof useGroup>

interface Props extends DataAttributes {
  style?: CSSProperties
}

export const useGroupItemProps = <Y extends Props>({
  style: {
    "--group-count": groupCount,
    "--group-index": groupIndex,
    ...style
  } = {},
  "data-group-between": dataGroupBetween,
  "data-group-end": dataGroupEnd,
  "data-group-start": dataGroupStart,
  "data-peer": dataPeer,
  ...rest
}: Y) => {
  const pickedProps = useMemo(
    () => ({
      style: {
        "--group-count": groupCount!,
        "--group-index": groupIndex!,
      },
      "data-group-between": dataGroupBetween,
      "data-group-end": dataGroupEnd,
      "data-group-start": dataGroupStart,
      "data-peer": dataPeer,
    }),
    [
      groupCount,
      groupIndex,
      dataGroupBetween,
      dataGroupEnd,
      dataGroupStart,
      dataPeer,
    ],
  )
  const omittedProps = useMemo(() => ({ style, ...rest }), [style, rest])

  return [pickedProps, omittedProps] as const
}

export type UseGroupItemReturn = ReturnType<typeof useGroupItemProps>
