import { defineComponentSlotStyle } from "@yamada-ui/react"
import { sliderStyle } from "../slider"

export const hueSliderStyle = defineComponentSlotStyle({
  base: {
    overlay: {
      position: "absolute",
      rounded: "{track-rounded}",
      top: "0",
      _horizontal: {
        h: "full",
        left: "calc({thumb-size} / 2 * -1)",
        w: "calc(full + {thumb-size})",
      },
      _vertical: {
        h: "calc(full + {thumb-size})",
        top: "calc({thumb-size} / 2 * -1)",
        w: "full",
      },
    },
    root: {},
    thumb: {
      "--thumb-stroke": "colors.white",
      borderWidth: "3px",
      boxShadow: "md",
    },
    track: {
      flex: "inherit",
      _horizontal: {
        ms: "calc({thumb-size} / 2)",
        w: "calc(full - {thumb-size})",
      },
      _vertical: {
        h: "calc(full - {thumb-size})",
        mt: "calc({thumb-size} / 2)",
      },
    },
  },

  props: {
    /**
     * The shape of the thumb.
     *
     * @default 'circle'
     */
    shape: {
      circle: {
        root: {
          ...sliderStyle.props?.shape.circle.root,
          "--track-rounded": "{thumb-rounded}",
        },
      },
      rounded: {
        root: {
          ...sliderStyle.props?.shape.rounded.root,
          "--track-rounded": "{thumb-rounded}",
        },
      },
      square: {
        root: {
          ...sliderStyle.props?.shape.square.root,
          "--track-rounded": "{thumb-rounded}",
        },
      },
    },
  },

  variants: {},

  sizes: {
    sm: {
      root: {
        "--thumb-size": sliderStyle.sizes?.sm.root["--thumb-size"],
        "--track-size": sliderStyle.sizes?.sm.root["--thumb-size"],
      },
    },
    md: {
      root: {
        "--thumb-size": sliderStyle.sizes?.md.root["--thumb-size"],
        "--track-size": sliderStyle.sizes?.md.root["--thumb-size"],
      },
    },
    lg: {
      root: {
        "--thumb-size": sliderStyle.sizes?.lg.root["--thumb-size"],
        "--track-size": sliderStyle.sizes?.lg.root["--thumb-size"],
      },
    },
  },

  defaultProps: {
    size: "md",
    shape: "circle",
  },
})

export type HueSliderStyle = typeof hueSliderStyle

"use client"

import type {
  HTMLStyledProps,
  ThemeProps,
  WithoutThemeProps,
} from "@yamada-ui/react"
import type { HueSliderStyle } from "./hue-slider.style"
import { useCallback, useMemo } from "react"
import { createSlotComponent, styled } from "@yamada-ui/react"
import { useValue } from "../../hooks/use-value"
import { useI18n } from "../../providers/i18n-provider"
import { Slider } from "../slider"
import { hueSliderStyle } from "./hue-slider.style"

interface ComponentContext
  extends Pick<Required<HueSliderRootProps>, "max" | "min" | "orientation"> {}

export interface HueSliderRootProps
  extends WithoutThemeProps<
      Omit<
        Slider.RootProps,
        | "betweenThumbs"
        | "marks"
        | "marksProps"
        | "max"
        | "rangeFill"
        | "rangeProps"
        | "thumbFill"
        | "thumbProps"
        | "trackFill"
        | "trackProps"
      >,
      HueSliderStyle
    >,
    ThemeProps<HueSliderStyle> {
  /**
   * The maximum allowed value of the slider. Cannot be less than min.
   *
   * @default 360
   */
  max?: number
  /**
   * Props for the overlay element.
   */
  overlayProps?: HueSliderOverlayProps
  /**
   * Props for the thumb element.
   */
  thumbProps?: HueSliderThumbProps
  /**
   * Props for the track element.
   */
  trackProps?: HueSliderTrackProps
}

const {
  ComponentContext,
  PropsContext: HueSliderPropsContext,
  useComponentContext,
  usePropsContext: useHueSliderPropsContext,
  withContext,
  withProvider,
} = createSlotComponent<HueSliderRootProps, HueSliderStyle, ComponentContext>(
  "hue-slider",
  hueSliderStyle,
)

export { HueSliderPropsContext, useHueSliderPropsContext }

/**
 * `HueSlider` is a component used to allow the user to select a color hue.
 *
 * @see https://yamada-ui.com/components/hue-slider
 */
export const HueSliderRoot = withProvider<"div", HueSliderRootProps>(
  ({
    children,
    max = 360,
    min = 0,
    orientation: orientationProp = "horizontal",
    overlayProps,
    thumbProps,
    trackProps,
    ...rest
  }) => {
    const { t } = useI18n("hueSlider")
    const orientation = useValue(orientationProp)
    const computedChildren = useMemo(() => {
      if (children) return children

      return (
        <HueSliderTrack {...trackProps}>
          <HueSliderThumb {...thumbProps} />

          <HueSliderOverlay {...overlayProps} />
        </HueSliderTrack>
      )
    }, [children, overlayProps, thumbProps, trackProps])
    const context = useMemo(
      () => ({ max, min, orientation }),
      [max, min, orientation],
    )
    const getColorName = useCallback(
      (hue: number): string => {
        if (hue < 30 || hue >= 330) return t("Red")
        if (hue < 90) return t("Yellow")
        if (hue < 150) return t("Green")
        if (hue < 210) return t("Cyan")
        if (hue < 270) return t("Blue")
        return t("Magenta")
      },
      [t],
    )
    const getAriaValueText = useCallback(
      (hue: number) => `${hue}Â°, ${getColorName(hue)}`,
      [getColorName],
    )

    return (
      <ComponentContext value={context}>
        <Slider.Root
          getAriaValueText={getAriaValueText}
          max={max}
          min={min}
          orientation={orientation}
          {...rest}
        >
          {computedChildren}
        </Slider.Root>
      </ComponentContext>
    )
  },
  "root",
)({
  "--thumb-fill": "transparent",
  "--track-fill": "transparent",
})

export interface HueSliderTrackProps extends Slider.TrackProps {}

export const HueSliderTrack = withContext<"div", HueSliderTrackProps>(
  Slider.Track,
  "track",
)()

export interface HueSliderOverlayProps extends HTMLStyledProps {
  /**
   * The layers used for the overlay element.
   */
  layers?: HTMLStyledProps[]
}

export const HueSliderOverlay = withContext<"fragment", HueSliderOverlayProps>(
  ({ layers, ...rest }) => {
    const { max, min, orientation } = useComponentContext()

    layers ??= [
      {
        bgGradient: `linear(${[
          orientation === "horizontal" ? "to-r" : "to-t",
          ...Array.from({ length: 7 }).map(
            (_, index) =>
              `hsl(${Math.round(min + ((max - min) / 6) * index)}, 100%, 50%)`,
          ),
        ].join(", ")})`,
        boxShadow: "inner",
      },
    ]

    return (
      <>
        {layers.map((props, index) => (
          <styled.div
            key={index}
            data-orientation={orientation}
            {...rest}
            {...props}
          />
        ))}
      </>
    )
  },
  "overlay",
)()

export interface HueSliderThumbProps extends Slider.ThumbProps {}

export const HueSliderThumb = withContext<"div", HueSliderThumbProps>(
  Slider.Thumb,
  "thumb",
)()
