import { defineComponentStyle } from "@yamada-ui/react"

export const kbdStyle = defineComponentStyle({
  base: {
    textStyle: "mono",
    alignItems: "center",
    display: "inline-flex",
    rounded: "l1",
  },

  variants: {
    outline: {
      layerStyle: "outline",
    },
    solid: {
      layerStyle: "solid",
    },
    subtle: {
      layerStyle: "subtle",
    },
    surface: {
      layerStyle: "surface",
    },
  },

  sizes: {
    sm: {
      fontSize: "xs",
      lineHeight: "{sizes.4}",
      minH: "4",
      px: "1",
    },
    md: {
      fontSize: "sm",
      lineHeight: "{sizes.5}",
      minH: "5",
      px: "1.5",
    },
    lg: {
      fontSize: "md",
      lineHeight: "{sizes.6}",
      minH: "6",
      px: "2",
    },
  },

  defaultProps: {
    size: "md",
    variant: "surface",
  },
})

export type KbdStyle = typeof kbdStyle

"use client"

import type { HTMLStyledProps, ThemeProps } from "@yamada-ui/react"
import type { KbdStyle } from "./kbd.style"
import { createComponent } from "@yamada-ui/react"
import { kbdStyle } from "./kbd.style"

export interface KbdProps
  extends HTMLStyledProps<"kbd">,
    ThemeProps<KbdStyle> {}

const {
  PropsContext: KbdPropsContext,
  usePropsContext: useKbdPropsContext,
  withContext,
} = createComponent<KbdProps, KbdStyle>("kbd", kbdStyle)

export { KbdPropsContext, useKbdPropsContext }

/**
 * `Kbd` is a component that represents keyboard input.
 *
 * @see https://yamada-ui.com/components/kbd
 */
export const Kbd = withContext("kbd")()
