import { defineComponentStyle } from "@yamada-ui/react"

export const linkStyle = defineComponentStyle({
  base: {
    alignItems: "center",
    cursor: "pointer",
    display: "inline-flex",
    gap: "3",
    rounded: "l1",
  },

  variants: {
    plain: {
      color: "colorScheme.solid",
      _hover: {
        textDecoration: "underline",
        textDecorationColor: "currentColor",
        textUnderlineOffset: "3px",
      },
    },
    underline: {
      color: "colorScheme.solid",
      textDecoration: "underline",
      textDecorationColor: "currentColor",
      textUnderlineOffset: "3px",
    },
  },

  defaultProps: {
    colorScheme: "link",
    variant: "plain",
  },
})

export type LinkStyle = typeof linkStyle

"use client"

import type { HTMLStyledProps, ThemeProps } from "@yamada-ui/react"
import type { LinkStyle } from "./link.style"
import { createComponent } from "@yamada-ui/react"
import { linkStyle } from "./link.style"

export interface LinkProps extends HTMLStyledProps<"a">, ThemeProps<LinkStyle> {
  /**
   * If `true`, the link will open in new tab.
   *
   * @default false
   */
  external?: boolean
}

const {
  PropsContext: LinkPropsContext,
  usePropsContext: useLinkPropsContext,
  withContext,
} = createComponent<LinkProps, LinkStyle>("link", linkStyle)

export { LinkPropsContext, useLinkPropsContext }

/**
 * `Link` is a component for creating hyperlinks to different web pages, locations within the same page, or other URLs.
 *
 * @see https://yamada-ui.com/components/link
 */
export const Link = withContext("a")(undefined, ({ external, ...rest }) => ({
  rel: external ? "noopener" : undefined,
  target: external ? "_blank" : undefined,
  ...rest,
}))
