import type { Variants } from "motion/react"
import type { FC } from "react"
import type { LoadingSharedProps } from "./loading-provider"
import { memo } from "react"
import { useTimeout } from "../../hooks/use-timeout"
import { isValidElement } from "@yamada-ui/react"
import { motion } from "../motion"
import { Text } from "../text"
import { useLoadingComponent } from "./use-loading-component"

const variants: Variants = {
  animate: {
    opacity: 1,
    scale: 1,
    transition: {
      duration: 0.4,
      ease: [0.4, 0, 0.2, 1],
    },
  },
  exit: {
    opacity: 0,
    scale: 0.95,
    transition: {
      duration: 0.4,
      ease: [0.4, 0, 1, 1],
    },
  },
  initial: {
    opacity: 0,
    scale: 0.95,
  },
}

export interface BackgroundProps extends LoadingSharedProps {}

export const Background: FC<BackgroundProps> = memo(
  ({ duration, initial, loadingScheme, message, onFinish }) => {
    const Component = useLoadingComponent(loadingScheme)

    useTimeout(onFinish, duration)

    return (
      <motion.div
        data-loading
        alignItems="center"
        animate="animate"
        bg="bg.panel"
        bottom="md"
        boxShadow="lg"
        display="flex"
        exit="exit"
        gap="sm"
        initial={initial}
        justifyContent="center"
        maxW="20rem"
        p="sm"
        position="fixed"
        right="md"
        rounded="l2"
        variants={variants}
        zIndex="beerus"
      >
        <Component fontSize="xl" />

        {message ? (
          isValidElement(message) ? (
            message
          ) : (
            <Text fontSize="sm" lineClamp={1}>
              {message}
            </Text>
          )
        ) : null}
      </motion.div>
    )
  },
)

Background.displayName = "Background"

"use client"

import type { FC, PropsWithChildren, ReactNode, RefObject } from "react"
import type {
  LoadingConfig,
  LoadingScheme,
  ThemeConfig,
} from "@yamada-ui/react"
import { AnimatePresence } from "motion/react"
import { createContext, createRef, use, useMemo, useRef, useState } from "react"
import { RemoveScroll } from "react-remove-scroll"
import { assignRef, isNumber, useUpdateEffect } from "@yamada-ui/react"
import { Portal } from "../portal"
import { Background } from "./background"
import { Page } from "./page"
import { Screen } from "./screen"

interface LoadingMethods {
  /**
   * Function to finish loading.
   */
  finish: () => void
  /**
   * Function to forcefully update the loading state.
   *
   * Please be careful, as it will forcefully overwrite the state of the loading component.
   */
  force: (state: Partial<LoadingState>) => void
  /**
   * Function to start loading.
   *
   * If you specify a `message` etc. as an argument, the loading will change accordingly.
   */
  start: (options?: LoadingOptions) => void
  /**
   * Function to update loading.
   *
   * If you specify a `message` etc. as an argument, the loading will change accordingly.
   */
  update: (options: LoadingOptions) => void
}

export interface LoadingOptions {
  duration?: null | number
  loadingScheme?: LoadingScheme
  message?: ReactNode
}

interface LoadingState {
  duration: null | number
  loadingCount: number
  loadingScheme: LoadingScheme
  message: ReactNode | undefined
}

interface LoadingContext {
  /**
   * The background loading methods.
   */
  background: LoadingMethods
  /**
   * The page loading methods.
   */
  page: LoadingMethods
  /**
   * The screen loading methods.
   */
  screen: LoadingMethods
}

const LoadingContext = createContext({} as LoadingContext)

const createController = () => ({
  finish: createRef<LoadingMethods["finish"]>(),
  force: createRef<LoadingMethods["force"]>(),
  start: createRef<LoadingMethods["start"]>(),
  update: createRef<LoadingMethods["update"]>(),
})

type Controller = ReturnType<typeof createController>

const createMethods = (refs: RefObject<Controller>): LoadingMethods => ({
  finish: () => refs.current.finish.current?.(),
  force: (state) => refs.current.force.current?.(state),
  start: (props) => refs.current.start.current?.(props),
  update: (props) => refs.current.update.current?.(props),
})

const incrementCount = (prev: number) => prev + 1
const decrementCount = (prev: number) => (prev === 0 ? prev : prev - 1)

export interface LoadingProviderProps
  extends PropsWithChildren<ThemeConfig["loading"]> {}

export const LoadingProvider: FC<LoadingProviderProps> = ({
  background: backgroundConfig,
  children,
  page: pageConfig,
  screen: screenConfig,
}) => {
  const screen = useRef(createController())
  const page = useRef(createController())
  const background = useRef(createController())

  const value = useMemo(
    () => ({
      background: createMethods(background),
      page: createMethods(page),
      screen: createMethods(screen),
    }),
    [],
  )

  return (
    <LoadingContext value={value}>
      {children}

      <Controller ref={screen} {...screenConfig} component={Screen} />

      <Controller ref={page} {...pageConfig} component={Page} />

      <Controller
        ref={background}
        {...backgroundConfig}
        blockScrollOnMount={backgroundConfig?.blockScrollOnMount ?? false}
        component={Background}
      />
    </LoadingContext>
  )
}

export interface LoadingSharedProps {
  duration: null | number
  loadingScheme: LoadingScheme
  message: ReactNode | undefined
  onFinish: () => void
  initial?: boolean | string
}

interface ControllerProps extends LoadingConfig {
  ref: RefObject<Controller>
  component: FC<LoadingSharedProps>
}

const Controller: FC<ControllerProps> = ({
  ref,
  allowPinchZoom = false,
  blockScrollOnMount = true,
  component: Component,
  duration: durationProp = null,
  loadingCount: loadingCountProp = 0,
  loadingScheme: loadingSchemeProp = "oval",
}) => {
  const loading = useRef<boolean>(false)
  const [{ duration, loadingCount, loadingScheme, message }, setState] =
    useState<LoadingState>({
      duration: durationProp,
      loadingCount: loadingCountProp,
      loadingScheme: loadingSchemeProp,
      message: undefined,
    })

  const { finish, force, start, update } = useMemo<LoadingMethods>(
    () => ({
      finish: () => {
        loading.current = false

        setState(({ loadingCount }) => ({
          duration: durationProp,
          loadingCount: decrementCount(loadingCount),
          loadingScheme: loadingSchemeProp,
          message: undefined,
        }))
      },

      force: ({
        duration = durationProp,
        loadingCount = 0,
        loadingScheme = loadingSchemeProp,
        message,
      }) => {
        loading.current = !!loadingCount

        setState({
          duration,
          loadingCount,
          loadingScheme,
          message,
        })
      },

      start: ({
        duration = durationProp,
        loadingScheme = loadingSchemeProp,
        message,
      } = {}) => {
        loading.current = true

        setState(({ loadingCount }) => ({
          duration,
          loadingCount: incrementCount(loadingCount),
          loadingScheme,
          message,
        }))
      },

      update: (next) => setState((prev) => ({ ...prev, ...next })),
    }),
    [durationProp, loadingSchemeProp],
  )

  assignRef(ref.current.start, start)
  assignRef(ref.current.finish, finish)
  assignRef(ref.current.update, update)
  assignRef(ref.current.force, force)

  const props: LoadingSharedProps = {
    duration,
    initial: loadingCountProp > 0 ? false : "initial",
    loadingScheme,
    message,
    onFinish: finish,
  }

  useUpdateEffect(() => {
    if (loadingCountProp > 0 || isNumber(durationProp))
      setState({
        duration: durationProp,
        loadingCount: loadingCountProp,
        loadingScheme: loadingSchemeProp,
        message: undefined,
      })
  }, [loadingCountProp, durationProp, loadingSchemeProp])

  return (
    <AnimatePresence initial={false}>
      {loadingCount ? (
        <Portal>
          <RemoveScroll
            allowPinchZoom={allowPinchZoom}
            enabled={blockScrollOnMount}
            forwardProps
          >
            <Component {...props} />
          </RemoveScroll>
        </Portal>
      ) : null}
    </AnimatePresence>
  )
}

/**
 * `useLoading` is a custom hook for controlling the loading of the application.
 *
 * @see https://yamada-ui.com/docs/hooks/use-loading
 */
export const useLoading = (): LoadingContext => {
  const { background, page, screen } = use(LoadingContext)

  const context = useMemo(
    () => ({ background, page, screen }),
    [background, page, screen],
  )

  return context
}

import { defineComponentStyle } from "@yamada-ui/react"

export const loadingStyle = defineComponentStyle({
  base: {
    color: ["colorScheme.500", "colorScheme.600"],
    fontSize: "1em",
  },
})

export type LoadingStyle = typeof loadingStyle

"use client"

import type {
  CSSProps,
  InitialProps,
  ThemeProps,
  WithoutThemeProps,
} from "@yamada-ui/react"
import type { IconProps } from "../icon"
import type { LoadingStyle } from "./loading.style"
import { createComponent } from "@yamada-ui/react"
import { loadingStyle } from "./loading.style"

export const initialProps: InitialProps<LoadingProps> = {
  "data-loading": "",
}

export const superProps = ({
  css,
  duration,
  dur = duration,
  ...rest
}: WithoutThemeProps<LoadingProps, LoadingStyle>): WithoutThemeProps<
  LoadingProps,
  LoadingStyle
> => ({
  css,
  dur,
  ...rest,
})

export interface LoadingProps
  extends WithoutThemeProps<IconProps>,
    ThemeProps<LoadingStyle> {
  /**
   * The CSS `dur` property.
   */
  duration?: IconProps["dur"]
  /**
   * The CSS `color` property.
   */
  secondaryColor?: CSSProps["color"]
}

const {
  PropsContext: LoadingPropsContext,
  usePropsContext: useLoadingPropsContext,
  withContext,
} = createComponent<LoadingProps, LoadingStyle>("loading", loadingStyle)

export { LoadingPropsContext, useLoadingPropsContext, withContext }

"use client"

import type { LoadingProps } from "./loading"
import { styled, varAttr } from "@yamada-ui/react"
import { Icon } from "../icon"
import { initialProps, superProps, withContext } from "./loading"

export interface OvalProps extends LoadingProps {}

/**
 * `Loading` is a component displayed during waiting times, such as when data is being loaded.
 *
 * @see https://yamada-ui.com/docs/components/feedback/loading
 */
export const Oval = withContext<"svg", OvalProps>(
  ({ dur = "1s", secondaryColor, ...rest }) => {
    return (
      <Icon stroke="currentColor" viewBox="0 0 38 38" {...rest}>
        <g fill="none" fillRule="evenodd">
          <g strokeWidth="2" transform="translate(1 1)">
            <styled.circle
              {...(secondaryColor
                ? { stroke: "{secondary-color}" }
                : { strokeOpacity: ".5" })}
              cx="18"
              cy="18"
              r="18"
            />
            <path d="M36 18c0-9.94-8.06-18-18-18">
              <animateTransform
                type="rotate"
                attributeName="transform"
                dur={dur}
                from="0 18 18"
                repeatCount="indefinite"
                to="360 18 18"
              />
            </path>
          </g>
        </g>
      </Icon>
    )
  },
)(initialProps, ({ secondaryColor, ...props }) => {
  const rest = superProps(props)

  return {
    "--secondary-color": varAttr(secondaryColor, "colors"),
    secondaryColor,
    ...rest,
  }
})

import type { Variants } from "motion/react"
import type { FC } from "react"
import type { LoadingSharedProps } from "./loading-provider"
import { memo } from "react"
import { useTimeout } from "../../hooks/use-timeout"
import { isValidElement } from "@yamada-ui/react"
import { motion } from "../motion"
import { Text } from "../text"
import { useLoadingComponent } from "./use-loading-component"

const variants: { [key: string]: Variants } = {
  overlay: {
    animate: {
      opacity: 1,
      transition: {
        duration: 0.4,
        ease: [0.4, 0, 0.2, 1],
      },
    },
    exit: {
