      transition: {
        duration: 0.4,
        ease: [0.4, 0, 1, 1],
      },
    },
    initial: {
      opacity: 0,
    },
  },
  panel: {
    animate: {
      opacity: 1,
      scale: 1,
      transition: {
        duration: 0.4,
        ease: [0.4, 0, 0.2, 1],
      },
    },
    exit: {
      opacity: 0,
      scale: 0.95,
      transition: {
        duration: 0.4,
        ease: [0.4, 0, 1, 1],
      },
    },
    initial: {
      opacity: 0,
      scale: 0.95,
    },
  },
}

export interface PageProps extends LoadingSharedProps {}

export const Page: FC<PageProps> = memo(
  ({ duration, initial, loadingScheme, message, onFinish }) => {
    const Component = useLoadingComponent(loadingScheme)

    useTimeout(onFinish, duration)

    return (
      <motion.div
        data-loading
        alignItems="center"
        animate="animate"
        bg="bg.overlay"
        display="flex"
        exit="exit"
        h="100dvh"
        initial={initial}
        inset={0}
        justifyContent="center"
        p="md"
        position="fixed"
        variants={variants.overlay}
        w="100vw"
        zIndex="beerus"
      >
        <motion.div
          alignItems="center"
          animate="animate"
          bg="bg.panel"
          boxShadow="lg"
          display="flex"
          exit="exit"
          flexDirection="column"
          gap="sm"
          initial={initial}
          justifyContent="center"
          maxW="24rem"
          p="md"
          rounded="l2"
          variants={variants.panel}
        >
          <Component fontSize="6xl" />

          {message ? (
            isValidElement(message) ? (
              message
            ) : (
              <Text lineClamp={3}>{message}</Text>
            )
          ) : null}
        </motion.div>
      </motion.div>
    )
  },
)

Page.displayName = "Page"

