import { defineComponentStyle } from "@yamada-ui/react"

export const markStyle = defineComponentStyle({
  base: {
    bg: "transparent",
    whiteSpace: "nowrap",
  },

  variants: {
    accent: {
      color: "colorScheme.solid",
    },
    solid: {
      layerStyle: "solid",
    },
    subtle: {
      layerStyle: "subtle",
    },
    text: {
      fontWeight: "semibold",
    },
  },

  defaultProps: {
    variant: "subtle",
  },
})

export type MarkStyle = typeof markStyle

"use client"

import type { HTMLStyledProps, ThemeProps } from "@yamada-ui/react"
import type { MarkStyle } from "./mark.style"
import { createComponent } from "@yamada-ui/react"
import { markStyle } from "./mark.style"

export interface MarkProps
  extends HTMLStyledProps<"mark">,
    ThemeProps<MarkStyle> {}

const {
  PropsContext: MarkPropsContext,
  usePropsContext: useMarkPropsContext,
  withContext,
} = createComponent<MarkProps, MarkStyle>("mark", markStyle)

export { MarkPropsContext, useMarkPropsContext }

/**
 * `Mark` is a component that emphasizes a specific part of the text.
 *
 * @see https://yamada-ui.com/docs/components/mark
 */
export const Mark = withContext("mark")()
