import { defineComponentSlotStyle } from "@yamada-ui/react"
import { popoverStyle } from "../popover"

export const menuStyle = defineComponentSlotStyle({
  base: {
    command: {
      textStyle: "mono",
      color: "fg.muted",
      ms: "auto",
    },
    content: {
      "&:has([data-footer])": {
        scrollPaddingBottom: "calc({space} + {footer-height} + {border-width})",
      },
      "&:has([data-header])": {
        scrollPaddingTop: "calc({space} + {header-height} + {border-width})",
      },
      "--border-width": "1px",
      "--footer-height": "{item-height}",
      "--header-height": "{item-height}",
      gap: "0",
      overflow: "auto",
      p: "{space}",
      scrollPaddingBottom: "{space}",
      scrollPaddingTop: "{space}",
    },
    footer: {
      alignItems: "center",
      bg: popoverStyle.base?.content?.bg,
      borderTopWidth: "{border-width}",
      bottom: "calc({space} * -1)",
      display: "flex",
      left: "0",
      mb: "calc({space} * -1)",
      minH: "{footer-height}",
      mt: "{space}",
      mx: "calc({space} * -1)",
      position: "sticky",
      right: "0",
    },
    group: {
      display: "flex",
      flexDirection: "column",
    },
    header: {
      alignItems: "center",
      bg: popoverStyle.base?.content?.bg,
      borderBottomWidth: "{border-width}",
      display: "flex",
      left: "0",
      mb: "{space}",
      minH: "{header-height}",
      mt: "calc({space} * -1)",
      mx: "calc({space} * -1)",
      position: "sticky",
      right: "0",
      top: "calc({space} * -1)",
    },
    indicator: {
      alignItems: "center",
      display: "flex",
      justifyContent: "center",
    },
    item: {
      alignItems: "center",
      display: "flex",
      focusVisibleRing: "none",
      minH: "{item-height}",
      userSelect: "none",
      _activedescendant: { bg: "bg.muted/70" },
      _disabled: { layerStyle: "disabled" },
    },
    label: {
      alignItems: "center",
      display: "flex",
      fontWeight: "medium",
      minH: "{item-height}",
    },
    separator: {
      "& + hr": { display: "none" },
      "&:last-child, &:first-child": { display: "none" },
      borderBottomWidth: "{border-width}",
      mx: "calc({space} * -1)",
      my: "{space}",
    },
  },

  sizes: {
    sm: {
      command: { fontSize: "2xs" },
      content: {
        "--item-height": "spaces.7",
        "--space": "spaces.1",
        fontSize: "xs",
        minW: "3xs",
      },
      footer: { px: "1.5" },
      header: { px: "1.5" },
      indicator: { fontSize: "sm" },
      item: { gap: "1.5", px: "1.5", rounded: "l1" },
      label: { px: "1.5" },
    },
    md: {
      command: { fontSize: "xs" },
      content: {
        "--item-height": "spaces.8",
        "--space": "spaces.1",
        fontSize: "sm",
        minW: "2xs",
      },
      footer: { px: "2" },
      header: { px: "2" },
      indicator: { fontSize: "md" },
      item: { gap: "2", px: "2", rounded: "l1" },
      label: { px: "2" },
    },
    lg: {
      command: { fontSize: "sm" },
      content: {
        "--item-height": "spaces.9",
        "--space": "spaces.1",
        fontSize: "md",
        minW: "2xs",
      },
      footer: { px: "2" },
      header: { px: "2" },
      indicator: { fontSize: "lg" },
      item: { gap: "2", px: "2", rounded: "l1" },
      label: { px: "2" },
    },
  },

  defaultProps: {
    size: "md",
  },
})

export type MenuStyle = typeof menuStyle

"use client"

import type { FC, ReactElement, ReactNode } from "react"
import type {
  Direction,
  GenericsComponent,
  HTMLProps,
  HTMLStyledProps,
  ThemeProps,
  WithoutThemeProps,
} from "@yamada-ui/react"
import type { MenuStyle } from "./menu.style"
import type {
  MenuOptionGroupType,
  MenuOptionGroupValue,
  UseMenuGroupProps,
  UseMenuItemProps,
  UseMenuOptionGroupProps,
  UseMenuOptionItemProps,
  UseMenuProps,
  UseMenuReturn,
} from "./use-menu"
import { Fragment, useMemo, useState } from "react"
import { createSlotComponent, styled, cast, handlerAll } from "@yamada-ui/react"
import { CheckIcon, ChevronRightIcon, CircleSmallIcon } from "../icon"
import { Popover, usePopoverProps } from "../popover"
import { menuStyle } from "./menu.style"
import {
  MainMenuContext,
  MenuContext,
  MenuDescendantsContext,
  MenuGroupContext,
  MenuOptionGroupContext,
  useMenu,
  useMenuContext,
  useMenuGroup,
  useMenuGroupContext,
  useMenuItem,
  useMenuOptionGroup,
  useMenuOptionItem,
} from "./use-menu"

interface MenuSharedItem {
  label?: ReactNode
}

interface MenuItemWithValue extends MenuSharedItem, MenuItemProps {}

interface MenuOptionItem extends MenuSharedItem, MenuOptionItemProps {}

interface MenuItemWithSeparator extends MenuSeparatorProps {
  type: "separator"
}

interface MenuItemWithRadioGroup<Y extends string = string>
  extends Omit<MenuItemWithItems, "defaultValue" | "items" | "onChange">,
    MenuOptionGroupProps<"radio", Y> {
  type: "radio"
  items?: MenuOptionItem[]
}

interface MenuItemWithCheckboxGroup<Y extends string[] = string[]>
  extends Omit<MenuItemWithItems, "defaultValue" | "items" | "onChange">,
    MenuOptionGroupProps<"checkbox", Y> {
  type: "checkbox"
  items?: MenuOptionItem[]
}

interface MenuItemWithItems extends MenuSharedItem, MenuGroupProps {
  hasEndSeparator?: boolean
  hasSeparator?: boolean
  hasStartSeparator?: boolean
  items?: MenuItemWithValue[]
  labelProps?: MenuLabelProps
}

interface ComponentContext
  extends Pick<
    UseMenuReturn,
    | "getContentProps"
    | "getContextTriggerProps"
    | "getSeparatorProps"
    | "getTriggerProps"
  > {}

export interface MenuRootProps
  extends UseMenuProps,
    Omit<
      WithoutThemeProps<Popover.RootProps, MenuStyle>,
      "autoFocus" | "modal" | "updateRef" | "withCloseButton"
    >,
    ThemeProps<MenuStyle> {
  /**
   * The placement of the popper relative to its reference.
   *
   * @default 'end-start'
   */
  placement?: Direction
}

const {
  ComponentContext,
  PropsContext: MenuPropsContext,
  StyleContext,
  useComponentContext,
  usePropsContext: useMenuPropsContext,
  withContext,
  useRootComponentProps,
} = createSlotComponent<MenuRootProps, MenuStyle, ComponentContext>(
  "menu",
  menuStyle,
)

export { MenuPropsContext, useMenuPropsContext }

/**
 * `Menu` is a component that displays a common dropdown menu.
 *
 * @see https://yamada-ui.com/components/menu
 */
export const MenuRoot: FC<MenuRootProps> = (props) => {
  const [styleContext, mergedProps] = useRootComponentProps(props)
  const [
    { animationScheme, initialFocusRef, offset, placement, ...popoverProps },
    { children, disabled, ...rest },
  ] = usePopoverProps(mergedProps, [
    "disabled",
    "open",
    "defaultOpen",
    "onOpen",
    "onClose",
  ])
  const {
    closeOnSelect,
    descendants,
    open,
    subMenu,
    subMenuDirection,
    updateRef,
    getContentProps,
    getContextTriggerProps,
    getSeparatorProps,
    getTriggerProps,
    onActiveDescendant,
    onClose,
    onCloseRef,
    onCloseSubMenu,
    onOpen,
    onSelect,
  } = useMenu({ disabled, ...rest })
  const mergedPopoverProps = useMemo<Popover.RootProps>(
    () => ({
      ...popoverProps,
      animationScheme:
        animationScheme ?? (subMenu ? "inline-start" : "block-start"),
      autoFocus: !!initialFocusRef,
      disabled,
      initialFocusRef,
      offset: offset ?? (subMenu ? [0, 0] : undefined),
      open,
      placement:
        placement ?? (subMenu ? `center-${subMenuDirection}` : "end-start"),
      updateRef,
      onClose,
      onOpen,
    }),
    [
      animationScheme,
      disabled,
      initialFocusRef,
      offset,
      onClose,
      onOpen,
      open,
      placement,
      popoverProps,
      subMenu,
      subMenuDirection,
      updateRef,
    ],
  )
  const menuContext = useMemo(
    () => ({
      subMenu,
      subMenuDirection,
      onActiveDescendant,
      onClose,
      onCloseSubMenu,
      onOpen,
      onSelect,
    }),
    [
      onClose,
      onOpen,
      onSelect,
      onActiveDescendant,
      subMenu,
      subMenuDirection,
      onCloseSubMenu,
    ],
  )
  const mainMenuContext = useMemo(
    () => ({
      closeOnSelect,
      descendants,
      onActiveDescendant,
      onCloseRef,
      onSelect,
    }),
    [closeOnSelect, descendants, onActiveDescendant, onCloseRef, onSelect],
  )
  const componentContext = useMemo(
    () => ({
      getContentProps,
      getContextTriggerProps,
      getSeparatorProps,
      getTriggerProps,
    }),
    [
      getContentProps,
      getContextTriggerProps,
      getSeparatorProps,
      getTriggerProps,
    ],
  )

  return (
    <StyleContext value={styleContext}>
      <MenuDescendantsContext value={descendants}>
        <MenuContext value={menuContext}>
          <MainMenuContext value={mainMenuContext}>
            <ComponentContext value={componentContext}>
              <Popover.Root {...mergedPopoverProps}>{children}</Popover.Root>
            </ComponentContext>
          </MainMenuContext>
        </MenuContext>
      </MenuDescendantsContext>
    </StyleContext>
  )
}

export interface MenuTriggerProps extends Popover.TriggerProps {}

export const MenuTrigger = withContext<"button", MenuTriggerProps>(
  Popover.Trigger,
  "trigger",
)(undefined, (props) => {
  const { getTriggerProps } = useComponentContext()

  return cast<HTMLProps<"button">>(getTriggerProps(cast<HTMLProps>(props)))
})

interface Rect {
  left: number
  top: number
}

export interface MenuContextTriggerProps extends HTMLProps {}

export const MenuContextTrigger = withContext<"div", MenuContextTriggerProps>(
  ({ children, onContextMenu, ...rest }) => {
    const [rect, setRect] = useState<Rect>({
      left: 0,
      top: 0,
    })
    const { getContextTriggerProps } = useComponentContext()

    return (
      <>
        <MenuAnchor>
          <styled.div style={{ position: "absolute", ...rect }} />
        </MenuAnchor>

        <styled.div
          asChild
          {...getContextTriggerProps({
            onContextMenu: handlerAll(onContextMenu, (ev) => {
              setRect({ left: ev.pageX, top: ev.pageY })
            }),
            ...rest,
          })}
        >
          {children}
        </styled.div>
      </>
    )
  },
  "contextTrigger",
)()

export interface MenuAnchorProps extends Popover.AnchorProps {}

export const MenuAnchor = withContext<"div", MenuAnchorProps>(
  Popover.Anchor,
  "anchor",
)()

export interface MenuContentProps extends Popover.ContentProps {
  /**
   * The footer of the menu.
   */
  footer?: ReactNode
  /**
   * The header of the menu.
   */
  header?: ReactNode
  /**
   * If provided, generate elements based on items.
   */
  items?: (
    | MenuItemWithCheckboxGroup
    | MenuItemWithItems
    | MenuItemWithRadioGroup
    | MenuItemWithSeparator
    | MenuItemWithValue
  )[]
  /**
   * Props for the footer component.
   */
  footerProps?: MenuFooterProps
  /**
   * Props for the header component.
   */
  headerProps?: MenuHeaderProps
}

export const MenuContent = withContext<"div", MenuContentProps>(
  Popover.Content,
  "content",
)(
  undefined,
  ({
    children,
    footer,
    header,
    items = [],
    footerProps,
    headerProps,
    portalProps,
    ...rest
  }) => {
    const { subMenu } = useMenuContext()
    const { getContentProps } = useComponentContext()
    const computedChildren = useMemo(() => {
      if (children) return children

      return (
        <>
          {header ? <MenuHeader {...headerProps}>{header}</MenuHeader> : null}

          {items.map((props, index) => {
            if ("type" in props) {
              if (props.type === "radio") {
                const {
                  type,
                  hasSeparator = true,
                  hasEndSeparator = hasSeparator,
                  hasStartSeparator = hasSeparator,
                  items = [],
                  ...rest
                } = props

                return (
                  <Fragment key={index}>
                    {hasStartSeparator ? <MenuSeparator /> : null}

                    <MenuOptionGroup type={type} {...rest}>
                      {items.map(({ label, ...rest }, index) => (
                        <MenuOptionItem key={index} {...rest}>
                          {label}
                        </MenuOptionItem>
                      ))}
                    </MenuOptionGroup>

                    {hasEndSeparator ? <MenuSeparator /> : null}
                  </Fragment>
                )
              } else if (props.type === "checkbox") {
                const {
                  type,
                  hasSeparator = true,
                  hasEndSeparator = hasSeparator,
                  hasStartSeparator = hasSeparator,
                  items = [],
                  ...rest
                } = props

                return (
                  <Fragment key={index}>
                    {hasStartSeparator ? <MenuSeparator /> : null}

                    <MenuOptionGroup type={type} {...rest}>
                      {items.map(({ label, ...rest }, index) => (
                        <MenuOptionItem key={index} {...rest}>
                          {label}
                        </MenuOptionItem>
                      ))}
                    </MenuOptionGroup>

                    {hasEndSeparator ? <MenuSeparator /> : null}
                  </Fragment>
                )
              } else {
                return <MenuSeparator key={index} />
              }
            } else if ("items" in props) {
              const {
                hasSeparator = true,
                hasEndSeparator = hasSeparator,
                hasStartSeparator = hasSeparator,
                items = [],
                ...rest
              } = props

              return (
                <Fragment key={index}>
                  {hasStartSeparator ? <MenuSeparator /> : null}

                  <MenuGroup {...rest}>
                    {items.map(({ label, ...rest }, index) => (
                      <MenuItem key={index} {...rest}>
                        {label}
                      </MenuItem>
                    ))}
                  </MenuGroup>

                  {hasEndSeparator ? <MenuSeparator /> : null}
                </Fragment>
              )
            } else if ("value" in props) {
              const { label, ...rest } = props

              return (
                <MenuItem key={index} {...rest}>
                  {label}
                </MenuItem>
              )
            }
          })}

          {footer ? <MenuFooter {...footerProps}>{footer}</MenuFooter> : null}
        </>
      )
    }, [children, footer, footerProps, header, headerProps, items])

    return {
      ...getContentProps(
        cast<HTMLProps>({ ...rest, children: computedChildren }),
      ),
      portalProps: subMenu ? { ...portalProps, disabled: true } : portalProps,
    }
  },
)

export interface MenuHeaderProps extends HTMLStyledProps {}

export const MenuHeader = withContext<"div", MenuHeaderProps>(
  "div",
  "header",
)({ "data-header": "" })

export interface MenuFooterProps extends HTMLStyledProps {}

export const MenuFooter = withContext<"div", MenuFooterProps>(
  "div",
  "footer",
)({ "data-footer": "" })

export interface MenuLabelProps extends HTMLStyledProps<"span"> {}

export const MenuLabel = withContext<"span", MenuLabelProps>("span", "label")(
  undefined,
  (props) => {
    const { getLabelProps } = useMenuGroupContext()

    return getLabelProps(props)
  },
)

export interface MenuGroupProps extends UseMenuGroupProps, HTMLStyledProps {
  /**
   * The label of the group.
   */
  label?: ReactNode
  /**
   * Props for the label component.
   */
  labelProps?: MenuLabelProps
}

export const MenuGroup = withContext<"div", MenuGroupProps>(
  ({ children, label, labelProps, ...rest }) => {
    const { getGroupProps, getLabelProps } = useMenuGroup(rest)
    const context = useMemo(() => ({ getLabelProps }), [getLabelProps])

    return (
      <MenuGroupContext value={context}>
        <styled.div {...getGroupProps()}>
          {label ? <MenuLabel {...labelProps}>{label}</MenuLabel> : null}
          {children}
        </styled.div>
      </MenuGroupContext>
    )
  },
  "group",
)()

export interface MenuOptionGroupProps<
  Y extends MenuOptionGroupType = "checkbox",
  M extends MenuOptionGroupValue<Y> = MenuOptionGroupValue<Y>,
> extends UseMenuOptionGroupProps<Y, M>,
    Omit<MenuGroupProps, "defaultValue" | "onChange"> {}

export const MenuOptionGroup = withContext<"div", MenuOptionGroupProps>(
  ({
    type: typeProp,
    defaultValue,
    value: valueProp,
    onChange: onChangeProp,
    ...rest
  }) => {
    const { type, value, onChange } = useMenuOptionGroup({
      type: typeProp,
      defaultValue,
      value: valueProp,
      onChange: onChangeProp,
    })
    const context = useMemo(
      () => ({ type, value, onChange }),
      [type, value, onChange],
    )

    return (
      <MenuOptionGroupContext value={context}>
        <MenuGroup {...rest} />
      </MenuOptionGroupContext>
    )
  },
  { name: "optionGroup", slot: ["group", "option"] },
)() as GenericsComponent<{
  <
    Y extends MenuOptionGroupType = "checkbox",
    M extends MenuOptionGroupValue<Y> = MenuOptionGroupValue<Y>,
  >(
    props: MenuOptionGroupProps<Y, M>,
  ): ReactElement
}>

export interface MenuItemProps extends HTMLStyledProps, UseMenuItemProps {}

export const MenuItem = withContext<"div", MenuItemProps>(
  ({ children, ...rest }) => {
    const { subMenuTrigger, getItemProps } = useMenuItem(rest)

    return (
      <styled.div {...getItemProps()}>
        {children}

        {subMenuTrigger ? (
          <MenuIndicator as={ChevronRightIcon} ms="auto" />
        ) : null}
      </styled.div>
    )
  },
  "item",
)()

export interface MenuOptionItemProps
  extends HTMLStyledProps,
    UseMenuOptionItemProps {
  /**
   * The icon to be used in the menu option item.
   */
  icon?: ReactNode
}

export const MenuOptionItem = withContext<"div", MenuOptionItemProps>(
  ({ children, icon, ...rest }) => {
    const { type, getIndicatorProps, getOptionItemProps } =
      useMenuOptionItem(rest)

    return (
      <styled.div {...getOptionItemProps()}>
        <MenuIndicator {...getIndicatorProps()}>
          {icon ||
            (type === "radio" ? (
              <CircleSmallIcon fill="currentColor" />
            ) : (
              <CheckIcon />
            ))}
        </MenuIndicator>
        {children}
      </styled.div>
    )
  },
  { name: "optionItem", slot: ["item", "option"] },
)()

export interface MenuIndicatorProps extends HTMLStyledProps {}

export const MenuIndicator = withContext<"div", MenuIndicatorProps>(
  "div",
  "indicator",
)()

export interface MenuCommandProps extends HTMLStyledProps<"kbd"> {}

export const MenuCommand = withContext<"kbd", MenuCommandProps>(
  "kbd",
  "command",
)()

export interface MenuSeparatorProps extends HTMLStyledProps<"hr"> {}

export const MenuSeparator = withContext<"hr", MenuSeparatorProps>(
  "hr",
  "separator",
)(undefined, (props) => {
  const { getSeparatorProps } = useComponentContext()

  return getSeparatorProps(props)
})

"use client"

import type { KeyboardEvent, MouseEvent, RefObject } from "react"
import type { HTMLProps, PropGetter } from "@yamada-ui/react"
import type { Descendant, Descendants } from "../../hooks/use-descendants"
import type { UseDisclosureProps } from "../../hooks/use-disclosure"
import { useCallback, useId, useRef } from "react"
import { useControllableState } from "../../hooks/use-controllable-state"
import { createDescendants } from "../../hooks/use-descendants"
import { useDisclosure } from "../../hooks/use-disclosure"
import {
  ariaAttr,
  assignRef,
  createContext,
  cx,
  dataAttr,
  handlerAll,
  isArray,
  isString,
  isTruthyDataAttr,
  mergeRefs,
  noop,
  runKeyAction,
  useUpdateEffect,
} from "@yamada-ui/react"

type SubMenuDirection = "end" | "start"

interface MenuDescendantProps {
  id: string
}
type MenuDescendant = Descendant<HTMLDivElement, MenuDescendantProps>

const {
  DescendantsContext: MenuDescendantsContext,
  useDescendant: useMenuDescendant,
  useDescendantRegister: useMenuDescendantRegister,
  useDescendants: useMenuDescendants,
} = createDescendants<HTMLDivElement, MenuDescendantProps>()

export { MenuDescendantsContext, useMenuDescendant, useMenuDescendants }

interface MenuContext
  extends Pick<
    UseMenuReturn,
    | "onActiveDescendant"
    | "onClose"
    | "onCloseSubMenu"
    | "onOpen"
    | "onSelect"
    | "subMenu"
    | "subMenuDirection"
  > {}

const [MenuContext, useMenuContext] = createContext<MenuContext>({
  name: "MenuContext",
})

interface MenuGroupContext extends Pick<UseMenuGroupReturn, "getLabelProps"> {}

const [MenuGroupContext, useMenuGroupContext] = createContext<MenuGroupContext>(
  {
    name: "MenuGroupContext",
  },
)

interface MainMenuContext {
  descendants: Descendants<HTMLDivElement, MenuDescendantProps>
  onActiveDescendant: (descendant?: MenuDescendant) => void
  onCloseRef: RefObject<() => void>
  onSelect: (value?: string, closeOnSelect?: boolean) => void
  closeOnSelect?: boolean
}

const [MainMenuContext, useMainMenuContext] = createContext<MainMenuContext>({
  name: "MainMenuContext",
  strict: false,
})

interface MenuOptionGroupContext {
  type: "checkbox" | "radio"
  value: string | string[]
  onChange?: (value: string) => void
}

const [MenuOptionGroupContext, useMenuOptionGroupContext] =
  createContext<MenuOptionGroupContext>({
    name: "MenuOptionGroupContext",
  })

export {
  MainMenuContext,
  MenuContext,
  MenuGroupContext,
  MenuOptionGroupContext,
  useMainMenuContext,
  useMenuContext,
  useMenuGroupContext,
  useMenuOptionGroupContext,
}

export interface UseMenuProps extends Omit<UseDisclosureProps, "timing"> {
  /**
   * If `true`, the menu item will be closed when selected.
   *
   * @default true
   */
  closeOnSelect?: boolean
  /**
   * If `true`, the menu will be disabled.
   *
   * @default false
   */
  disabled?: boolean
  /**
   * The direction of the sub menu.
   *
   * @default 'end'
   */
  subMenuDirection?: SubMenuDirection
  /**
   * Callback invoked when a menu item is selected.
   */
  onSelect?: (value?: string) => void
}

export const useMenu = ({
  closeOnSelect,
  defaultOpen,
  disabled = false,
  open: openProp,
  subMenuDirection = "end",
  onClose: onCloseProp,
  onOpen: onOpenProp,
  onSelect: onSelectProp,
}: UseMenuProps = {}) => {
  const triggerId = useId()
  const contentId = useId()
  const descendants = useMenuDescendants()
  const updateRef = useRef<() => void>(noop)
  const onCloseRef = useRef<() => void>(noop)
  const contentRef = useRef<HTMLDivElement>(null)
  const { open, onClose, onOpen } = useDisclosure({
    defaultOpen,
    open: openProp,
    onClose: onCloseProp,
    onOpen: onOpenProp,
  })
  const onCloseSubMenu = useCallback(() => onCloseRef.current(), [])
  const onActiveDescendant = useCallback(
    (
      descendant?: MenuDescendant,
      options: FocusOptions = { preventScroll: true },
    ) => {
      if (!contentRef.current || !descendant || disabled) return

      contentRef.current.setAttribute("aria-activedescendant", descendant.id)

      descendants.active(descendant, options)
    },
    [descendants, disabled],
  )
  const { mainCloseOnSelect, subMenu, getSubMenuProps, onMainSelect } =
    useSubMenu({
      descendants,
      disabled,
      open,
      subMenuDirection,
      onActiveDescendant,
      onClose,
      onOpen,
    })

  closeOnSelect ??= mainCloseOnSelect ?? true

  const onSelect = useCallback(
    (value?: string, closeOnSelectProp = closeOnSelect) => {
      if (disabled) return

      onSelectProp?.(value)
      onMainSelect?.(value, closeOnSelectProp)

      if (!closeOnSelectProp) return

      onClose()
    },
    [closeOnSelect, disabled, onClose, onMainSelect, onSelectProp],
  )

  const onClick = useCallback(
    (ev: MouseEvent<HTMLDivElement>) => {
      if (disabled) return

      ev.preventDefault()

      if (!open) {
        onOpen()
      } else {
        onClose()
      }
    },
    [disabled, onClose, onOpen, open],
  )

  const onContextMenu = useCallback(
    (ev: MouseEvent<HTMLDivElement>) => {
      if (disabled) return

      ev.preventDefault()
      onOpen()
      updateRef.current()
    },
    [disabled, onOpen],
  )

  const onKeyDown = useCallback(
    (ev: KeyboardEvent<HTMLDivElement>) => {
      if (disabled) return

      runKeyAction(ev, {
        ArrowDown: () => {
          onOpen()

          setTimeout(() => {
            const descendant = descendants.enabledFirstValue()

            onActiveDescendant(descendant)
          })
        },
        ArrowUp: () => {
          onOpen()

          setTimeout(() => {
            const descendant = descendants.enabledLastValue()

            onActiveDescendant(descendant)
          })
        },
        Enter: () => {
          onOpen()

          setTimeout(() => {
            const descendant = descendants.enabledFirstValue()

            onActiveDescendant(descendant)
          })
        },
        Space: () => {
          onOpen()

          setTimeout(() => {
            const descendant = descendants.enabledFirstValue()

            onActiveDescendant(descendant)
          })
        },
      })
    },
    [descendants, disabled, onActiveDescendant, onOpen],
  )

  const getTriggerProps: PropGetter = useCallback(
    (props = {}) => ({
      ...getSubMenuProps({
        id: triggerId,
        "aria-controls": open ? contentId : undefined,
        "aria-disabled": ariaAttr(disabled),
        "aria-expanded": open,
        "aria-haspopup": "menu",
        "data-trigger": dataAttr(true),
        role: "button",
        tabIndex: disabled ? -1 : 0,
        ...props,
        onClick: handlerAll(props.onClick, onClick),
        onKeyDown: handlerAll(props.onKeyDown, onKeyDown),
      }),
    }),
    [contentId, disabled, getSubMenuProps, onClick, onKeyDown, open, triggerId],
  )

  const getContextTriggerProps: PropGetter = useCallback(
    (props = {}) => ({
      id: triggerId,
      "aria-controls": open ? contentId : undefined,
      "aria-disabled": ariaAttr(disabled),
      "aria-expanded": open,
      "aria-haspopup": "menu",
      "data-trigger": dataAttr(true),
      role: "application",
      ...props,
      onContextMenu: handlerAll(props.onContextMenu, onContextMenu),
    }),
    [contentId, disabled, onContextMenu, open, triggerId],
  )

  const getContentProps: PropGetter = useCallback(
    ({ ref, "aria-labelledby": ariaLabelledby, ...props } = {}) => ({
      id: contentId,
      "aria-labelledby": cx(ariaLabelledby, triggerId),
      role: "menu",
      ...props,
      ref: mergeRefs(ref, contentRef),
    }),
    [contentId, triggerId],
  )

  const getSeparatorProps: PropGetter = useCallback(
    (props) => ({ role: "separator", ...props }),
    [],
  )

  return {
    closeOnSelect,
    descendants,
    open,
    subMenu,
    subMenuDirection,
    updateRef,
    getContentProps,
    getContextTriggerProps,
    getSeparatorProps,
    getTriggerProps,
    onActiveDescendant,
    onClose,
    onCloseRef,
    onCloseSubMenu,
    onOpen,
    onSelect,
  }
}

export type UseMenuReturn = ReturnType<typeof useMenu>

export interface UseSubMenuProps
  extends Omit<Required<UseDisclosureProps>, "defaultOpen" | "timing"> {
  descendants: Descendants<HTMLDivElement, MenuDescendantProps>
  onActiveDescendant: (descendant?: MenuDescendant) => void
  disabled?: boolean
  subMenuDirection?: SubMenuDirection
}

export const useSubMenu = ({
  descendants,
  disabled = false,
  open,
  subMenuDirection = "end",
  onActiveDescendant,
  onClose,
  onOpen,
}: UseSubMenuProps) => {
  const uuid = useId()
  const {
    closeOnSelect: mainCloseOnSelect,
    descendants: mainDescendants,
    onActiveDescendant: onActiveMainDescendant,
    onCloseRef,
    onSelect: onMainSelect,
  } = useMainMenuContext() ?? {}
  const subMenu = !!mainDescendants && !!onActiveMainDescendant
  const createRegister = useMenuDescendantRegister(mainDescendants)
  const triggerRef = useRef<HTMLDivElement>(null)

  const dataDisabled = useCallback((node?: HTMLDivElement | null) => {
    node ??= triggerRef.current

    if (!node) return false

    const dataDisabled = isTruthyDataAttr(node.getAttribute("data-disabled"))

    return dataDisabled
  }, [])

  const ariaDisabled = useCallback((node?: HTMLDivElement | null) => {
    node ??= triggerRef.current

    if (!node) return false

    const ariaDisabled = isTruthyDataAttr(node.getAttribute("aria-disabled"))

    return ariaDisabled
  }, [])

  const onClick = useCallback(
    (ev: MouseEvent<HTMLDivElement>) => {
      if (!subMenu) return

      ev.defaultPrevented = disabled || dataDisabled() || ariaDisabled()
    },
    [ariaDisabled, dataDisabled, disabled, subMenu],
  )

  const onMouseEnter = useCallback(() => {
    if (!subMenu || disabled || dataDisabled() || ariaDisabled()) return

    onOpen()
  }, [ariaDisabled, dataDisabled, disabled, onOpen, subMenu])

  const onMouseMove = useCallback(
    (ev: MouseEvent<HTMLDivElement>) => {
      if (!subMenu || disabled || dataDisabled() || ariaDisabled()) return

      const descendant = descendants.value(triggerRef.current)

      onActiveMainDescendant(descendant)

      ev.defaultPrevented = true
    },
    [
      ariaDisabled,
      dataDisabled,
      descendants,
      disabled,
      onActiveMainDescendant,
      subMenu,
    ],
  )

  const onKeyDown = useCallback(
    (ev: KeyboardEvent<HTMLDivElement>) => {
      if (!subMenu || disabled) return

      const currentDescendant = open ? descendants : mainDescendants
      const onActiveCurrentDescendant = open
        ? onActiveDescendant
        : onActiveMainDescendant

      runKeyAction(ev, {
        ArrowDown: () => {
          const descendant = currentDescendant.enabledNextValue(
            triggerRef.current,
          )

          onActiveCurrentDescendant(descendant)

          ev.defaultPrevented = true
        },
        ArrowUp: () => {
          const descendant = currentDescendant.enabledPrevValue(
            triggerRef.current,
          )

          onActiveCurrentDescendant(descendant)

          ev.defaultPrevented = true
        },
        End: () => {
          const descendant = currentDescendant.enabledLastValue()

          onActiveCurrentDescendant(descendant)

          ev.defaultPrevented = true
        },
        Home: () => {
          const descendant = currentDescendant.enabledFirstValue()

          onActiveCurrentDescendant(descendant)

          ev.defaultPrevented = true
        },
        [subMenuDirection === "end" ? "ArrowRight" : "ArrowLeft"]: () => {
          onOpen()

          setTimeout(() => {
            const descendant = descendants.enabledFirstValue()

            onActiveDescendant(descendant)
          })

          ev.defaultPrevented = true
        },
      })
    },
    [
      subMenu,
      disabled,
      open,
      descendants,
      mainDescendants,
      onActiveDescendant,
      onActiveMainDescendant,
      subMenuDirection,
      onOpen,
    ],
  )

  assignRef(onCloseRef, onClose)

  const getSubMenuProps: PropGetter = useCallback(
    ({ id = uuid, ref, ...props } = {}) => {
      const getDisabled = (node: HTMLDivElement) =>
        disabled || dataDisabled(node) || ariaDisabled(node)
      const register = createRegister({ id, disabled: getDisabled })

      return {
        role: subMenu ? "menuitem" : "button",
        ...props,
        ref: mergeRefs(ref, triggerRef, register),
        onClick: handlerAll(onClick, props.onClick),
        onKeyDown: handlerAll(onKeyDown, props.onKeyDown),
        onMouseEnter: handlerAll(onMouseEnter, props.onMouseEnter),
        onMouseMove: handlerAll(onMouseMove, props.onMouseMove),
      }
    },
    [
      uuid,
      subMenu,
      createRegister,
      onClick,
      onKeyDown,
      onMouseEnter,
      onMouseMove,
      disabled,
      dataDisabled,
      ariaDisabled,
    ],
  )

  return { mainCloseOnSelect, subMenu, getSubMenuProps, onMainSelect }
}

export type UseSubMenuReturn = ReturnType<typeof useSubMenu>

export interface UseMenuGroupProps extends HTMLProps {}

export const useMenuGroup = ({
  "aria-labelledby": ariaLabelledbyProp,
  ...rest
}: UseMenuGroupProps) => {
  const labelId = useId()

  const getGroupProps: PropGetter = useCallback(
    ({ "aria-labelledby": ariaLabelledby, ...props } = {}) => ({
      "aria-labelledby": cx(ariaLabelledbyProp, ariaLabelledby, labelId),
      role: "group",
      ...rest,
      ...props,
    }),
    [ariaLabelledbyProp, labelId, rest],
  )

  const getLabelProps: PropGetter<"span"> = useCallback(
    (props) => ({ id: labelId, role: "presentation", ...props }),
    [labelId],
  )

  return { getGroupProps, getLabelProps }
}

export type UseMenuGroupReturn = ReturnType<typeof useMenuGroup>

export interface UseMenuItemProps extends HTMLProps {
  /**
   * If `true`, the menu item will be closed when selected.
   */
  closeOnSelect?: boolean
  /**
   * If `true`, the menu item will be disabled.
   *
   * @default false
   */
  disabled?: boolean
  /**
   * The value of the menu item.
   */
  value?: string
}

export const useMenuItem = ({
  id,
  "aria-disabled": ariaDisabled,
  "data-disabled": dataDisabled,
  "data-trigger": dataTrigger,
  closeOnSelect,
  disabled = false,
  value,
  ...rest
}: UseMenuItemProps) => {
  const trigger = isTruthyDataAttr(dataTrigger)
  const {
    subMenu,
    subMenuDirection,
    onActiveDescendant,
    onClose,
    onCloseSubMenu,
    onSelect,
  } = useMenuContext()
  const uuid = useId()
  const itemRef = useRef<HTMLDivElement>(null)
  const subMenuTrigger = subMenu && trigger

  id ??= uuid

  const { descendants, register } = useMenuDescendant({
    id,
    disabled: disabled || subMenuTrigger,
  })

  const onActive = useCallback(() => {
    if (disabled) return

    const descendant = descendants.value(itemRef.current)

    onActiveDescendant(descendant)
  }, [descendants, disabled, onActiveDescendant])

  const onKeyDown = useCallback(
    (ev: KeyboardEvent<HTMLDivElement>) => {
      runKeyAction(ev, {
        ArrowDown: () => {
          const descendant = descendants.enabledNextValue(itemRef.current)

          onActiveDescendant(descendant)
        },
        ArrowUp: () => {
          const descendant = descendants.enabledPrevValue(itemRef.current)

          onActiveDescendant(descendant)
        },
        End: () => {
          const descendant = descendants.enabledLastValue()

          onActiveDescendant(descendant)
        },
        Enter: () => onSelect(value, closeOnSelect),
        Home: () => {
          const descendant = descendants.enabledFirstValue()

          onActiveDescendant(descendant)
        },
        Space: () => onSelect(value, closeOnSelect),
        [subMenuDirection === "end" ? "ArrowLeft" : "ArrowRight"]: () => {
          if (!subMenu) return

          onClose()

          const descendant = descendants.firstValue()

          descendant?.node.focus()
        },
      })
    },
    [
      closeOnSelect,
      descendants,
      onActiveDescendant,
      onClose,
      onSelect,
      subMenu,
      subMenuDirection,
      value,
    ],
  )

  const getItemProps: PropGetter = useCallback(
    ({ ref, ...props } = {}) => ({
      id,
      "aria-disabled": ariaDisabled ?? ariaAttr(disabled),
      "data-disabled": dataDisabled ?? dataAttr(disabled),
      role: "menuitem",
      tabIndex: -1,
      ...rest,
      ...props,
      ref: mergeRefs(ref, rest.ref, itemRef, register),
      onClick: handlerAll(props.onClick, rest.onClick, () =>
        onSelect(value, closeOnSelect),
      ),
      onFocus: handlerAll(props.onFocus, rest.onFocus, onActive),
      onKeyDown: handlerAll(props.onKeyDown, rest.onKeyDown, onKeyDown),
      onMouseMove: handlerAll(props.onMouseMove, rest.onMouseMove, () => {
        onCloseSubMenu()
        onActive()
      }),
    }),
    [
      id,
      ariaDisabled,
      disabled,
      dataDisabled,
      rest,
      register,
      onActive,
      onKeyDown,
      onSelect,
      value,
      closeOnSelect,
      onCloseSubMenu,
    ],
  )

  return {
    subMenuTrigger,
    getItemProps,
  }
}

export type UseMenuItemReturn = ReturnType<typeof useMenuItem>

export type MenuOptionGroupType = "checkbox" | "radio"
export type MenuOptionGroupValue<Y extends MenuOptionGroupType> =
  Y extends "checkbox" ? string[] : string

export interface UseMenuOptionGroupProps<
  Y extends MenuOptionGroupType = "checkbox",
  M extends MenuOptionGroupValue<Y> = MenuOptionGroupValue<Y>,
> {
  /**
   * The type of the menu option group.
   */
  type?: Y
  /**
   * The initial value of the menu item group.
   */
  defaultValue?: M
  /**
   * The value of the menu item group.
   */
  value?: M
  /**
   * The callback fired when any children checkbox is checked or unchecked.
   */
  onChange?: (value: M) => void
}

export const useMenuOptionGroup = <
  Y extends "checkbox" | "radio" = "checkbox",
  M extends Y extends "checkbox" ? string[] : string = Y extends "checkbox"
    ? string[]
    : string,
>({
  type = "checkbox" as Y,
  defaultValue = (type === "checkbox" ? [] : "") as M,
  value: valueProp,
  onChange: onChangeProp,
}: UseMenuOptionGroupProps<Y, M>) => {
  const [value, setValue] = useControllableState({
    defaultValue,
    value: valueProp,
    onChange: onChangeProp,
  })
  const radio = type === "radio"

  const onChange = useCallback(
    (selectedValue: string) => {
      setValue((prev) => {
        if (radio && isString(prev)) {
          return selectedValue as M
        } else if (!radio && isArray(prev)) {
          return (
            prev.includes(selectedValue)
              ? prev.filter((value) => value !== selectedValue)
              : prev.concat(selectedValue)
          ) as M
        } else {
          return prev
        }
      })
    },
    [radio, setValue],
  )

  useUpdateEffect(() => {
    setValue(valueProp!)
  }, [valueProp])

  return {
    type,
    value,
    onChange,
  }
}

export type UseMenuOptionGroupReturn = ReturnType<typeof useMenuOptionGroup>

export interface UseMenuOptionItemProps extends UseMenuItemProps {
  /**
   * The value of the menu option item.
   */
  value: string
}

export const useMenuOptionItem = ({
  disabled,
  value,
  ...rest
}: UseMenuOptionItemProps) => {
  const { type, value: selectedValue, onChange } = useMenuOptionGroupContext()
  const { getItemProps } = useMenuItem({ disabled, value, ...rest })
  const radio = type === "radio" && isString(selectedValue)
  const checkbox = type === "checkbox" && isArray(selectedValue)
  const selected = radio
    ? value === selectedValue
    : checkbox
      ? selectedValue.includes(value)
      : false

  const getIndicatorProps: PropGetter = useCallback(
    ({ style, ...props } = {}) => ({
      style: { opacity: selected ? 1 : 0, ...style },
      ...props,
    }),
    [selected],
  )

  const getOptionItemProps: PropGetter = useCallback(
    (props = {}) =>
      getItemProps({
        role: radio ? "menuitemradio" : "menuitemcheckbox",
        ...props,
        onClick: handlerAll(props.onClick, () =>
          !disabled ? onChange?.(value) : noop,
        ),
      }),
    [disabled, getItemProps, onChange, radio, value],
  )

  return {
    type,
    selected,
    getIndicatorProps,
    getOptionItemProps,
  }
}

export type UseMenuOptionItemReturn = ReturnType<typeof useMenuOptionItem>
