import type { ComponentType } from "react"
import type { DOMElement, StyledOptions } from "@yamada-ui/react"
import type { MotionStyledComponent } from "./index.types"
import { motion as _motion } from "motion/react"
import { styled } from "@yamada-ui/react"

type Components = {
  [Y in DOMElement]: MotionStyledComponent<Y>
}

interface Factory extends Components {
  <Y extends DOMElement = DOMElement, M extends object = {}>(
    el: Y,
    options?: StyledOptions,
  ): MotionStyledComponent<Y, M>
}

function factory() {
  const cache = new Map<DOMElement, ComponentType<any>>()

  return new Proxy(styled, {
    apply: function (
      _target,
      _thisArg,
      [el, options]: [DOMElement, StyledOptions],
    ) {
      const component = styled(el, options) as ComponentType<any>

      return _motion.create(component)
    },

    get: function (_target, el: DOMElement) {
      if (!cache.has(el)) {
        const component = styled(el) as ComponentType<any>

        cache.set(el, _motion.create(component))
      }

      return cache.get(el)
    },
  }) as Factory
}

/**
 * `motion` is a component that allows for the easy implementation of a wide variety of animations.
 *
 * @see https://yamada-ui.com/docs/components/motion
 */
export const motion = factory()

"use client"

import type { Dict } from "@yamada-ui/react"
import type { HTMLMotionProps, MotionStyledComponent } from "./index.types"
import { motion } from "motion/react"
import { useMemo } from "react"
import { styled, useSystem, cx } from "@yamada-ui/react"

const forwardProps = ["transition"]

const Component = styled<"div", Dict>("div", { forwardProps })

/**
 * `Motion` is a component that allows for the easy implementation of a wide variety of animations.
 *
 * @see https://yamada-ui.com/docs/components/motion
 */
export const Motion = (({
  ref,
  as: asProp = "div",
  className,
  ...rest
}: HTMLMotionProps) => {
  const system = useSystem()
  const as = useMemo(() => motion.create(asProp), [asProp])

  return (
    <Component
      ref={ref}
      as={as}
      className={cx(system.utils.getClassName("motion"), className)}
      {...rest}
    />
  )
}) as MotionStyledComponent<"div">

import type { Transition } from "motion/react"
import type { MotionTransitionProps } from "./index.types"
import { isNumber } from "@yamada-ui/react"

export const createTransition = {
  enter: function (transition?: Transition) {
    return function (
      delay?: MotionTransitionProps["delay"],
      duration?: MotionTransitionProps["duration"],
    ): Transition {
      return {
        ...(transition ?? {
          duration: 0.2,
          ease: [0, 0, 0.58, 1],
        }),
        ...(duration
          ? { duration: isNumber(duration) ? duration : duration.enter }
          : {}),
        delay: isNumber(delay) ? delay : delay?.enter,
      }
    }
  },
  exit: function (transition?: Transition) {
    return function (
      delay?: MotionTransitionProps["delay"],
      duration?: MotionTransitionProps["duration"],
    ): Transition {
      return {
        ...(transition ?? {
          duration: 0.2,
          ease: [0.42, 0, 1, 1],
        }),
        ...(duration
          ? { duration: isNumber(duration) ? duration : duration.exit }
          : {}),
        delay: isNumber(delay) ? delay : delay?.exit,
      }
    }
  },
}
