import { defineComponentSlotStyle } from "@yamada-ui/react"
import { buttonStyle } from "../button"
import {
  getInputHeightStyle,
  getInputPaddingResetStyle,
  inputStyle,
} from "../input"

export const passwordInputStyle = defineComponentSlotStyle({
  base: {
    button: {
      ...buttonStyle.base,
      rounded: "l1",
      _readOnly: { cursor: "pointer" },
    },
    field: inputStyle.base,
    root: {},
  },

  variants: {
    base: {
      button: {
        layerStyle: "ghost",
        focusVisibleRing: "none",
        _hover: {
          layerStyle: "ghost.hover",
        },
        _focusVisible: {
          layerStyle: "ghost.hover",
        },
      },
    },

    filled: {
      field: inputStyle.variants?.filled,
    },
    flushed: {
      field: inputStyle.variants?.flushed,
      root: getInputPaddingResetStyle("& > input"),
    },
    outline: {
      field: inputStyle.variants?.outline,
    },
    plain: {
      field: inputStyle.variants?.plain,
    },
  },

  sizes: {
    xs: {
      button: {
        boxSize: `calc({--input-height} - {spaces.2})`,
        fontSize: inputStyle.sizes?.xs.fontSize,
      },
      field: inputStyle.sizes?.xs,
      root: getInputHeightStyle(inputStyle.sizes?.xs.minH, "& > input"),
    },
    sm: {
      button: {
        boxSize: `calc({--input-height} - {spaces.2})`,
        fontSize: inputStyle.sizes?.sm.fontSize,
      },
      field: inputStyle.sizes?.sm,
      root: getInputHeightStyle(inputStyle.sizes?.sm.minH, "& > input"),
    },
    md: {
      button: {
        boxSize: `calc({--input-height} - {spaces.2})`,
        fontSize: inputStyle.sizes?.md.fontSize,
      },
      field: inputStyle.sizes?.md,
      root: getInputHeightStyle(inputStyle.sizes?.md.minH, "& > input"),
    },
    lg: {
      button: {
        boxSize: `calc({--input-height} - {spaces.2.5})`,
        fontSize: inputStyle.sizes?.lg.fontSize,
      },
      field: inputStyle.sizes?.lg,
      root: getInputHeightStyle(inputStyle.sizes?.lg.minH, "& > input"),
    },
    xl: {
      button: {
        boxSize: `calc({--input-height} - {spaces.3})`,
        fontSize: inputStyle.sizes?.xl.fontSize,
      },
      field: inputStyle.sizes?.xl,
      root: getInputHeightStyle(inputStyle.sizes?.xl.minH, "& > input"),
    },
    "2xl": {
      button: {
        boxSize: `calc({--input-height} - {spaces.3})`,
        fontSize: inputStyle.sizes?.["2xl"].fontSize,
      },
      field: inputStyle.sizes?.["2xl"],
      root: getInputHeightStyle(inputStyle.sizes?.["2xl"]?.minH, "& > input"),
    },
  },

  defaultProps: {
    size: "md",
    variant: "outline",
  },
})

export type PasswordInputStyle = typeof passwordInputStyle

"use client"

import type { ReactNode } from "react"
import type {
  HTMLStyledProps,
  ThemeProps,
  WithoutThemeProps,
} from "@yamada-ui/react"
import type { InputProps } from "../input"
import type { PasswordInputStyle } from "./password-input.style"
import type { UsePasswordInputProps } from "./use-password-input"
import { createSlotComponent } from "@yamada-ui/react"
import { useGroupItemProps } from "../group"
import { EyeIcon, EyeOffIcon } from "../icon"
import { Input, InputGroup, useInputPropsContext } from "../input"
import { passwordInputStyle } from "./password-input.style"
import { usePasswordInput } from "./use-password-input"

export interface PasswordInputProps
  extends WithoutThemeProps<InputProps, PasswordInputStyle>,
    ThemeProps<PasswordInputStyle>,
    UsePasswordInputProps {
  /**
   * The icons to be used for the visibility toggle.
   */
  visibilityIcon?: { off: ReactNode; on: ReactNode }
  /**
   * The props for the button element.
   */
  buttonProps?: PasswordInputButtonProps
  /**
   * The props for the end element.
   */
  elementProps?: InputGroup.ElementProps
  /**
   * The props for the root element.
   */
  rootProps?: InputGroup.RootProps
}

const {
  PropsContext: PasswordInputPropsContext,
  usePropsContext: usePasswordInputPropsContext,
  withContext,
  withProvider,
} = createSlotComponent<PasswordInputProps, PasswordInputStyle>(
  "password-input",
  passwordInputStyle,
)

export { PasswordInputPropsContext, usePasswordInputPropsContext }

/**
 * `PasswordInput` is a component that allows users to input passwords with a visibility toggle.
 *
 * @see https://yamada-ui.com/components/password-input
 */
export const PasswordInput = withProvider<"input", PasswordInputProps>(
  ({
    className,
    css,
    colorScheme,
    visibilityIcon = { off: <EyeOffIcon />, on: <EyeIcon /> },
    buttonProps,
    elementProps,
    rootProps,
    ...props
  }) => {
    const [groupItemProps, rest] = useGroupItemProps(props)
    const { visible, getButtonProps, getInputProps } = usePasswordInput(rest)

    return (
      <InputGroup.Root
        className={className}
        css={css}
        colorScheme={colorScheme}
        {...groupItemProps}
        {...rootProps}
      >
        <PasswordInputField {...getInputProps()} />

        <InputGroup.Element clickable {...elementProps}>
          <PasswordInputButton {...getButtonProps(buttonProps)}>
            {visible ? visibilityIcon.off : visibilityIcon.on}
          </PasswordInputButton>
        </InputGroup.Element>
      </InputGroup.Root>
    )
  },
  "root",
)((props) => {
  const context = useInputPropsContext()

  return { ...context, ...props }
})

interface PasswordInputFieldProps extends InputProps {}

const PasswordInputField = withContext<"input", PasswordInputFieldProps>(
  Input,
  "field",
)({ "data-group-propagate": "" })

interface PasswordInputButtonProps extends HTMLStyledProps<"button"> {}

const PasswordInputButton = withContext<"button", PasswordInputButtonProps>(
  "button",
  "button",
)()

import { defineComponentSlotStyle } from "@yamada-ui/react"

export const strengthMeterStyle = defineComponentSlotStyle({
  base: {
    indicator: {
      bg: "bg.subtle",
      flex: "1",
      h: "1",
      rounded: "l1",
    },
    indicators: {
      display: "flex",
      gap: "sm",
      transitionDuration: "moderate",
      transitionProperty: "common",
      w: "full",
    },
    label: {
      color: "fg.muted",
      fontSize: "xs",
    },
    root: {
      alignItems: "end",
      display: "flex",
      flexDirection: "column",
      gap: "xs",
      w: "full",
    },
  },
})

export type StrengthMeterStyle = typeof strengthMeterStyle

"use client"

import type { ReactNode } from "react"
import type { HTMLStyledProps } from "@yamada-ui/react"
import type { StrengthMeterStyle } from "./strength-meter.style"
import type { UseStrengthMeterProps } from "./use-strength-meter"
import { createSlotComponent, styled } from "@yamada-ui/react"
import { strengthMeterStyle } from "./strength-meter.style"
import { useStrengthMeter } from "./use-strength-meter"

interface IndicatorProps extends HTMLStyledProps {
  label?: ReactNode
}

export interface StrengthMeterProps
  extends HTMLStyledProps,
    UseStrengthMeterProps {
  /**
   * If `true`, the password strength meter will display the label.
   *
   * @default true
   */
  withLabel?: boolean
  /**
   * The props of the password strength meter indicator.
   */
  getIndicatorProps?: (percent: number) => IndicatorProps
}

const {
  PropsContext: StrengthMeterPropsContext,
  usePropsContext: useStrengthMeterPropsContext,
  withContext,
  withProvider,
} = createSlotComponent<StrengthMeterProps, StrengthMeterStyle>(
  "strength-meter",
  strengthMeterStyle,
)

export { StrengthMeterPropsContext, useStrengthMeterPropsContext }

export const StrengthMeter = withProvider<"div", StrengthMeterProps>(
  ({
    max = 4,
    value,
    withLabel = true,
    getIndicatorProps: getCustomIndicatorProps,
    ...rest
  }) => {
    const percent = (value / max) * 100

    const getDefaultIndicatorProps = (percent: number): IndicatorProps => {
      switch (true) {
        case percent < 33:
          return { label: "Low", _selected: { bg: "red.500" } }
        case percent < 66:
          return { label: "Medium", _selected: { bg: "orange.500" } }
        default:
          return { label: "High", _selected: { bg: "green.500" } }
      }
    }

    getCustomIndicatorProps ??= getDefaultIndicatorProps

    const { label, ...indicatorProps } = getCustomIndicatorProps(percent)

    const { getIndicatorProps, getRootProps } = useStrengthMeter({
      max,
      value,
      ...rest,
    })

    return (
      <styled.div {...getRootProps()}>
        <StrengthMeterIndicators>
          {Array.from({ length: max }).map((_, index) => (
            <StrengthMeterIndicator
              key={index}
              {...getIndicatorProps({ index, ...indicatorProps })}
            />
          ))}
        </StrengthMeterIndicators>

        {withLabel && label ? (
          <StrengthMeterLabel>{label}</StrengthMeterLabel>
        ) : null}
      </styled.div>
    )
  },
  "root",
)()

interface StrengthMeterIndicatorsProps extends HTMLStyledProps {}

const StrengthMeterIndicators = withContext<
  "div",
  StrengthMeterIndicatorsProps
>("div", "indicators")()

interface StrengthMeterIndicatorProps extends HTMLStyledProps {}

const StrengthMeterIndicator = withContext<"div", StrengthMeterIndicatorProps>(
  "div",
  "indicator",
)()

interface StrengthMeterLabelProps extends HTMLStyledProps<"span"> {}

const StrengthMeterLabel = withContext<"span", StrengthMeterLabelProps>(
  "span",
  "label",
)()

"use client"

import type { MouseEvent } from "react"
import type { HTMLProps, PropGetter } from "@yamada-ui/react"
import type { FieldProps } from "../field"
import { useCallback } from "react"
import { useControllableState } from "../../hooks/use-controllable-state"
import { useI18n } from "../../providers/i18n-provider"
import { handlerAll, mergeRefs } from "@yamada-ui/react"
import { useFieldProps } from "../field"

export interface UsePasswordInputProps extends HTMLProps<"input">, FieldProps {
  /**
   * Determines whether the password input is visible by default.
   *
   * @default false
   */
  defaultVisible?: boolean
  /**
   * Determines the visibility of the password input.
   *
   * @default false
   */
  visible?: boolean
  /**
   * Callback function that is triggered when the visibility of the password input changes.
   */
  onVisibleChange?: (visible: boolean) => void
}

export const usePasswordInput = (props: UsePasswordInputProps = {}) => {
  const {
    props: {
      defaultVisible,
      disabled,
      visible: visibleProp,
      onVisibleChange,
      ...rest
    },
    ariaProps,
    dataProps,
    eventProps,
  } = useFieldProps(props)
  const [visible, setVisible] = useControllableState({
    defaultValue: defaultVisible,
    value: visibleProp,
    onChange: onVisibleChange,
  })
  const { t } = useI18n("passwordInput")

  const onChangeVisibility = useCallback(
    (ev: MouseEvent<HTMLButtonElement>) => {
      if (disabled || ev.button !== 0) return

      ev.preventDefault()

      setVisible((prev) => !prev)
    },
    [disabled, setVisible],
  )

  const getInputProps: PropGetter<"input"> = useCallback(
    ({ ref, ...props } = {}) => ({
      type: visible ? "text" : "password",
      disabled,
      ...ariaProps,
      ...dataProps,
      ...rest,
      ...props,
      ref: mergeRefs(ref, rest.ref),
      onBlur: handlerAll(eventProps.onBlur, props.onBlur),
      onFocus: handlerAll(eventProps.onFocus, props.onFocus),
    }),
    [visible, rest, disabled, dataProps, eventProps, ariaProps],
  )

  const getButtonProps: PropGetter<"button"> = useCallback(
    (props = {}) => ({
      type: "button",
      "aria-label": t("Toggle password visibility"),
      disabled,
      ...dataProps,
      ...props,
      onClick: handlerAll(props.onClick, onChangeVisibility),
    }),
    [onChangeVisibility, dataProps, disabled, t],
  )

  return {
    setVisible,
    visible,
    getButtonProps,
    getInputProps,
  }
}

export type UsePasswordInputReturn = ReturnType<typeof usePasswordInput>

"use client"

import type {
  HTMLProps,
  PropGetter,
  RequiredPropGetter,
} from "@yamada-ui/react"
import { useCallback } from "react"
import { useI18n } from "../../providers/i18n-provider"
import { dataAttr } from "@yamada-ui/react"

export interface UseStrengthMeterProps extends HTMLProps {
  /**
   * The value of the password strength meter.
   */
  value: number
  /**
   * The maximum value of the password strength meter.
   *
   * @default 4
   */
  max?: number
}

export const useStrengthMeter = ({
  max = 4,
  value,
  ...rest
}: UseStrengthMeterProps) => {
  const { t } = useI18n("passwordInput")

  const getRootProps: PropGetter = useCallback(
    (props) => ({
      "aria-label": t("Password strength meter"),
      "aria-valuemax": max,
      "aria-valuemin": 0,
      "aria-valuenow": value,
      role: "meter",
      ...rest,
      ...props,
    }),
    [max, rest, value, t],
  )

  const getIndicatorProps: RequiredPropGetter<"div", { index: number }> =
    useCallback(
      ({ index, ...props }) => ({
        "data-selected": dataAttr(index < value),
        ...props,
      }),
      [value],
    )

  return { getIndicatorProps, getRootProps }
}

export type UseStrengthMeterReturn = ReturnType<typeof useStrengthMeter>
