import { defineComponentSlotStyle } from "@yamada-ui/react"

export const popoverStyle = defineComponentSlotStyle({
  base: {
    body: {
      alignItems: "flex-start",
      display: "flex",
      flex: "1",
      flexDirection: "column",
      gap: "{gap}",
    },
    content: {
      bg: "bg.float",
      borderColor: "border.muted",
      borderWidth: "1px",
      boxShadow: "sm",
      display: "flex",
      flexDirection: "column",
      focusVisibleRing: "none",
      gap: "{gap}",
      p: "{padding}",
      rounded: "l2",
    },
    footer: {
      alignItems: "center",
      display: "flex",
      gap: "{gap}",
      justifyContent: "flex-start",
    },
    header: {
      alignItems: "center",
      display: "flex",
      fontWeight: "semibold",
      gap: "{gap}",
      justifyContent: "flex-start",
    },
    positioner: {
      zIndex: "recoome",
    },
  },

  sizes: {
    xs: {
      content: {
        "--gap": "spaces.1",
        "--padding": "spaces.2",
        fontSize: "xs",
      },
    },
    sm: {
      content: {
        "--gap": "spaces.2",
        "--padding": "spaces.3",
        fontSize: "sm",
      },
    },
    md: {
      content: {
        "--gap": "spaces.3",
        "--padding": "spaces.4",
        fontSize: "md",
      },
    },
    lg: {
      content: {
        "--gap": "spaces.4",
        "--padding": "spaces.5",
        fontSize: "md",
      },
    },
  },

  defaultProps: {
    size: "md",
  },
})

export type PopoverStyle = typeof popoverStyle

"use client"

import type { FC, PropsWithChildren } from "react"
import type {
  HTMLProps,
  HTMLStyledProps,
  SimplePlacement,
  ThemeProps,
  ReactNodeOrFunction,
} from "@yamada-ui/react"
import type { HTMLMotionProps, MotionTransitionProps } from "../motion"
import type { PortalProps } from "../portal"
import type { PopoverStyle } from "./popover.style"
import type { UsePopoverProps, UsePopoverReturn } from "./use-popover"
import { AnimatePresence } from "motion/react"
import { useMemo } from "react"
import { createSlotComponent, cast, runIfFn } from "@yamada-ui/react"
import { fadeScaleVariants } from "../fade-scale"
import { motion } from "../motion"
import { Portal } from "../portal"
import { slideFadeVariants } from "../slide-fade"
import { popoverStyle } from "./popover.style"
import { usePopover } from "./use-popover"

export interface PopupAnimationProps {
  /**
   * The animation of the element.
   *
   * @default 'scale'
   */
  animationScheme?: "none" | "scale" | SimplePlacement
  /**
   * The animation duration.
   *
   * @default 0.2
   */
  duration?: MotionTransitionProps["duration"]
}

export const getPopupAnimationProps = (
  animationScheme: PopupAnimationProps["animationScheme"] = "scale",
  duration?: PopupAnimationProps["duration"],
) => {
  const sharedProps = { animate: "enter", exit: "exit", initial: "exit" }

  switch (animationScheme) {
    case "scale":
      return {
        ...sharedProps,
        custom: { duration, reverse: true, scale: 0.95 },
        variants: fadeScaleVariants,
      }
    case "block-start":
      return {
        ...sharedProps,
        custom: { duration, offsetY: -16, reverse: true },
        variants: slideFadeVariants,
      }
    case "inline-end":
      return {
        ...sharedProps,
        custom: { duration, offsetX: 16, reverse: true },
        variants: slideFadeVariants,
      }
    case "inline-start":
      return {
        ...sharedProps,
        custom: { duration, offsetX: -16, reverse: true },
        variants: slideFadeVariants,
      }
    case "block-end":
      return {
        ...sharedProps,
        custom: { duration, offsetY: 16, reverse: true },
        variants: slideFadeVariants,
      }
    default:
      return {}
  }
}

interface ComponentContext
  extends
    Pick<
      UsePopoverReturn,
      | "getAnchorProps"
      | "getBodyProps"
      | "getContentProps"
      | "getFooterProps"
      | "getHeaderProps"
      | "getPositionerProps"
      | "getTriggerProps"
      | "open"
    >,
    PopupAnimationProps,
    Pick<PopoverRootProps, "withCloseButton"> {}

export interface PopoverRootProps
  extends UsePopoverProps, PopupAnimationProps, ThemeProps<PopoverStyle> {
  /**
   * The children of the popover.
   */
  children?: ReactNodeOrFunction<{
    open: boolean
    onClose: () => void
  }>
  /**
   * The animation duration.
   *
   * @default 0.2
   */
  duration?: PopupAnimationProps["duration"]
  /**
   * If `true`, display the popover close button.
   *
   * @default true
   */
  withCloseButton?: boolean
}

const {
  ComponentContext,
  PropsContext: PopoverPropsContext,
  StyleContext,
  useComponentContext,
  usePropsContext: usePopoverPropsContext,
  withContext,
  useRootComponentProps,
} = createSlotComponent<PopoverRootProps, PopoverStyle, ComponentContext>(
  "popover",
  popoverStyle,
)

export { PopoverPropsContext, usePopoverPropsContext }

/**
 * `Popover` is a component that floats around an element to display information.
 *
 * @see https://yamada-ui.com/docs/components/popover
 */
export const PopoverRoot: FC<PopoverRootProps> = (props) => {
  const [
    styleContext,
    {
      animationScheme = "scale",
      children,
      duration = 0.1,
      withCloseButton = true,
      ...rest
    },
  ] = useRootComponentProps(props)
  const {
    open,
    getAnchorProps,
    getBodyProps,
    getContentProps,
    getFooterProps,
    getHeaderProps,
    getPositionerProps,
    getTriggerProps,
    onClose,
  } = usePopover(rest)
  const componentContext = useMemo(
    () => ({
      animationScheme,
      duration,
      open,
      withCloseButton,
      getAnchorProps,
      getBodyProps,
      getContentProps,
      getFooterProps,
      getHeaderProps,
      getPositionerProps,
      getTriggerProps,
    }),
    [
      withCloseButton,
      open,
      animationScheme,
      duration,
      getAnchorProps,
      getBodyProps,
      getContentProps,
      getFooterProps,
      getHeaderProps,
      getPositionerProps,
      getTriggerProps,
    ],
  )

  return (
    <StyleContext value={styleContext}>
      <ComponentContext value={componentContext}>
        {runIfFn(children, { open, onClose })}
      </ComponentContext>
    </StyleContext>
  )
}

export interface PopoverTriggerProps extends HTMLStyledProps<"button"> {}

export const PopoverTrigger = withContext<"button", PopoverTriggerProps>(
  "button",
  "trigger",
)({ asChild: true }, (props) => {
  const { getTriggerProps } = useComponentContext()

  return getTriggerProps(props)
})

export interface PopoverAnchorProps extends HTMLStyledProps {}

export const PopoverAnchor = withContext<"div", PopoverAnchorProps>(
  "div",
  "anchor",
)({ asChild: true }, (props) => {
  const { getAnchorProps } = useComponentContext()

  return getAnchorProps(props)
})

interface PopoverPositionerProps extends HTMLStyledProps {}

const PopoverPositioner = withContext<"div", PopoverPositionerProps>(
  "div",
  "positioner",
)(undefined, (props) => {
  const { getPositionerProps } = useComponentContext()

  return getPositionerProps(props)
})

export interface PopoverContentProps
  extends Omit<HTMLMotionProps, "children">, PropsWithChildren {
  /**
   * Props for portal component.
   */
  portalProps?: Omit<PortalProps, "children">
}

export const PopoverContent = withContext<"div", PopoverContentProps>(
  ({ portalProps, ...rest }) => {
    const { animationScheme, duration, open, getContentProps } =
      useComponentContext()

    return (
      <AnimatePresence>
        {open ? (
          <Portal {...portalProps}>
            <PopoverPositioner>
              <motion.div
                {...getPopupAnimationProps(animationScheme, duration)}
                {...cast<HTMLMotionProps>(
                  getContentProps(cast<HTMLProps>(rest)),
                )}
              />
            </PopoverPositioner>
          </Portal>
        ) : null}
      </AnimatePresence>
    )
  },
  "content",
)()

export interface PopoverHeaderProps extends HTMLStyledProps {}

export const PopoverHeader = withContext<"div", PopoverHeaderProps>(
  "div",
  "header",
)(undefined, (props) => {
  const { getHeaderProps } = useComponentContext()

  return getHeaderProps(props)
})

export interface PopoverBodyProps extends HTMLStyledProps {}

export const PopoverBody = withContext<"div", PopoverBodyProps>("div", "body")(
  undefined,
  (props) => {
    const { getBodyProps } = useComponentContext()

    return getBodyProps(props)
  },
)

export interface PopoverFooterProps extends HTMLStyledProps {}

export const PopoverFooter = withContext<"div", PopoverFooterProps>(
  "div",
  "footer",
)(undefined, (props) => {
  const { getFooterProps } = useComponentContext()

  return getFooterProps(props)
})

"use client"

import type { FocusEvent, KeyboardEvent, RefObject } from "react"
import type { Direction, PropGetter, Dict } from "@yamada-ui/react"
import type { UseDisclosureProps } from "../../hooks/use-disclosure"
import type { UsePopperProps } from "../../hooks/use-popper"
import type { PopupAnimationProps } from "./popover"
import { useCallback, useEffect, useId, useRef } from "react"
import {
  useEnvironment,
  useSplitProps,
  ariaAttr,
  assignRef,
  contains,
  dataAttr,
  focusTransfer,
  focusTrap,
  getEventRelatedTarget,
  handlerAll,
  mergeRefs,
  runKeyAction,
  scrollLock,
  setAttribute,
  useSafeLayoutEffect,
  useUnmountEffect,
} from "@yamada-ui/react"
import { useDisclosure } from "../../hooks/use-disclosure"
import { useEventListener } from "../../hooks/use-event-listener"
import { useFocusOnShow } from "../../hooks/use-focus"
import { useOutsideClick } from "../../hooks/use-outside-click"
import { popperProps, usePopper } from "../../hooks/use-popper"

export interface UsePopoverProps
  extends Omit<UseDisclosureProps, "timing">, UsePopperProps<"button"> {
  /**
   * If `true`, focus will be transferred to the first interactive element when the popover opens.
   *
   * @default true
   */
  autoFocus?: boolean
  /**
   * If `true`, scrolling will be disabled on the `body` when the modal opens.
   *
   * @default false
   */
  blockScrollOnMount?: boolean
  /**
   * If `true`, the popover will close when you blur out it by clicking outside or tabbing out.
   *
   * @default true
   */
  closeOnBlur?: boolean
  /**
   * If `true`, the popover will hide on pressing Esc key.
   *
   * @default true
   */
  closeOnEsc?: boolean
  /**
   * If `true`, the popover will hide on scroll.
   *
   * @default false
   */
  closeOnScroll?: boolean
  /**
   * If `true`, the popover will be disabled.
   *
   * @default false
   */
  disabled?: boolean
  /**
   * The `ref` of the element that should receive focus when the popover opens.
   */
  initialFocusRef?: RefObject<HTMLElement | null>
  /**
   * If `true`, the popover will be modal.
   *
   * - scrolling is blocked.
   * - focus is trapped within the popover.
   *
   * @default false
   */
  modal?: boolean
  /**
   * If `true`, the popover will be opened when click on the field.
   *
   * @default true
   */
  openOnClick?: boolean
  /**
   * The placement of the popper relative to its reference.
   *
   * @default 'end'
   */
  placement?: Direction
  /**
   * If `true`, the focus will be transferred to the popover content when the tab key is pressed.
   *
   * @default true
   */
  transferFocus?: boolean
  /**
   * Update the position of the floating element, re-rendering the component if required.
   */
  updateRef?: RefObject<() => void>
}

export const usePopover = ({
  autoFocus = true,
  autoUpdate,
  modal = false,
  blockScrollOnMount = modal,
  closeOnBlur = true,
  closeOnEsc = true,
  closeOnScroll,
  defaultOpen,
  disabled,
  elements,
  flip,
  gutter,
  initialFocusRef,
  matchWidth,
  middleware,
  offset,
  open: openProp,
  openOnClick = true,
  placement = "end",
  platform,
  preventOverflow,
  strategy,
  transferFocus = true,
  transform,
  updateRef,
  whileElementsMounted,
  onClose: onCloseProp,
  onOpen: onOpenProp,
}: UsePopoverProps = {}) => {
  const { getDocument } = useEnvironment()
  const headerId = useId()
  const bodyId = useId()
  const contentId = useId()
  const anchorRef = useRef<HTMLElement>(null)
  const triggerRef = useRef<HTMLButtonElement>(null)
  const contentRef = useRef<HTMLElement>(null)
  const openTimeout = useRef<NodeJS.Timeout>(undefined)
  const closeTimeout = useRef<NodeJS.Timeout>(undefined)
  const { open, onClose, onOpen } = useDisclosure({
    defaultOpen,
    open: openProp,
    onClose: onCloseProp,
    onOpen: onOpenProp,
  })
  const { refs, update, getPopperProps } = usePopper<"button">({
    autoUpdate,
    elements,
    flip,
    gutter,
    matchWidth,
    middleware,
    offset,
    open,
    placement,
    platform,
    preventOverflow,
    strategy,
    transform,
    whileElementsMounted,
  })

  assignRef(updateRef, update)

  const onKeyDown = useCallback(
    (ev: KeyboardEvent<HTMLElement>) => {
      runKeyAction(ev, {
        Escape: () => {
          if (!closeOnEsc) return

          onClose()

          triggerRef.current?.focus()
        },
      })
    },
    [closeOnEsc, onClose],
  )

  const onBlur = useCallback(
    (ev: FocusEvent<HTMLDivElement>) => {
      const relatedTarget = getEventRelatedTarget(ev)
      const popup = relatedTarget?.hasAttribute("data-popup")

      if (contains(triggerRef.current, relatedTarget)) return
      if (contains(contentRef.current, relatedTarget)) return
      if (contains(contentRef.current, ev.target) && popup) return

      if (closeOnBlur) onClose()
    },
    [closeOnBlur, onClose],
  )

  useEventListener(getDocument(), "scroll", () => {
    if (open && closeOnScroll) onClose()
  })

  useFocusOnShow(contentRef, {
    focusTarget: initialFocusRef,
    shouldFocus: autoFocus,
    visible: open,
  })

  useOutsideClick({
    ref: [contentRef, triggerRef],
    enabled: open && closeOnBlur,
    handler: onClose,
  })

  useSafeLayoutEffect(() => {
    const el = contentRef.current
    const hasHeader = !!getDocument()?.getElementById(headerId)
    const hasBody = !!getDocument()?.getElementById(bodyId)

    if (el && hasHeader) setAttribute(el, "aria-labelledby", headerId)
    if (el && hasBody) setAttribute(el, "aria-describedby", bodyId)
  }, [open, headerId, bodyId])

  useEffect(() => {
    if (!open || !modal) return

    return focusTrap(contentRef.current)
  }, [open, modal])

  useEffect(() => {
    if (!open || !blockScrollOnMount) return

    return scrollLock(contentRef.current)
  }, [open, modal, blockScrollOnMount])

  useEffect(() => {
    if (!open || modal || !transferFocus) return

    return focusTransfer(contentRef.current, triggerRef.current)
  }, [open, modal, transferFocus])

  useUnmountEffect(() => {
    clearTimeout(openTimeout.current)
    clearTimeout(closeTimeout.current)
  })

  const getTriggerProps: PropGetter<"button"> = useCallback(
    ({ ref, ...props } = {}) => ({
      "aria-controls": open ? contentId : undefined,
      "aria-disabled": ariaAttr(disabled),
      "aria-expanded": open,
      "aria-haspopup": "dialog",
      role: "button",
      ...props,
      ref: mergeRefs(ref, triggerRef, (node) => {
        if (anchorRef.current == null) refs.setReference(node)
      }),
      onBlur: handlerAll(props.onBlur, (ev) =>
        !contains(contentRef.current, getEventRelatedTarget(ev)) && closeOnBlur
          ? onClose()
          : void 0,
      ),
      onClick: handlerAll(
        props.onClick,
        !open ? (!disabled && openOnClick ? onOpen : undefined) : onClose,
      ),
      onKeyDown: handlerAll(props.onKeyDown, onKeyDown),
    }),
    [
      closeOnBlur,
      contentId,
      disabled,
      onClose,
      onKeyDown,
      onOpen,
      open,
      openOnClick,
      refs,
    ],
  )

  const getAnchorProps: PropGetter = useCallback(
    ({ ref, ...props } = {}) => ({
      ...props,
      ref: mergeRefs(ref, anchorRef, refs.setReference),
    }),
    [refs.setReference],
  )

  const getPositionerProps: PropGetter = useCallback(
    (props) => {
      return getPopperProps(props)
    },
    [getPopperProps],
  )

  const getContentProps: PropGetter = useCallback(
    ({ ref, ...props } = {}) => ({
      id: contentId,
      "aria-hidden": !open,
      "aria-modal": modal ? "true" : undefined,
      "data-close": dataAttr(!open),
      "data-open": dataAttr(open),
      "data-popup": dataAttr(true),
      role: "dialog",
      tabIndex: -1,
      ...props,
      ref: mergeRefs(ref, contentRef),
      onBlur: handlerAll(props.onBlur, onBlur),
      onKeyDown: handlerAll(props.onKeyDown, onKeyDown),
    }),
    [contentId, open, modal, onBlur, onKeyDown],
  )

  const getHeaderProps: PropGetter = useCallback(
    (props) => ({
      id: headerId,
      ...props,
    }),
    [headerId],
  )

  const getBodyProps: PropGetter = useCallback(
    (props) => ({
      id: bodyId,
      ...props,
    }),
    [bodyId],
  )

  const getFooterProps: PropGetter = useCallback((props) => ({ ...props }), [])

  return {
    open,
    getAnchorProps,
    getBodyProps,
    getContentProps,
    getFooterProps,
    getHeaderProps,
    getPositionerProps,
    getTriggerProps,
    onClose,
    onOpen,
  }
}

export type UsePopoverReturn = ReturnType<typeof usePopover>

export const popoverProps: (
  | keyof PopupAnimationProps
  | keyof UsePopoverProps
)[] = [
  ...popperProps,
  "autoFocus",
  "transferFocus",
  "blockScrollOnMount",
  "closeOnBlur",
  "closeOnEsc",
  "closeOnScroll",
  "openOnClick",
  "disabled",
  "initialFocusRef",
  "modal",
  "updateRef",
  "defaultOpen",
  "onOpen",
  "onClose",
  "animationScheme",
  "duration",
]

export const usePopoverProps = <
  Y extends Dict = Dict,
  M extends keyof PopupAnimationProps | keyof UsePopoverProps =
    | keyof PopupAnimationProps
    | keyof UsePopoverProps,
>(
  props: Y,
  omitKeys?: M[],
) => {
  return useSplitProps(
    props,
    popoverProps.filter((key) => !omitKeys?.includes(key as M)),
  ) as unknown as [
    keyof PopupAnimationProps | keyof UsePopoverProps extends M
      ? PopupAnimationProps & UsePopoverProps
      : Omit<PopupAnimationProps & UsePopoverProps, M>,
    Omit<
      Y,
      keyof PopupAnimationProps | keyof UsePopoverProps extends M
        ? keyof PopupAnimationProps | keyof UsePopoverProps
        : Exclude<keyof PopupAnimationProps | keyof UsePopoverProps, M>
    >,
  ]
}
