"use client"

import type { ReactElement, ReactNode } from "react"
import type {
  GenericsComponent,
  ThemeProps,
  WithoutThemeProps,
} from "@yamada-ui/react"
import type { GroupProps } from "../group"
import type { UseInputBorderProps } from "../input"
import type { RadioProps } from "./radio"
import type { RadioStyle } from "./radio.style"
import type { UseRadioGroupProps } from "./use-radio-group"
import { useMemo } from "react"
import { Group } from "../group"
import {
  component,
  Radio,
  RadioPropsContext,
  useRootComponentProps,
} from "./radio"
import { RadioGroupContext, useRadioGroup } from "./use-radio-group"

export interface RadioItem<Y extends string = string> extends RadioProps<Y> {
  label: ReactNode
}

export interface RadioGroupRootProps<Y extends string = string>
  extends Omit<
      WithoutThemeProps<GroupProps, RadioStyle>,
      "defaultValue" | "onChange" | "value"
    >,
    ThemeProps<RadioStyle>,
    UseRadioGroupProps<Y>,
    UseInputBorderProps {
  /**
   * If provided, generate options based on items.
   *
   * @default '[]'
   */
  items?: RadioItem<Y>[]
}

/**
 * `RadioGroup` is a component that groups `Radio` components.
 *
 * @see https://yamada-ui.com/components/radio
 */
export const RadioGroupRoot = component<"div", RadioGroupRootProps>((props) => {
  const [
    ,
    {
      colorScheme,
      size,
      variant,
      children,
      disabled,
      errorBorderColor,
      focusBorderColor,
      invalid,
      items = [],
      readOnly,
      shape,
      ...rest
    },
  ] = useRootComponentProps(props, "group", {
    transferProps: ["variant", "colorScheme", "size", "shape"],
  })
  const { name, value, getInputProps, getLabelProps, getRootProps, onChange } =
    useRadioGroup(rest)
  const computedChildren = useMemo(() => {
    if (children) return children

    return items.map(({ label, value, ...rest }, index) => (
      <Radio key={value ?? index} value={value} {...rest}>
        {label}
      </Radio>
    ))
  }, [items, children])
  const context = useMemo(
    () => ({
      colorScheme,
      size,
      variant,
      disabled,
      errorBorderColor,
      focusBorderColor,
      invalid,
      readOnly,
      shape,
    }),
    [
      variant,
      size,
      colorScheme,
      shape,
      disabled,
      invalid,
      readOnly,
      errorBorderColor,
      focusBorderColor,
    ],
  )
  const groupContext = useMemo(
    () => ({ name, value, getInputProps, getLabelProps, onChange }),
    [name, value, getInputProps, getLabelProps, onChange],
  )

  return (
    <RadioPropsContext value={context}>
      <RadioGroupContext value={groupContext}>
        <Group orientation="vertical" {...getRootProps()}>
          {computedChildren}
        </Group>
      </RadioGroupContext>
    </RadioPropsContext>
  )
}, "group")() as GenericsComponent<{
  <Y extends string = string>(props: RadioGroupRootProps<Y>): ReactElement
}>

import { defineComponentSlotStyle, focusRingStyle } from "@yamada-ui/react"

export const radioStyle = defineComponentSlotStyle({
  base: {
    group: {},
    indicator: {
      alignItems: "center",
      borderColor: "{indicator-border-color}",
      borderWidth: "2px",
      boxSize: "{indicator-size}",
      display: "inline-flex",
      focusRingColor: "{focus-border-color}",
      "input:focus-visible + &": focusRingStyle.outline,
      justifyContent: "center",
      _before: { display: "block" },
      _invalid: {
        borderColor: "{error-border-color}",
        focusRingColor: "{error-border-color}",
      },
    },
    label: {},
    root: {
      "--error-border-color": "colors.border.error",
      "--focus-border-color": "colorScheme.outline",
      "--indicator-border-color": "colors.border.emphasized",
      alignItems: "center",
      display: "flex",
      fontSize: "{label-size}",
      gap: "2",
      userSelect: "none",
      _readOnly: { layerStyle: "readOnly" },
      _disabled: { layerStyle: "disabled" },
    },
  },

  props: {
    /**
     * The shape of the component
     *
     * @default 'circle'
     */
    shape: {
      circle: { indicator: { rounded: "full", _before: { rounded: "full" } } },
      rounded: {
        indicator: {
          rounded: "l1",
          _before: { rounded: "calc({radii.l1} / 2)" },
        },
      },
      square: {
        indicator: { rounded: "0", _before: { rounded: "0" } },
      },
    },
  },

  variants: {
    base: {
      indicator: { _checked: { _before: { bg: "colorScheme.solid" } } },
    },
    outline: {
      indicator: { _checked: { _before: { bg: "colorScheme.outline" } } },
      root: { _checked: { "--indicator-border-color": "colorScheme.outline" } },
    },
    solid: {},
    subtle: {
      indicator: { bg: "colorScheme.subtle" },
      root: { "--indicator-border-color": "transparent" },
    },
    surface: {
      indicator: { bg: "colorScheme.subtle" },
      root: { "--indicator-border-color": "colorScheme.muted" },
    },
  },

  sizes: {
    sm: {
      indicator: { _before: { boxSize: "1.5" } },
      root: { "--indicator-size": "sizes.3.5", "--label-size": "fontSizes.sm" },
    },
    md: {
      indicator: { _before: { boxSize: "2" } },
      root: { "--indicator-size": "sizes.4", "--label-size": "fontSizes.md" },
    },
    lg: {
      indicator: { _before: { boxSize: "3" } },
      root: { "--indicator-size": "sizes.5", "--label-size": "fontSizes.lg" },
    },
  },

  defaultProps: {
    size: "md",
    variant: "solid",
    shape: "circle",
  },
})

export type RadioStyle = typeof radioStyle

"use client"

import type { ReactElement } from "react"
import type {
  GenericsComponent,
  HTMLProps,
  HTMLStyledProps,
  ThemeProps,
  Merge,
} from "@yamada-ui/react"
import type { UseInputBorderProps } from "../input"
import type { RadioStyle } from "./radio.style"
import type { UseRadioProps } from "./use-radio"
import { useMemo } from "react"
import { createSlotComponent, styled, cast } from "@yamada-ui/react"
import { useInputBorder } from "../input"
import { radioStyle } from "./radio.style"
import { useRadio } from "./use-radio"

export interface RadioProps<Y extends string = string>
  extends Merge<HTMLStyledProps<"label">, UseRadioProps<Y>>,
    ThemeProps<RadioStyle>,
    UseInputBorderProps {
  /**
   * Props for the indicator component.
   */
  indicatorProps?: RadioIndicatorProps
  /**
   * Props for the input element.
   */
  inputProps?: HTMLStyledProps<"input">
  /**
   * Props for the label component.
   */
  labelProps?: RadioLabelProps
  /**
   * Props for the label element.
   */
  rootProps?: HTMLStyledProps<"label">
}

const {
  component,
  PropsContext: RadioPropsContext,
  usePropsContext: useRadioPropsContext,
  withContext,
  withProvider,
  useRootComponentProps,
} = createSlotComponent<RadioProps, RadioStyle>("radio", radioStyle)

export {
  component,
  RadioPropsContext,
  useRadioPropsContext,
  useRootComponentProps,
}

/**
 * `Radio` is a component used for allowing users to select one option from multiple choices.
 *
 * @see https://yamada-ui.com/components/radio
 */
export const Radio = withProvider<"label", RadioProps>(
  ({
    children,
    errorBorderColor,
    focusBorderColor,
    indicatorProps,
    inputProps,
    labelProps,
    rootProps,
    ...rest
  }) => {
    const { getIndicatorProps, getInputProps, getRootProps } = useRadio(rest)
    const varProps = useInputBorder({ errorBorderColor, focusBorderColor })
    const input = useMemo(() => {
      return <styled.input {...getInputProps(inputProps)} />
    }, [getInputProps, inputProps])
    const indicator = useMemo(() => {
      return <RadioIndicator {...getIndicatorProps(indicatorProps)} />
    }, [getIndicatorProps, indicatorProps])

    if (children) {
      return (
        <styled.label {...getRootProps({ ...varProps, ...rootProps })}>
          {input}
          {indicator}
          <RadioLabel {...labelProps}>{children}</RadioLabel>
        </styled.label>
      )
    } else {
      return (
        <styled.div
          {...cast<HTMLProps>(getRootProps({ ...varProps, ...rootProps }))}
        >
          {input}
          {indicator}
        </styled.div>
      )
    }
  },
  "root",
)() as GenericsComponent<{
  <Y extends string = string>(props: RadioProps<Y>): ReactElement
}>

interface RadioIndicatorProps extends HTMLStyledProps {}

const RadioIndicator = withContext<"div", RadioIndicatorProps>(
  "div",
  "indicator",
)()

interface RadioLabelProps extends HTMLStyledProps<"span"> {}

const RadioLabel = withContext<"span", RadioLabelProps>("span", "label")()

"use client"

import type { ChangeEvent, ChangeEventHandler, FocusEventHandler } from "react"
import type { HTMLProps, HTMLRefAttributes, PropGetter } from "@yamada-ui/react"
import type { FieldProps } from "../field"
import { useCallback, useId } from "react"
import { useControllableEventState } from "../../hooks/use-controllable-state"
import {
  cx,
  dataAttr,
  handlerAll,
  isUndefined,
  mergeRefs,
  visuallyHiddenAttributes,
} from "@yamada-ui/react"
import { useFieldProps } from "../field"
import { useRadioGroupContext } from "./use-radio-group"

export interface UseRadioProps<Y extends string = string>
  extends Omit<HTMLProps<"label">, "onBlur" | "onChange" | "onFocus" | "ref">,
    HTMLRefAttributes<"input">,
    FieldProps {
  /**
   * id assigned to input.
   */
  id?: string
  /**
   * The name of the input field in a radio.
   */
  name?: string
  /**
   * If `true`, the radio will be checked.
   */
  checked?: boolean
  /**
   * If `true`, the radio will be initially checked.
   *
   * @default false
   */
  defaultChecked?: boolean
  /**
   * The value of the radio.
   */
  value?: Y
  /**
   * The callback invoked when the radio is blurred.
   */
  onBlur?: FocusEventHandler<HTMLInputElement>
  /**
   * The callback invoked when the checked state changes.
   */
  onChange?: ChangeEventHandler<HTMLInputElement>
  /**
   * The callback invoked when the radio is focused.
   */
  onFocus?: FocusEventHandler<HTMLInputElement>
}

export const useRadio = <Y extends string = string>({
  "aria-describedby": ariaDescribedbyProp,
  ...props
}: UseRadioProps<Y> = {}) => {
  const group = useRadioGroupContext()
  const {
    name: groupName,
    value: groupValue,
    getInputProps: getGroupInputProps,
    getLabelProps,
  } = group ?? {}
  const uuid = useId()
  const {
    props: {
      id,
      ref,
      name = groupName,
      checked: checkedProp,
      defaultChecked = false,
      disabled,
      readOnly,
      required,
      value,
      onChange: onChangeProp,
      ...rest
    },
    ariaProps: { "aria-describedby": ariaDescribedby, ...ariaProps },
    dataProps,
    eventProps,
  } = useFieldProps({
    id: group ? uuid : undefined,
    ...props,
    notSupportReadOnly: true,
  })
  const interactive = !(readOnly || disabled)
  const resolvedAriaDescribedby = group ? ariaDescribedbyProp : ariaDescribedby
  const resolvedChecked =
    !isUndefined(groupValue) && !isUndefined(value)
      ? groupValue === value
      : checkedProp
  const [checked, setChecked] = useControllableEventState({
    defaultValue: defaultChecked,
    value: resolvedChecked,
    onChange: onChangeProp,
  })

  const onChange = useCallback(
    (ev: ChangeEvent<HTMLInputElement>) => {
      if (!interactive) return (ev.defaultPrevented = true)

      setChecked(ev)
    },
    [setChecked, interactive],
  )

  const getRootProps: PropGetter<"label"> = useCallback(
    (props = {}) => {
      const sharedProps = {
        ...dataProps,
        "data-checked": dataAttr(checked),
        ...rest,
        ...props,
      }

      return getLabelProps?.(sharedProps) ?? sharedProps
    },
    [dataProps, getLabelProps, checked, rest],
  )

  const getInputProps: PropGetter<"input"> = useCallback(
    ({ "aria-describedby": ariaDescribedby, ...props } = {}) => {
      const sharedProps = {
        ...dataProps,
        ...ariaProps,
        id,
        type: "radio",
        name,
        style: visuallyHiddenAttributes.style,
        "aria-checked": checked,
        "aria-describedby": cx(resolvedAriaDescribedby, ariaDescribedby),
        "data-checked": dataAttr(checked),
        checked,
        disabled,
        readOnly,
        required,
        value,
        ...props,
        ref: mergeRefs(props.ref, ref),
        onBlur: handlerAll(props.onBlur, eventProps.onBlur),
        onChange: handlerAll(props.onChange, onChange),
        onFocus: handlerAll(props.onFocus, eventProps.onFocus),
      }

      return getGroupInputProps?.(sharedProps) ?? sharedProps
    },
    [
      id,
      name,
      checked,
      resolvedAriaDescribedby,
      disabled,
      readOnly,
      required,
      value,
      dataProps,
      ariaProps,
      ref,
      eventProps,
      onChange,
      getGroupInputProps,
    ],
  )

  const getIndicatorProps: PropGetter = useCallback(
    (props = {}) => ({
      ...dataProps,
      "aria-hidden": "true",
      "data-checked": dataAttr(checked),
      ...props,
    }),
    [dataProps, checked],
  )

  return {
    checked,
    getIndicatorProps,
    getInputProps,
    getRootProps,
    onChange,
  }
}

export type UseRadioReturn = ReturnType<typeof useRadio>

"use client"

import type { ChangeEvent } from "react"
import type { HTMLProps, PropGetter } from "@yamada-ui/react"
import type { FieldProps } from "../field"
import { useCallback, useId } from "react"
import { useControllableState } from "../../hooks/use-controllable-state"
import {
  createContext,
  cx,
  dataAttr,
  handlerAll,
  isObject,
  isUndefined,
  mergeRefs,
  visuallyHiddenAttributes,
} from "@yamada-ui/react"
import { useFieldProps } from "../field"

interface RadioGroupContext extends Omit<UseRadioGroupReturn, "getRootProps"> {}

const [RadioGroupContext, useRadioGroupContext] =
  createContext<RadioGroupContext>({
    name: "RadioGroupContext",
    strict: false,
  })

export { RadioGroupContext, useRadioGroupContext }

export interface UseRadioGroupProps<Y extends string = string>
  extends Omit<HTMLProps, "defaultValue" | "onChange" | "value">,
    FieldProps {
  /**
   * The initial value of the radio group.
   */
  defaultValue?: Y
  /**
   * The value of the radio group.
   */
  value?: Y
  /**
   * The callback fired when any children radio is checked or unchecked.
   */
  onChange?: (value: Y) => void
}

export const useRadioGroup = <Y extends string = string>(
  props: UseRadioGroupProps<Y> = {},
) => {
  const uuid = useId()
  const {
    context: { labelId } = {},
    props: {
      id = uuid,
      defaultValue,
      disabled,
      readOnly,
      required,
      value: valueProp,
      onChange: onChangeProp,
      ...rest
    },
    ariaProps: { "aria-describedby": ariaDescribedbyProp, ...ariaProps },
    dataProps,
    eventProps,
  } = useFieldProps(props)
  const interactive = !(readOnly || disabled)
  const [value, setValue] = useControllableState({
    defaultValue,
    value: valueProp,
    onChange: onChangeProp,
  })

  const onChange = useCallback(
    (valueOrEv: ChangeEvent<HTMLInputElement> | Y) => {
      if (!interactive) return

      if (isObject(valueOrEv)) {
        setValue(valueOrEv.target.value as Y)
      } else {
        setValue(valueOrEv)
      }
    },
    [interactive, setValue],
  )

  const getRootProps: PropGetter = useCallback(
    ({
      ref,
      "aria-describedby": ariaDescribedby,
      "aria-labelledby": ariaLabelledby,
      ...props
    } = {}) => ({
      ...dataProps,
      id,
      "aria-describedby": cx(ariaDescribedbyProp, ariaDescribedby),
      "aria-labelledby": cx(labelId, ariaLabelledby),
      role: "radiogroup",
      ...rest,
      ...props,
      ref: mergeRefs(ref, rest.ref),
    }),
    [ariaDescribedbyProp, dataProps, id, labelId, rest],
  )

  const getInputProps: PropGetter<"input"> = useCallback(
    (props = {}) => {
      const checked = !isUndefined(value) && value === props.value

      return {
        ...dataProps,
        ...ariaProps,
        type: "radio",
        name: id,
        style: visuallyHiddenAttributes.style,
        "aria-checked": checked,
        "data-checked": dataAttr(checked),
        checked,
        disabled,
        readOnly,
        required,
        ...props,
        onBlur: handlerAll(props.onBlur, eventProps.onBlur),
        onChange: handlerAll(props.onChange, onChange),
        onFocus: handlerAll(props.onFocus, eventProps.onFocus),
      }
    },
    [
      ariaProps,
      dataProps,
      disabled,
      eventProps,
      id,
      onChange,
      readOnly,
      required,
      value,
    ],
  )

  const getLabelProps: PropGetter<"label"> = useCallback(
    (props) => ({
      ...dataProps,
      ...props,
    }),
    [dataProps],
  )

  return {
    name: id,
    value,
    getInputProps,
    getLabelProps,
    getRootProps,
    onChange,
  }
}

export type UseRadioGroupReturn = ReturnType<typeof useRadioGroup>
