export {
  ReorderItem as Item,
  ReorderPropsContext as PropsContext,
  ReorderRoot as Root,
  ReorderTrigger as Trigger,
  useReorderPropsContext as usePropsContext,
} from "./reorder"
export type {
  ReorderItemProps as ItemProps,
  ReorderRootProps as RootProps,
  ReorderTriggerProps as TriggerProps,
} from "./reorder"
export {
  ReorderContext as Context,
  ReorderItemContext as ItemContext,
  useReorderContext as useContext,
  useReorderItemContext as useItemContext,
} from "./use-reorder"

import { defineComponentSlotStyle } from "@yamada-ui/react"

export const reorderStyle = defineComponentSlotStyle({
  base: {
    item: {
      "&[data-has-trigger]": {
        cursor: "default",
        userSelect: "none",
      },
      cursor: "grab",
      flex: "1",
      rounded: "l2",
      _selected: {
        boxShadow: "md",
        cursor: "grabbing",
      },
    },
    root: {
      display: "flex",
      w: "full",
    },
    trigger: {
      alignItems: "center",
      color: "fg.subtle",
      cursor: "grab",
      display: "flex",
      fontSize: "2xl",
      justifyContent: "center",
      _selected: {
        cursor: "grabbing",
      },
      _disabled: {
        layerStyle: "disabled",
      },
    },
  },

  sizes: {
    sm: {
      item: { p: "3" },
      root: { gap: "3" },
    },
    md: {
      item: { p: "4" },
      root: { gap: "4" },
    },
    lg: {
      item: { p: "6" },
      root: { gap: "6" },
    },
    xl: {
      item: { p: "8" },
      root: { gap: "8" },
    },
  },

  variants: {
    elevated: {
      item: {
        bg: "bg.panel",
        boxShadow: "md",
        _selected: {
          boxShadow: "lg",
        },
      },
    },
    outline: {
      item: {
        layerStyle: "outline",
        bg: "bg",
      },
    },
    panel: {
      item: {
        layerStyle: "panel",
      },
    },
    plain: {
      item: {
        flex: "inherit",
        p: "0px",
        rounded: "0px",
        _selected: { boxShadow: "unset" },
      },
      root: { gap: "0px" },
    },
    solid: {
      item: {
        layerStyle: "solid",
      },
    },
    subtle: {
      item: {
        layerStyle: "subtle",
      },
    },
    surface: {
      item: {
        layerStyle: "surface",
      },
    },
  },

  props: {
    /**
     * The orientation of the reorder.
     *
     * @default 'vertical'
     */
    orientation: {
      horizontal: {
        root: {
          flexDirection: "row",
        },
      },
      vertical: {
        root: {
          alignItems: "stretch",
          flexDirection: "column",
        },
      },
    },
  },

  defaultProps: {
    size: "md",
    variant: "panel",
    orientation: "vertical",
  },
})

export type ReorderStyle = typeof reorderStyle

"use client"

import type { PropsWithChildren, ReactElement } from "react"
import type {
  GenericsComponent,
  HTMLStyledProps,
  ThemeProps,
  Merge,
} from "@yamada-ui/react"
import type { HTMLMotionProps } from "../motion"
import type { ReorderStyle } from "./reorder.style"
import type { UseReorderItemProps, UseReorderProps } from "./use-reorder"
import { Reorder as OriginalReorder } from "motion/react"
import { createSlotComponent, styled } from "@yamada-ui/react"
import { useValue } from "../../hooks/use-value"
import { GripVerticalIcon } from "../icon"
import { reorderStyle } from "./reorder.style"
import {
  ReorderContext,
  ReorderItemContext,
  useReorder,
  useReorderItem,
  useReorderItemContext,
} from "./use-reorder"

export interface ReorderRootProps<Y = string>
  extends Merge<
      HTMLMotionProps<"ul">,
      Omit<UseReorderProps<Y>, "item" | "items" | "orientation">
    >,
    ThemeProps<ReorderStyle> {
  /**
   * If provided, generate reorder items based on items.
   *
   */
  items?: ReorderItemProps<Y>[]
}

const {
  PropsContext: ReorderPropsContext,
  usePropsContext: useReorderPropsContext,
  withContext,
  withProvider,
} = createSlotComponent<ReorderRootProps, ReorderStyle>("reorder", reorderStyle)

export { ReorderPropsContext, useReorderPropsContext }

/**
 * `Reorder` is a component that allows you to change the order of items using drag and drop.
 *
 * @see https://yamada-ui.com/components/reorder
 */
export const ReorderRoot = withProvider<"ul", ReorderRootProps>(
  // eslint-disable-next-line @typescript-eslint/no-unnecessary-type-constraint
  <Y extends any = string>(props: ReorderRootProps<Y>) => {
    const orientation = useValue(props.orientation)
    const { children, getRootProps } = useReorder({
      ...props,
      item: <ReorderItem />,
      orientation,
    })

    return (
      <ReorderContext value={{ orientation }}>
        <styled.ul
          as={OriginalReorder.Group<Y>}
          {...(getRootProps() as HTMLStyledProps<"ul">)}
        >
          {children}
        </styled.ul>
      </ReorderContext>
    )
  },
  "root",
  { transferProps: ["orientation"] },
)() as GenericsComponent<{
  <Y = string>(props: ReorderRootProps<Y>): ReactElement
}>

export interface ReorderItemProps<Y = string>
  extends Omit<
      Merge<HTMLMotionProps<"li">, UseReorderItemProps<Y>>,
      "children"
    >,
    PropsWithChildren {}

export const ReorderItem = withContext<"li", ReorderItemProps>(
  // eslint-disable-next-line @typescript-eslint/no-unnecessary-type-constraint
  <Y extends any = string>(props: ReorderItemProps<Y>) => {
    const { getItemProps, getTriggerProps } = useReorderItem(props)

    return (
      <ReorderItemContext value={{ getTriggerProps }}>
        <styled.li
          as={OriginalReorder.Item<Y>}
          {...(getItemProps() as HTMLStyledProps<"li">)}
        />
      </ReorderItemContext>
    )
  },
  "item",
)() as GenericsComponent<{
  <Y = string>(props: ReorderItemProps<Y>): ReactElement
}>

export interface ReorderTriggerProps extends HTMLStyledProps {}

export const ReorderTrigger = withContext<"div", ReorderTriggerProps>(
  "div",
  "trigger",
)(undefined, (props) => {
  const { getTriggerProps } = useReorderItemContext()

  return { children: <GripVerticalIcon />, ...getTriggerProps(props) }
})

"use client"

import type { HTMLMotionProps, Reorder } from "motion/react"
import type { PropsWithChildren, ReactElement, ReactNode } from "react"
import type { Orientation, PropGetter, Dict, Merge } from "@yamada-ui/react"
import { useDragControls, useMotionValue } from "motion/react"
import {
  cloneElement,
  useCallback,
  useEffect,
  useMemo,
  useRef,
  useState,
} from "react"
import {
  createContext,
  dataAttr,
  handlerAll,
  isArray,
  isObject,
  mergeRefs,
  useUpdateEffect,
  useValidChildren,
} from "@yamada-ui/react"

const getAlternativeValue = ({ children, label }: Dict = {}) =>
  serializeValue(children ?? label)

const serializeValue = (value: any) =>
  isObject(value) || isArray(value) ? JSON.stringify(value) : value

type ReorderGroupProps<Y = string> = Merge<
  HTMLMotionProps<"ul">,
  Pick<Parameters<typeof Reorder.Group<Y>>[0], "axis" | "onReorder" | "values">
>
type ReorderItemProps<Y = string> = Merge<
  HTMLMotionProps<"li">,
  Partial<Pick<Parameters<typeof Reorder.Item<Y>>[0], "layout" | "value">>
>

interface ReorderContext extends Pick<UseReorderProps, "orientation"> {}

const [ReorderContext, useReorderContext] = createContext<ReorderContext>({
  name: "ReorderContext",
})

export { ReorderContext, useReorderContext }

interface ReorderItemContext {
  getTriggerProps: PropGetter
}

const [ReorderItemContext, useReorderItemContext] =
  createContext<ReorderItemContext>({
    name: "ReorderItemContext",
  })

export { ReorderItemContext, useReorderItemContext }

export interface UseReorderProps<Y = string>
  extends Partial<
      Omit<ReorderGroupProps<Y>, "children" | "onChange" | "values">
    >,
    PropsWithChildren {
  /**
   * The component to be used for the item.
   */
  item?: ReactElement<any>
  /**
   * If provided, generate reorder items based on items.
   *
   */
  items?: UseReorderItemProps<Y>[]
  /**
   * The orientation of the reorder.
   *
   * @default 'vertical'
   */
  orientation?: Orientation
  /**
   * The callback invoked when reorder items are moved.
   */
  onChange?: (values: Y[]) => void
  /**
   * The callback invoked when the movement of reorder items is completed.
   */
  onCompleteChange?: (values: Y[]) => void
}

export const useReorder = <Y = string,>({
  ref,
  children,
  item,
  items = [],
  orientation = "vertical",
  onChange,
  onCompleteChange,
  ...rest
}: UseReorderProps<Y> = {}) => {
  const axis = orientation === "vertical" ? "y" : "x"
  const validChildren = useValidChildren(children)
  const hasChildren = !!validChildren.length
  const defaultValues = useMemo(() => {
    const values = hasChildren
      ? validChildren.map(
          ({ props }) => props.value ?? getAlternativeValue(props),
        )
      : items.map((props) => props.value ?? getAlternativeValue(props))

    const duplicatedValues = values.filter(
      (value, index, self) =>
        self.indexOf(value) === index && index !== self.lastIndexOf(value),
    )

    if (duplicatedValues.length)
      console.warn(
        `Reorder: 'value' of 'ReorderItem' must not be duplicated. duplicate 'value' is '${duplicatedValues.join(
          `', '`,
        )}' `,
      )

    return Array.from(new Set(values))
  }, [hasChildren, validChildren, items])
  const childMap = useMemo(() => {
    return Object.fromEntries(
      validChildren.map((child) => [
        serializeValue(child.props.value) ??
          serializeValue(getAlternativeValue(child.props)),
        child,
      ]),
    )
  }, [validChildren])
  const itemMap = useMemo(() => {
    return Object.fromEntries(
      items.map((props) => [
        serializeValue(props.value) ??
          serializeValue(getAlternativeValue(props)),
        props,
      ]),
    )
  }, [items])
  const prevDefaultValues = useRef<Y[]>(defaultValues)
  const [values, setValues] = useState<Y[]>(defaultValues)
  const prevValues = useRef<Y[]>(defaultValues)
  const cloneChildren = useMemo(
    () =>
      values.map((value) => {
        if (hasChildren) {
          return childMap[serializeValue(value)]
        } else {
          const props = itemMap[serializeValue(value)]

          return props && item
            ? cloneElement(item, { key: serializeValue(props.value), ...props })
            : null
        }
      }),
    [values, hasChildren, childMap, itemMap, item],
  )

  const onReorder = useCallback(
    (newValues: Y[]) => {
      setValues(newValues)

      onChange?.(newValues)
    },
    [onChange],
  )

  const onCompleteReorder = useCallback(() => {
    const equal = JSON.stringify(prevValues.current) === JSON.stringify(values)

    if (equal) return

    prevValues.current = values

    onCompleteChange?.(values)
  }, [onCompleteChange, values])

  useUpdateEffect(() => {
    const equal =
      JSON.stringify(defaultValues) ===
      JSON.stringify(prevDefaultValues.current)

    if (equal) return

    prevValues.current = defaultValues
    prevDefaultValues.current = defaultValues

    setValues(defaultValues)
  }, [defaultValues])

  const getRootProps: PropGetter<
    Partial<Omit<ReorderGroupProps<Y>, "values">>,
    undefined,
    ReorderGroupProps<Y>
  > = useCallback(
    (props = {}) => ({
      axis,
      values,
      ...rest,
      ...props,
      ref: mergeRefs(props.ref, ref),
      onMouseUp: handlerAll(props.onMouseUp, rest.onMouseUp, onCompleteReorder),
      onReorder: handlerAll(props.onReorder, rest.onReorder, onReorder),
      onTouchEnd: handlerAll(
        props.onTouchEnd,
        rest.onTouchEnd,
        onCompleteReorder,
      ),
    }),
    [rest, ref, onCompleteReorder, onReorder, axis, values],
  )

  return { children: cloneChildren, orientation, values, getRootProps }
}

export type UseReorderReturn<Y = string> = ReturnType<typeof useReorder<Y>>

export interface UseReorderItemProps<Y = string> extends ReorderItemProps<Y> {
  /**
   * The label of the reorder item.
   */
  label?: ReactNode
  /**
   * The value of the reorder item.
   */
  value?: Y
}

export const useReorderItem = <Y = string,>({
  ref,
  label,
  value,
  ...rest
}: UseReorderItemProps<Y>) => {
  const { orientation } = useReorderContext()
  const dragControls = useDragControls()
  const [hasTrigger, setHasTrigger] = useState<boolean>(false)
  const [drag, setDrag] = useState<boolean>(false)
  const x = useMotionValue(0)
  const y = useMotionValue(0)

  const register = useCallback(
    (node: HTMLElement | null) => setHasTrigger(!!node),
    [],
  )

  useEffect(() => {
    const unsubscribeX = x.on("change", (x) => {
      if (orientation === "horizontal") setDrag(x !== 0)
    })
    const unsubscribeY = y.on("change", (y) => {
      if (orientation === "vertical") setDrag(y !== 0)
    })

    return () => {
      unsubscribeX()
      unsubscribeY()
    }
  }, [orientation, x, y])

  const getItemProps: PropGetter<
    Omit<HTMLMotionProps<"li">, "layout" | "value"> &
      Pick<ReorderItemProps<Y>, "layout">,
    undefined,
    ReorderItemProps<Y>
  > = useCallback(
    (props = {}) => {
      const children = props.children ?? rest.children ?? label

      return {
        "data-has-trigger": dataAttr(hasTrigger),
        "data-selected": dataAttr(drag),
        dragControls,
        dragListener: !hasTrigger,
        value: value ?? getAlternativeValue({ children }),
        ...props,
        ...rest,
        ref: mergeRefs(props.ref, ref),
        style: { x, y, ...props.style, ...rest.style },
        children,
      }
    },
    [ref, rest, dragControls, hasTrigger, value, x, y, drag, label],
  )

  const getTriggerProps: PropGetter = useCallback(
    (props = {}) => ({
      ...props,
      ref: mergeRefs(register, props.ref),
      "data-selected": dataAttr(drag),
      onPointerDown: handlerAll(props.onPointerDown, (ev) =>
        dragControls.start(ev),
      ),
    }),
    [drag, dragControls, register],
  )

  return { getItemProps, getTriggerProps }
}

export type UseReorderItemReturn<Y = string> = ReturnType<
  typeof useReorderItem<Y>
>
