import { defineComponentSlotStyle, focusRingStyle } from "@yamada-ui/react"
import { menuStyle } from "../menu"
import { nativeSelectStyle } from "../native-select"

export const selectStyle = defineComponentSlotStyle({
  base: {
    ...nativeSelectStyle.base,
    content: { ...menuStyle.base?.content, maxH: "xs" },
    field: {
      ...nativeSelectStyle.base?.field,
      alignItems: "center",
      display: "flex",
    },
    group: menuStyle.base?.group,
    indicator: menuStyle.base?.indicator,
    label: menuStyle.base?.label,
    option: menuStyle.base?.item,
    separator: menuStyle.base?.separator,
    valueText: { truncated: true, userSelect: "none" },
  },

  variants: {
    filled: {
      ...nativeSelectStyle.variants?.filled,
      field: {
        ...nativeSelectStyle.variants?.filled.field,
        _expanded: focusRingStyle.inside,
      },
    },
    flushed: {
      ...nativeSelectStyle.variants?.flushed,
      field: {
        ...nativeSelectStyle.variants?.flushed.field,
        _expanded: nativeSelectStyle.variants?.flushed.field?._focusVisible,
      },
    },
    outline: {
      ...nativeSelectStyle.variants?.outline,
      field: {
        ...nativeSelectStyle.variants?.outline.field,
        _expanded: focusRingStyle.inside,
      },
    },
    plain: {
      ...nativeSelectStyle.variants?.plain,
      field: {
        ...nativeSelectStyle.variants?.plain.field,
        _expanded: focusRingStyle.inside,
      },
    },
  },

  sizes: {
    xs: {
      ...nativeSelectStyle.sizes?.xs,
      content: menuStyle.sizes?.sm.content,
      indicator: menuStyle.sizes?.sm.indicator,
      label: menuStyle.sizes?.sm.label,
      option: menuStyle.sizes?.sm.item,
    },
    sm: {
      ...nativeSelectStyle.sizes?.sm,
      content: menuStyle.sizes?.md.content,
      indicator: menuStyle.sizes?.md.indicator,
      label: menuStyle.sizes?.md.label,
      option: menuStyle.sizes?.md.item,
    },
    md: {
      ...nativeSelectStyle.sizes?.md,
      content: menuStyle.sizes?.md.content,
      indicator: menuStyle.sizes?.md.indicator,
      label: menuStyle.sizes?.md.label,
      option: menuStyle.sizes?.md.item,
    },
    lg: {
      ...nativeSelectStyle.sizes?.lg,
      content: menuStyle.sizes?.lg.content,
      indicator: menuStyle.sizes?.lg.indicator,
      label: menuStyle.sizes?.lg.label,
      option: menuStyle.sizes?.lg.item,
    },
    xl: {
      ...nativeSelectStyle.sizes?.xl,
      content: menuStyle.sizes?.lg.content,
      indicator: menuStyle.sizes?.lg.indicator,
      label: menuStyle.sizes?.lg.label,
      option: menuStyle.sizes?.lg.item,
    },
  },

  defaultProps: {
    size: "md",
    variant: "outline",
  },
})

export type SelectStyle = typeof selectStyle

"use client"

import type { ReactElement, ReactNode } from "react"
import type {
  GenericsComponent,
  HTMLProps,
  HTMLStyledProps,
  ThemeProps,
  WithoutThemeProps,
} from "@yamada-ui/react"
import type {
  ComboboxItem,
  UseComboboxGroupProps,
} from "../../hooks/use-combobox"
import type { FieldProps } from "../field"
import type { UseInputBorderProps } from "../input"
import type { SelectStyle } from "./select.style"
import type {
  UseSelectOptionProps,
  UseSelectProps,
  UseSelectReturn,
} from "./use-select"
import { useMemo } from "react"
import { createSlotComponent, styled, cast, isArray } from "@yamada-ui/react"
import {
  ComboboxContext,
  ComboboxDescendantsContext,
  ComboboxGroupContext,
  createComboboxChildren,
  createComboboxItem,
  useComboboxGroup,
  useComboboxGroupContext,
} from "../../hooks/use-combobox"
import { useGroupItemProps } from "../group"
import { CheckIcon, ChevronDownIcon, XIcon } from "../icon"
import { InputGroup, useInputBorder, useInputPropsContext } from "../input"
import { Popover, usePopoverProps } from "../popover"
import { selectStyle } from "./select.style"
import { SelectContext, useSelect, useSelectOption } from "./use-select"

interface ComponentContext
  extends Pick<UseSelectReturn, "getSeparatorProps">,
    Pick<SelectRootProps, "groupProps" | "optionProps"> {}

export interface SelectRootProps<Y extends string | string[] = string>
  extends Omit<
      HTMLStyledProps,
      "defaultValue" | "offset" | "onChange" | "value"
    >,
    UseSelectProps<Y>,
    Omit<
      WithoutThemeProps<Popover.RootProps, SelectStyle>,
      | "autoFocus"
      | "children"
      | "initialFocusRef"
      | "modal"
      | "transform"
      | "updateRef"
      | "withCloseButton"
    >,
    ThemeProps<SelectStyle>,
    FieldProps,
    UseInputBorderProps {
  /**
   * If `true`, display the clear icon.
   *
   * @default false
   */
  clearable?: boolean
  /**
   * The icon to be used in the clear button.
   */
  clearIcon?: ReactNode
  /**
   * The icon to be used in the select.
   */
  icon?: ReactNode
  /**
   * Props for content element.
   */
  contentProps?: SelectContentProps
  /**
   * The props for the end element.
   */
  elementProps?: InputGroup.ElementProps
  /**
   * Props for group element.
   */
  groupProps?: Omit<SelectGroupProps, "children" | "label">
  /**
   * Props for icon element.
   */
  iconProps?: SelectIconProps
  /**
   * Props for option element.
   */
  optionProps?: Omit<SelectOptionProps, "children" | "value">
  /**
   * Props for placeholder element.
   */
  placeholderProps?: Omit<SelectOptionProps, "children" | "value">
  /**
   * Props for root element.
   */
  rootProps?: InputGroup.RootProps
}

const {
  ComponentContext,
  PropsContext: SelectPropsContext,
  useComponentContext,
  usePropsContext: useSelectPropsContext,
  withContext,
  withProvider,
} = createSlotComponent<SelectRootProps, SelectStyle, ComponentContext>(
  "select",
  selectStyle,
)

export { SelectPropsContext, useSelectPropsContext }

/**
 * `Select` is a component used for allowing a user to choose one option from a list.
 *
 * @see https://yamada-ui.com/components/select
 */
export const SelectRoot = withProvider<"div", SelectRootProps>(
  <Y extends string | string[] = string>(props: SelectRootProps<Y>) => {
    const [groupItemProps, mergedProps] = useGroupItemProps(props)
    const [
      popoverProps,
      {
        className,
        css,
        colorScheme,
        children,
        clearable,
        clearIcon = <XIcon />,
        errorBorderColor,
        focusBorderColor,
        icon,
        items: itemsProp,
        contentProps,
        elementProps,
        groupProps,
        iconProps,
        optionProps,
        placeholderProps,
        rootProps,
        ...rest
      },
    ] = usePopoverProps(mergedProps, [
      "disabled",
      "open",
      "defaultOpen",
      "onOpen",
      "onClose",
      "openOnClick",
    ])
    const items = useMemo<ComboboxItem[]>(() => {
      if (itemsProp) return itemsProp

      return createComboboxItem(children, {
        Group: SelectGroup,
        Label: SelectLabel,
        Option: SelectOption,
      })
    }, [itemsProp, children])
    const {
      descendants,
      includePlaceholder,
      interactive,
      items: computedItems,
      max,
      open,
      placeholder,
      value,
      getClearIconProps,
      getContentProps,
      getFieldProps,
      getIconProps,
      getRootProps,
      getSeparatorProps,
      onActiveDescendant,
      onChange,
      onClose,
      onOpen,
      onSelect,
    } = useSelect({ items, ...rest })
    const mergedPopoverProps = useMemo<Popover.RootProps>(
      () => ({
        animationScheme: "block-start",
        autoFocus: false,
        matchWidth: true,
        openOnClick: false,
        ...popoverProps,
        disabled: !interactive,
        open,
        onClose,
        onOpen,
      }),
      [interactive, onClose, onOpen, open, popoverProps],
    )
    const computedChildren = useMemo(() => {
      if (children)
        return (
          <>
            {placeholder ? (
              <SelectOption
                {...placeholderProps}
                hidden={!includePlaceholder}
                value=""
              >
                {placeholder}
              </SelectOption>
            ) : null}

            {children}
          </>
        )

      return createComboboxChildren(computedItems, {
        Group: SelectGroup,
        Option: SelectOption,
      })
    }, [
      children,
      computedItems,
      placeholder,
      includePlaceholder,
      placeholderProps,
    ])
    const varProps = useInputBorder({ errorBorderColor, focusBorderColor })
    const comboboxContext = useMemo(
      () => ({ max, value, onActiveDescendant, onChange, onClose, onSelect }),
      [max, onActiveDescendant, onChange, onClose, onSelect, value],
    )
    const selectContext = useMemo(() => ({ max, value }), [max, value])
    const componentContext = useMemo(
      () => ({ getSeparatorProps, groupProps, optionProps }),
      [getSeparatorProps, groupProps, optionProps],
    )
    const hasValue = isArray(value) ? !!value.length : !!value

    return (
      <ComboboxDescendantsContext value={descendants}>
        <ComboboxContext value={comboboxContext}>
          <SelectContext value={selectContext}>
            <ComponentContext value={componentContext}>
              <Popover.Root {...mergedPopoverProps}>
                <InputGroup.Root
                  className={className}
                  css={css}
                  colorScheme={colorScheme}
                  {...getRootProps({ ...groupItemProps, ...rootProps })}
                >
                  <Popover.Trigger>
                    <SelectField {...getFieldProps(varProps)} />
                  </Popover.Trigger>

                  <InputGroup.Element
                    {...{ clickable: clearable && hasValue, ...elementProps }}
                  >
                    {clearable && hasValue ? (
                      <SelectIcon
                        icon={clearIcon}
                        {...getClearIconProps(iconProps)}
                      />
                    ) : (
                      <SelectIcon icon={icon} {...getIconProps(iconProps)} />
                    )}
                  </InputGroup.Element>
                </InputGroup.Root>

                <SelectContent
                  {...cast<SelectContentProps>(
                    getContentProps(cast<HTMLProps>(contentProps)),
                  )}
                >
                  {computedChildren}
                </SelectContent>
              </Popover.Root>
            </ComponentContext>
          </SelectContext>
        </ComboboxContext>
      </ComboboxDescendantsContext>
    )
  },
  "root",
)((props) => {
  const context = useInputPropsContext()

  return { ...context, ...props }
}) as GenericsComponent<{
  <Y extends string | string[] = string>(
    props: SelectRootProps<Y>,
  ): ReactElement
}>

interface SelectFieldProps extends HTMLStyledProps {}

const SelectField = withContext<"div", SelectFieldProps>("div", "field")(
  { "data-group-propagate": "" },
  ({ children, ...rest }) => ({
    ...rest,
    children: <SelectValueText>{children}</SelectValueText>,
  }),
)

interface SelectIconProps extends HTMLStyledProps {
  icon?: ReactNode
}

const SelectIcon = withContext<"div", SelectIconProps>("div", "icon")(
  undefined,
  ({ children, icon, ...rest }) => ({
    children: icon || children || <ChevronDownIcon />,
    ...rest,
  }),
)

interface SelectValueTextProps extends HTMLProps<"span"> {}

const SelectValueText = withContext<"span", SelectValueTextProps>(
  "span",
  "valueText",
)()

interface SelectContentProps extends Popover.ContentProps {}

const SelectContent = withContext<"div", SelectContentProps>(
  Popover.Content,
  "content",
)()

export interface SelectLabelProps extends HTMLStyledProps<"span"> {}

export const SelectLabel = withContext<"span", SelectLabelProps>(
  "span",
  "label",
)(undefined, (props) => {
  const { getLabelProps } = useComboboxGroupContext()

  return getLabelProps(props)
})

export interface SelectGroupProps
  extends UseComboboxGroupProps,
    HTMLStyledProps {
  /**
   * The label of the group.
   */
  label?: ReactNode
  /**
   * Props for the label component.
   */
  labelProps?: SelectLabelProps
}

export const SelectGroup = withContext<"div", SelectGroupProps>(
  ({ children, label, labelProps, ...rest }) => {
    const { groupProps } = useComponentContext()
    const { getGroupProps, getLabelProps } = useComboboxGroup({
      ...groupProps,
      ...rest,
    })
    const context = useMemo(() => ({ getLabelProps }), [getLabelProps])

    return (
      <ComboboxGroupContext value={context}>
        <styled.div {...getGroupProps()}>
          {label ? <SelectLabel {...labelProps}>{label}</SelectLabel> : null}
          {children}
        </styled.div>
      </ComboboxGroupContext>
    )
  },
  "group",
)()

export interface SelectOptionProps
  extends UseSelectOptionProps,
    HTMLStyledProps {
  /**
   * The icon to be used in the select option.
   */
  icon?: ReactNode
}

export const SelectOption = withContext<"div", SelectOptionProps>(
  ({ children, icon: iconProp, ...rest }) => {
    const { optionProps: { icon, ...optionProps } = {} } = useComponentContext()
    const { getIndicatorProps, getOptionProps } = useSelectOption({
      ...optionProps,
      ...rest,
    })

    return (
      <styled.div {...getOptionProps()}>
        <SelectIndicator {...getIndicatorProps()}>
          {iconProp ?? icon ?? <CheckIcon />}
        </SelectIndicator>
        {children}
      </styled.div>
    )
  },
  "option",
)()

interface SelectIndicatorProps extends HTMLStyledProps {}

const SelectIndicator = withContext<"div", SelectIndicatorProps>(
  "div",
  "indicator",
)()

export interface SelectSeparatorProps extends HTMLStyledProps<"hr"> {}

export const SelectSeparator = withContext<"hr", SelectSeparatorProps>(
  "hr",
  "separator",
)(undefined, (props) => {
  const { getSeparatorProps } = useComponentContext()

  return getSeparatorProps(props)
})

"use client"

import type { ReactNode } from "react"
import type { HTMLProps, PropGetter, Dict } from "@yamada-ui/react"
import type {
  ComboboxItem,
  ComboboxItemWithValue,
  UseComboboxItemProps,
  UseComboboxProps,
} from "../../hooks/use-combobox"
import type { FieldProps } from "../field"
import { cloneElement, isValidElement, useCallback, useMemo } from "react"
import { useCombobox, useComboboxItem } from "../../hooks/use-combobox"
import { useControllableState } from "../../hooks/use-controllable-state"
import { useI18n } from "../../providers/i18n-provider"
import {
  createContext,
  cx,
  dataAttr,
  handlerAll,
  isArray,
  isNumber,
  isString,
  isUndefined,
  runKeyAction,
  toArray,
} from "@yamada-ui/react"
import { useFieldProps } from "../field"

interface SelectRenderProps extends ComboboxItemWithValue {
  count: number
  index: number
  separator: string
}

export interface SelectItemRender {
  (props: SelectRenderProps): ReactNode
}

const defaultRender: SelectItemRender = ({
  count,
  index,
  label,
  separator,
  value,
}) => {
  const last = count - 1 === index

  return (
    <span data-placeholder={dataAttr(value === "")}>
      {label}
      {!last ? `${separator} ` : null}
    </span>
  )
}

interface SelectContext extends Pick<UseSelectReturn, "max" | "value"> {}

const [SelectContext, useSelectContext] = createContext<SelectContext>({
  name: "SelectContext",
})

export { SelectContext, useSelectContext }

export interface UseSelectProps<Y extends string | string[] = string>
  extends Omit<HTMLProps, "defaultValue" | "onChange" | "value">,
    Omit<
      UseComboboxProps,
      "defaultValue" | "initialFocusValue" | "onChange" | "value"
    >,
    FieldProps {
  /**
   * The initial value of the select.
   */
  defaultValue?: Y
  /**
   * If `true`, include placeholder in options.
   *
   * @default true
   */
  includePlaceholder?: boolean
  /**
   * If provided, generate options based on items.
   *
   * @default '[]'
   */
  items?: ComboboxItem[]
  /**
   * The maximum selectable value.
   */
  max?: number
  /**
   * If `true`, the select will be multiple.
   *
   * @default false
   */
  multiple?: boolean
  /**
   * The placeholder for select.
   */
  placeholder?: string
  /**
   * The function to render the selected items.
   */
  render?: (props: SelectRenderProps) => ReactNode
  /**
   * The visual separator between each value.
   *
   * @default ','
   */
  separator?: string
  /**
   * The value of the select.
   */
  value?: Y
  /**
   * The callback invoked when value state changes.
   */
  onChange?: (value: Y) => void
}

export const useSelect = <Y extends string | string[] = string>(
  props: UseSelectProps<Y> = {},
) => {
  const { t } = useI18n("select")
  const {
    context: { labelId } = {},
    props: {
      multiple = false,
      closeOnSelect = !multiple,
      defaultValue = (multiple ? [] : "") as Y,
      disabled,
      includePlaceholder = !multiple,
      items: itemProp = [],
      max,
      placeholder,
      readOnly,
      render = defaultRender,
      separator = ",",
      value: valueProp,
      onChange: onChangeProp,
      ...rest
    },
    ariaProps,
    dataProps,
    eventProps,
  } = useFieldProps(props)
  const [value, setValue] = useControllableState({
    defaultValue,
    value: valueProp,
    onChange: onChangeProp,
  })
  const onChange = useCallback(
    (value: string) => {
      setValue((prev) => {
        if (isArray(prev)) {
          if (prev.includes(value)) {
            return prev.filter((prevValue) => prevValue !== value) as Y
          } else if (!isNumber(max) || prev.length < max) {
            return [...prev, value] as Y
          } else {
            return prev
          }
        } else {
          return value as Y
        }
      })
    },
    [max, setValue],
  )
  const items = useMemo<ComboboxItem[]>(() => {
    const items = [...itemProp]

    if (placeholder)
      items.unshift({
        hidden: !includePlaceholder,
        label: placeholder,
        value: "",
      })

    return items
  }, [itemProp, placeholder, includePlaceholder])
  const empty = useMemo(
    () => !items.filter(({ hidden }) => !hidden).length,
    [items],
  )
  const {
    descendants,
    interactive,
    open,
    getContentProps: getComboboxContentProps,
    getSeparatorProps,
    getTriggerProps,
    onActiveDescendant,
    onClose,
    onOpen,
    onSelect,
  } = useCombobox({
    closeOnSelect,
    disabled,
    initialFocusValue: isArray(value) ? value[0] : value,
    readOnly,
    onChange,
    ...ariaProps,
    ...dataProps,
    ...eventProps,
    ...rest,
  })
  const valueMap = useMemo<{ [key: string]: ComboboxItemWithValue }>(() => {
    const valueMap: { [key: string]: ComboboxItemWithValue } = {}

    items.forEach((item) => {
      if ("items" in item) {
        item.items.forEach((item) => {
          item.value ??= isString(item.label) ? item.label : undefined

          if (!isUndefined(item.value)) valueMap[item.value] = item
        })
      } else {
        item.value ??= isString(item.label) ? item.label : undefined

        if (!isUndefined(item.value)) valueMap[item.value] = item
      }
    })

    return valueMap
  }, [items])
  const selectedItems = useMemo<ComboboxItemWithValue[]>(() => {
    if (isArray(value)) {
      if (value.length) {
        return toArray(value.map((value) => valueMap[value]))
      } else {
        return placeholder ? [{ label: placeholder, value: "" }] : []
      }
    } else {
      return isString(value) ? toArray(valueMap[value]) : []
    }
  }, [placeholder, value, valueMap])
  const children = useMemo<ReactNode>(() => {
    const count = selectedItems.length

    return selectedItems.map((item, index) => {
      const component = render({ count, index, separator, ...item })

      if (isValidElement<Dict>(component)) {
        return cloneElement(component, { ...component.props, key: index })
      } else {
        return component
      }
    })
  }, [render, selectedItems, separator])

  const onClear = useCallback(() => {
    setValue((prev) => (isArray(prev) ? [] : "") as Y)
  }, [setValue])

  const getRootProps: PropGetter = useCallback(
    (props) => ({ ...dataProps, ...props }),
    [dataProps],
  )

  const getFieldProps: PropGetter = useCallback(
    ({ "aria-labelledby": ariaLabelledby, ...props } = {}) =>
      getTriggerProps({
        "aria-label": placeholder,
        "aria-labelledby": cx(ariaLabelledby, labelId),
        ...props,
        children,
      }),
    [children, getTriggerProps, labelId, placeholder],
  )

  const getContentProps: PropGetter = useCallback(
    (props) => getComboboxContentProps({ hidden: empty, ...props }),
    [empty, getComboboxContentProps],
  )

  const getIconProps: PropGetter = useCallback(
    (props) => ({ ...dataProps, ...props }),
    [dataProps],
  )

  const getClearIconProps: PropGetter = useCallback(
    (props = {}) =>
      getIconProps({
        "aria-label": t("Clear value"),
        role: "button",
        tabIndex: 0,
        ...props,
        onClick: handlerAll(props.onClick, onClear),
        onKeyDown: handlerAll(props.onKeyDown, (ev) =>
          runKeyAction(ev, { Enter: onClear, Space: onClear }),
        ),
      }),
    [getIconProps, onClear, t],
  )

  return {
    descendants,
    includePlaceholder,
    interactive,
    items,
    max,
    open,
    placeholder,
    setValue,
    value,
    valueMap,
    getClearIconProps,
    getContentProps,
    getFieldProps,
    getIconProps,
    getRootProps,
    getSeparatorProps,
    onActiveDescendant,
    onChange,
    onClose,
    onOpen,
    onSelect,
  }
}

export type UseSelectReturn = ReturnType<typeof useSelect>

export interface UseSelectOptionProps extends UseComboboxItemProps {}

export const useSelectOption = ({
  children,
  closeOnSelect,
  disabled,
  hidden,
  value,
  ...rest
}: UseSelectOptionProps = {}) => {
  const { max, value: selectedValue } = useSelectContext()

  value ??= isString(children) ? children : undefined

  const selected = isArray(selectedValue)
    ? !isUndefined(value) && selectedValue.includes(value)
    : selectedValue === value
  const completed =
    isNumber(max) && isArray(selectedValue) && selectedValue.length >= max
  const { getIndicatorProps, getItemProps } = useComboboxItem({
    children,
    closeOnSelect,
    disabled: disabled || hidden || (completed && !selected),
    hidden,
    selected,
    value,
    ...rest,
  })

  const getOptionProps: PropGetter = useCallback(
    (props = {}) => getItemProps(props),
    [getItemProps],
  )

  return { getIndicatorProps, getOptionProps }
}

export type UseSelectOptionReturn = ReturnType<typeof useSelectOption>
