import type { Component } from "@yamada-ui/react"
import type { SkeletonProps } from "./skeleton"
import { mergeCSS } from "@yamada-ui/react"
import { Skeleton } from "./skeleton"

export interface SkeletonCircleProps extends SkeletonProps {}

export const SkeletonCircle = (({ css, ...rest }: SkeletonCircleProps) => {
  return (
    <Skeleton
      css={mergeCSS(css, { "--height": "sizes.10", "--width": "sizes.10" })}
      _loading={{ rounded: "full" }}
      {...rest}
    />
  )
}) as Component<"div", SkeletonCircleProps>

import { defineComponentStyle } from "@yamada-ui/react"

export const skeletonStyle = defineComponentStyle({
  base: {},

  props: {
    /**
     * If `false`, it'll render its children with a nice fade transition.
     *
     * @default true
     */
    loading: {
      false: {
        animationDuration: "{fade-duration, 0.1s}",
        animationIterationCount: "1",
        animationName: "fade-in",
        animationTimingFunction: "ease-out",
        bg: "unset",
      },
      true: {
        "&::before, &::after, *": {
          visibility: "hidden",
        },
        "--height": "sizes.6",
        "--width": "sizes.full",
        bgClip: "padding-box",
        boxShadow: "none",
        color: "transparent",
        cursor: "default",
        flexShrink: "0",
        h: "{height}",
        maxW: "full",
        pointerEvents: "none",
        rounded: "l1",
        userSelect: "none",
        w: "{width}",
      },
    },
  },

  variants: {
    none: {
      animation: "none",
    },
    pulse: {
      animationDuration: "{duration, 1.2s}",
      animationIterationCount: "infinite",
      animationName: "pulse",
      bg: "bg.muted",
    },
    shine: {
      "--animation-from": "200%",
      "--animation-to": "-200%",
      "--end-color": "colors.bg.muted",
      "--start-color": "colors.bg.subtle",
      animationDuration: "{duration, 5s}",
      animationIterationCount: "infinite",
      animationName: "bg-position",
      animationTimingFunction: "ease-in-out",
      bgImage:
        "linear-gradient(270deg, {start-color}, {end-color}, {end-color}, {start-color})",
      bgSize: "400% 100%",
    },
  },

  defaultProps: {
    variant: "pulse",
    loading: true,
  },
})

export type SkeletonStyle = typeof skeletonStyle

"use client"

import type { Component, HTMLStyledProps, StyleValue } from "@yamada-ui/react"
import type { SkeletonProps } from "./skeleton"
import { mergeCSS, styled } from "@yamada-ui/react"
import { useValue } from "../../hooks/use-value"
import { Skeleton } from "./skeleton"

export interface SkeletonTextProps extends Omit<SkeletonProps, "lineClamp"> {
  /**
   * The number of lines to display.
   */
  lineClamp?: StyleValue<number>
  /**
   * Props for the root element.
   */
  rootProps?: HTMLStyledProps
}

export const SkeletonText = (({
  css,
  children,
  gap = "2",
  lineClamp: lineClampProp = 3,
  loading = true,
  _loading,
  _notFirst,
  rootProps,
  ...rest
}: SkeletonTextProps) => {
  const lineClamp = useValue(lineClampProp)

  return (
    <styled.div w="full" {...rootProps}>
      {Array.from({ length: lineClamp }).map((_, index) => {
        if (!loading && index > 0) return null

        return (
          <Skeleton
            key={index}
            css={mergeCSS(css, { "--height": "sizes.4" })}
            loading={loading}
            _loading={{
              _last: { maxW: lineClamp > 1 ? "80%" : "100%" },
              ..._loading,
            }}
            _notFirst={{ mt: gap, ..._notFirst }}
            {...rest}
          >
            {!loading && index === 0 ? children : undefined}
          </Skeleton>
        )
      })}
    </styled.div>
  )
}) as Component<"div", SkeletonTextProps>
