"use client"

import type { DependencyList } from "react"
import type { Breakpoint } from "@yamada-ui/react"
import { useEffect } from "react"
import { useBreakpoint } from "./use-breakpoint"

/**
 * `useBreakpointEffect` is a custom hook that executes a specific callback function when the breakpoint changes.
 *
 * @see https://yamada-ui.com/docs/hooks/use-breakpoint-effect
 */
export const useBreakpointEffect = (
  callback: (breakpoint: Breakpoint) => void,
  deps: DependencyList,
) => {
  const breakpoint = useBreakpoint()

  useEffect(() => {
    callback(breakpoint)
    // eslint-disable-next-line react-hooks/exhaustive-deps
  }, [breakpoint, ...deps])
}

"use client"

import type { ResponsiveObject } from "@yamada-ui/react"
import { useState } from "react"
import { useBreakpointValue } from "./use-breakpoint-value"

/**
 * `useBreakpointState` is a custom hook that takes a responsive object as an initial state and returns a state corresponding to the current breakpoint.
 *
 * @see https://yamada-ui.com/docs/hooks/use-breakpoint-state
 */
export const useBreakpointState = <Y,>(
  initialState: ResponsiveObject<Y, false>,
) => {
  const state = useBreakpointValue(initialState)

  return useState(state)
}

import type { Breakpoint, ResponsiveObject, System } from "@yamada-ui/react"
import { useMemo } from "react"
import { useSystem } from "@yamada-ui/react"
import { useBreakpoint } from "./use-breakpoint"

/**
 * `useBreakpointValue` is a custom hook that returns the value of the current breakpoint from the provided object.
 * This hook monitors changes in the window size and returns the appropriate value.
 *
 * @see https://yamada-ui.com/docs/hooks/use-breakpoint-value
 */
export const useBreakpointValue = <Y,>(
  values: ResponsiveObject<Y, false>,
): Y => {
  const system = useSystem()
  const breakpoint = useBreakpoint()

  return useMemo(
    () => getBreakpointValue<Y>(values)(system, breakpoint),
    [values, system, breakpoint],
  )
}

export const getBreakpointValue =
  <Y,>(values: ResponsiveObject<Y, false> = {}) =>
  (system: System, breakpoint: Breakpoint): Y => {
    const breakpoints = system.breakpoints.keys

    if (!breakpoints.length) {
      console.warn("getBreakpointValue: `breakpoints` is undefined.")
    }

    const currentIndex = breakpoints.indexOf(breakpoint)

    for (let i = currentIndex; 0 < i; i--) {
      const nextBreakpoint = breakpoints[i]

      if (nextBreakpoint && values.hasOwnProperty(nextBreakpoint)) {
        return values[nextBreakpoint] as Y
      }
    }

    return values.base as Y
  }

"use client"

import type { Breakpoint } from "@yamada-ui/react"
import { useCallback, useMemo, useRef, useSyncExternalStore } from "react"
import {
  useEnvironment,
  useSystem,
  createdDom,
  isUndefined,
  noop,
} from "@yamada-ui/react"

/**
 * `useBreakpoint` is a custom hook that returns the current breakpoint.
 * This hook monitors changes in the window size and returns the appropriate value.
 *
 * @see https://yamada-ui.com/docs/hooks/use-breakpoint
 */
export const useBreakpoint = () => {
  const animationFrameId = useRef(0)
  const { breakpoints, config } = useSystem()
  const { getWindow } = useEnvironment()
  const {
    containerRef,
    direction = "down",
    identifier = "@media screen",
  } = config.breakpoint ?? {}
  const hasContainer = !!containerRef

  const queries = useMemo(() => {
    return breakpoints.queries.map(
      ({ breakpoint, maxW, minMaxQuery, minW }) => {
        const searchValue =
          identifier === "@media screen"
            ? "@media screen and "
            : `${identifier} `
        const query = minMaxQuery?.replace(searchValue, "") ?? ""

        return {
          breakpoint,
          maxW,
          minW,
          query,
        }
      },
    )
  }, [breakpoints, identifier])

  const hasQueries = !!queries.length

  const getBreakpoint = useCallback(
    (width?: number) => {
      if (isUndefined(width)) {
        const win = getWindow()

        if (hasContainer || !hasQueries) return "base"

        for (const { breakpoint, query } of queries) {
          const mql = win?.matchMedia(query)

          if (mql?.matches) return breakpoint
        }
      } else {
        for (const { breakpoint, maxW, minW } of queries) {
          if (direction !== "up") {
            if ((minW ?? 0) <= width) return breakpoint
          } else {
            if (width <= (maxW ?? Infinity)) return breakpoint
          }
        }
      }

      return "base"
    },
    [direction, getWindow, hasContainer, hasQueries, queries],
  )

  const breakpointRef = useRef<Breakpoint>(getBreakpoint())

  const subscribe = useCallback(
    (listener: () => void) => {
      if (!hasContainer || !hasQueries) {
        const observer = queries.map(({ breakpoint, query }): (() => void) => {
          const mql = getWindow()?.matchMedia(query)

          const onChange = (e: MediaQueryListEvent) => {
            if (e.matches) breakpointRef.current = breakpoint

            listener()
          }

          mql?.addEventListener("change", onChange)

          return () => {
            mql?.removeEventListener("change", onChange)
          }
        })

        return () => {
          observer.forEach((unobserve) => unobserve())
        }
      } else if (createdDom()) {
        const observer = new ResizeObserver(([entry]) => {
          if (!entry) return

          cancelAnimationFrame(animationFrameId.current)

          const { width } = entry.contentRect

          breakpointRef.current = getBreakpoint(width)

          animationFrameId.current = requestAnimationFrame(listener)
        })

        if (containerRef.current) observer.observe(containerRef.current)

        return () => {
          observer.disconnect()

          if (process.env.NODE_ENV !== "test")
            cancelAnimationFrame(animationFrameId.current)
        }
      } else {
        return noop
      }
    },
    [containerRef, getBreakpoint, getWindow, hasContainer, hasQueries, queries],
  )

  const getSnapshot = useCallback(() => {
    return breakpointRef.current
  }, [])

  const breakpoint = useSyncExternalStore(subscribe, getSnapshot, getSnapshot)

  return breakpoint
}

import type { DependencyList } from "react"
import type { Breakpoint } from "@yamada-ui/react"
import { useUpdateEffect } from "@yamada-ui/react"
import { useBreakpoint } from "./use-breakpoint"

/**
 * `useUpdateBreakpointEffect` is a custom hook that skips the side effect on the initial render and executes a specific callback function when the breakpoint changes.
 *
 * @see https://yamada-ui.com/docs/hooks/use-update-breakpoint-effect
 */
export const useUpdateBreakpointEffect = (
  callback: (breakpoint: Breakpoint) => void,
  deps: DependencyList,
) => {
  const breakpoint = useBreakpoint()

  useUpdateEffect(() => {
    callback(breakpoint)
  }, [breakpoint, ...deps])
}
