"use client"

import { useCallback, useState } from "react"
import { useSafeLayoutEffect } from "@yamada-ui/react"

interface EyeDropperOptions {
  signal?: AbortSignal
}

export interface EyeDropperReturn {
  sRGBHex: string
}

/**
 * `useEyeDropper` is a custom hook that opens the eye dropper tool and returns the color value.
 *
 * @see https://yamada-ui.com/hooks/use-eye-dropper
 */
export const useEyeDropper = () => {
  const [supported, setSupported] = useState(false)

  useSafeLayoutEffect(() => {
    setSupported(typeof window !== "undefined" && "EyeDropper" in window)
  }, [])

  const onOpen = useCallback(
    async (
      options: EyeDropperOptions = {},
    ): Promise<EyeDropperReturn | undefined> => {
      if (supported) {
        const eyeDropper = new (window as any).EyeDropper()

        return eyeDropper.open(options)
      }

      return Promise.resolve(undefined)
    },
    [supported],
  )

  return { supported, onOpen }
}
