import { defineTokens } from "@yamada-ui/react"

export const keyframes = defineTokens.keyframes({
  bounce: {
    "0%, 100%": {
      animationTimingFunction: "cubic-bezier(0.8, 0, 1, 1)",
      transform: "translateY(-25%)",
    },
    "50%": {
      animationTimingFunction: "cubic-bezier(0, 0, 0.2, 1)",
      transform: "none",
    },
  },
  ping: {
    "75%, 100%": {
      opacity: "0",
      transform: "scale({animation-scale, 2})",
    },
  },
  pulse: {
    "50%": { opacity: "0.5" },
  },
  spin: {
    from: {
      transform: "rotate(0deg)",
    },
    to: {
      transform: "rotate(360deg)",
    },
  },

  "bg-position": {
    from: {
      bgPosition: "var(--animation-from, 1rem) 0",
    },
    to: {
      bgPosition: "var(--animation-to, 0) 0",
    },
  },
  position: {
    from: {
      insetBlockStart: "{animation-from-y}",
      insetInlineStart: "{animation-from-x}",
    },
    to: {
      insetBlockStart: "{animation-to-y}",
      insetInlineStart: "{animation-to-x}",
    },
  },

  translate: {
    from: {
      translate: "{animation-from-x, 0} {animation-from-y, 0}",
    },
    to: {
      translate: "{animation-to-x, 0} {animation-to-y, 0}",
    },
  },

  "fade-in": {
    from: { opacity: "{animation-from-opacity, 0}" },
    to: { opacity: "{animation-to-opacity, 1}" },
  },
  "fade-out": {
    from: { opacity: "{animation-from-opacity, 1}" },
    to: { opacity: "{animation-to-opacity, 0}" },
  },

  "scale-in": {
    from: { scale: "{animation-from-scale, 0.95}" },
    to: { scale: "{animation-to-scale, 1}" },
  },
  "scale-out": {
    from: { scale: "{animation-from-scale, 1}" },
    to: { scale: "{animation-to-scale, 0.95}" },
  },

  "collapse-height": {
    from: { height: "{animation-height}" },
    to: { height: "0" },
  },
  "collapse-width": {
    from: { width: "{animation-width}" },
    to: { width: "0" },
  },
  "expand-height": {
    from: { height: "0" },
    to: { height: "{animation-height}" },
  },
  "expand-width": {
    from: { width: "0" },
    to: { width: "{animation-width}" },
  },

  "slide-from-bottom-full": {
    from: { translate: "0 {animation-from, 100%}" },
    to: { translate: "0 {animation-to, 0}" },
  },
  "slide-from-left-full": {
    from: { translate: "{animation-from, -100%} 0" },
    to: { translate: "{animation-to, 0} 0" },
  },
  "slide-from-right-full": {
    from: { translate: "{animation-from, 100%} 0" },
    to: { translate: "{animation-to, 0} 0" },
  },
  "slide-from-top-full": {
    from: { translate: "0 {animation-from, -100%}" },
    to: { translate: "0 {animation-to, 0}" },
  },
  "slide-to-bottom-full": {
    from: { translate: "0 {animation-from, 0}" },
    to: { translate: "0 {animation-to, 100%}" },
  },
  "slide-to-left-full": {
    from: { translate: "{animation-from, 0} 0" },
    to: { translate: "{animation-to, -100%} 0" },
  },
  "slide-to-right-full": {
    from: { translate: "{animation-from, 0} 0" },
    to: { translate: "{animation-to, 100%} 0" },
  },
  "slide-to-top-full": {
    from: { translate: "0 {animation-from, 0}" },
    to: { translate: "0 {animation-to, -100%}" },
  },

  "slide-from-bottom": {
    from: { translate: "0 {animation-from, 0.5rem}" },
    to: { translate: "0 {animation-to, 0}" },
  },
  "slide-from-left": {
    from: { translate: "{animation-from, -0.5rem} 0" },
    to: { translate: "{animation-to, 0} 0" },
  },
  "slide-from-right": {
    from: { translate: "{animation-from, 0.5rem} 0" },
    to: { translate: "{animation-to, 0} 0" },
  },
  "slide-from-top": {
    from: { translate: "0 {animation-from, -0.5rem}" },
    to: { translate: "0 {animation-to, 0}" },
  },
  "slide-to-bottom": {
    from: { translate: "0 {animation-from, 0}" },
    to: { translate: "0 {animation-to, 0.5rem}" },
  },
  "slide-to-left": {
    from: { translate: "{animation-from, 0} 0" },
    to: { translate: "{animation-to, -0.5rem} 0" },
  },
  "slide-to-right": {
    from: { translate: "{animation-from, 0} 0" },
    to: { translate: "{animation-to, 0.5rem} 0" },
  },
  "slide-to-top": {
    from: { translate: "0 {animation-from, 0}" },
    to: { translate: "0 {animation-to, -0.5rem}" },
  },
})

import { defineTokens } from "@yamada-ui/react"

export const fonts = defineTokens.fonts({
  body: `Inter, -apple-system, BlinkMacSystemFont, "Segoe UI", "Helvetica Neue", "游ゴシック体", YuGothic, "YuGothic M", "Hiragino Kaku Gothic ProN", Meiryo, sans-serif, "Apple Color Emoji", "Segoe UI Emoji", "Segoe UI Symbol"`,
  heading: `Inter, -apple-system, BlinkMacSystemFont, "Segoe UI", "Helvetica Neue", "游ゴシック体", YuGothic, "YuGothic M", "Hiragino Kaku Gothic ProN", Meiryo, sans-serif, "Apple Color Emoji", "Segoe UI Emoji", "Segoe UI Symbol"`,
  mono: `Inter, -apple-system, BlinkMacSystemFont, "Segoe UI", "Helvetica Neue", "游ゴシック体", YuGothic, "YuGothic M", "Hiragino Kaku Gothic ProN", Meiryo, sans-serif, "Apple Color Emoji", "Segoe UI Emoji", "Segoe UI Symbol"`,
})

import { defineStyles } from "@yamada-ui/react"

export const textStyles = defineStyles.textStyle({
  mono: {
    fontFamily: "mono",
    fontWeight: "medium",
    letterSpacing: "widest",
    whiteSpace: "nowrap",
  },
  outline: {
    color: "colorScheme.outline",
  },
  solid: {
    color: "colorScheme.contrast",
  },
  subtle: {
    color: "colorScheme.fg",
  },
  surface: {
    color: "colorScheme.fg",
  },
})

import { defineStyles } from "@yamada-ui/react"

export const resetStyle = defineStyles.resetStyle({
  "*, *::before, *::after": {
    boxSizing: "border-box",
    margin: 0,
    padding: 0,
  },

  "::-webkit-file-upload-button": {
    font: "inherit",
    WebkitAppearance: "button",
  },

  "::-webkit-search-cancel-button, ::-webkit-search-decoration": {
    WebkitAppearance: "none",
  },

  "[contenteditable]": {
    outline: "none",
  },

  "[hidden]:where(:not([hidden='until-found']))": {
    display: "none !important",
  },

  "[type='time']::-webkit-calendar-picker-indicator": {
    display: "none",
  },

  a: {
    color: "inherit",
    textDecoration: "none",
  },

  "abbr[title]": {
    textDecoration: "underline dotted",
  },

  "b, strong": {
    fontWeight: "bolder",
  },

  "button, input, optgroup, select, textarea": {
    appearance: "none",
    backgroundColor: "transparent",
    border: 0,
    borderRadius: 0,
    color: "inherit",
    font: "inherit",
    fontFeatureSettings: "inherit",
    fontVariationSettings: "inherit",
    letterSpacing: "inherit",
    outline: 0,
    WebkitAppearance: "none",
  },

  "code, kbd, samp, pre": {
    fontFamily: "inherit",
    fontSize: "1em",
  },

  fieldset: {
    border: 0,
    minWidth: 0,
  },

  "h1, h2, h3, h4, h5, h6": {
    fontSize: "inherit",
    fontWeight: "inherit",
  },

  hr: {
    blockSize: 0,
    border: "none",
    borderBlockStart: "1px solid",
    color: "inherit",
    overflow: "visible",
  },

  html: {
    fontFamily:
      "system-ui, 'Segoe UI', Roboto, Helvetica, Arial, sans-serif, 'Apple Color Emoji', 'Segoe UI Emoji'",
    lineHeight: 1.5,
    WebkitTapHighlightColor: "transparent",
    WebkitTextSizeAdjust: "100%",
  },

  "img, svg, video, canvas, audio, iframe, embed, object": {
    display: "block",
  },

  "input[type='number']::-webkit-inner-spin-button, input[type='number']::-webkit-outer-spin-button":
    {
      display: "none",
    },

  "input[type='search']": {
    outlineOffset: "-2px",
  },

  legend: {
    display: "table",
    float: "left",
    width: "100%",
  },

  mark: {
    backgroundColor: "inherit",
    color: "inherit",
  },

  progress: {
    verticalAlign: "baseline",
  },

  small: {
    fontSize: "80%",
  },

  sub: {
    bottom: "-0.25em",
  },

  "sub, sup": {
    fontSize: "75%",
    lineHeight: 0,
    position: "relative",
    verticalAlign: "baseline",
  },

  summary: {
    display: "list-item",
  },

  sup: {
    top: "-0.5em",
  },

  "ul, ol": {
    listStyle: "none",
  },

  "@media (prefers-reduced-motion: no-preference)": {
    ":where(html:focus-within)": {
      scrollBehavior: "smooth",
    },
  },

  ":where(html:has(dialog:modal[open]))": {
    overflow: "clip",
  },

  ":where(dialog, [popover])": {
    background: "none",
    border: "none",
    color: "inherit",
    inset: "unset",
    maxHeight: "unset",
    maxWidth: "unset",
    overflow: "unset",
  },

  ":where(dialog:not([open], [popover]), [popover]:not(:popover-open))": {
    display: "none !important",
  },
})

import { defineStyles, visuallyHiddenAttributes } from "@yamada-ui/react"

export const layerStyles = defineStyles.layerStyle({
  active: { opacity: 1 },
  disabled: {
    cursor: "not-allowed",
    opacity: 0.4,
    _ripple: { display: "none" },
  },
  ghost: {
    bg: "transparent",
    border: "1px solid transparent",
    color: "colorScheme.outline",
  },
  "ghost.hover": {
    bg: "colorScheme.ghost",
  },
  hover: { opacity: 0.8 },
  outline: {
    bg: "transparent",
    border: "1px solid {colorScheme.muted}",
    color: "colorScheme.outline",
  },
  "outline.hover": {
    bg: "colorScheme.ghost",
  },
  panel: {
    bg: "bg.panel",
    borderColor: "border",
    borderWidth: "1px",
  },
  readOnly: {
    cursor: "default",
    _ripple: { display: "none" },
  },
  solid: {
    bg: "colorScheme.solid",
    border: "1px solid transparent",
    color: "colorScheme.contrast",
  },
  "solid.hover": {
    bg: "colorScheme.solid/80",
  },
  subtle: {
    bg: "colorScheme.subtle",
    border: "1px solid transparent",
    color: "colorScheme.fg",
  },
  "subtle.hover": {
    bg: "colorScheme.muted",
  },
  surface: {
    bg: "colorScheme.subtle",
    border: "1px solid {colorScheme.muted}",
    color: "colorScheme.fg",
  },
  "surface.hover": {
    bg: "colorScheme.muted",
  },
  visuallyHidden: visuallyHiddenAttributes.style,
})

import { defineStyles } from "@yamada-ui/react"

export const globalStyle = defineStyles.globalStyle({
  "*, *::before, *::after": {
    borderColor: "border",
    borderStyle: "solid",
    borderWidth: "0",
    focusVisibleRing: "outline",
    fontFeatureSettings: '"cv11"',
    overflowWrap: "break-word",
  },
  "*::placeholder, *[data-placeholder]": {
    color: "fg.subtle",
  },
  body: {
    colorScheme: "mono",
    bg: "bg",
    color: "fg",
    fontFamily: "body",
    lineHeight: "moderate",
    overflowX: "hidden",
    transitionDuration: "moderate",
    transitionProperty: "background-color",
  },
})
