import { defineComponentSlotStyle } from "@yamada-ui/react"

export const alertStyle = defineComponentSlotStyle({
  base: {
    description: {
      lineHeight: "shorter",
    },
    icon: {
      boxSize: "5",
      flexShrink: 0,
      fontSize: "xl",
      me: "3",
    },
    loading: {
      flexShrink: 0,
      fontSize: "xl",
      me: "3",
    },
    root: {
      alignItems: "start",
      display: "flex",
      overflow: "hidden",
      position: "relative",
      px: "4",
      py: "3",
      rounded: "l2",
      w: "full",
    },
    title: {
      display: "block",
      lineHeight: "shorter",
      me: "2",
    },
  },

  variants: {
    island: {
      description: {
        color: "fg.muted",
      },
      icon: { color: "colorScheme.solid" },
      root: {
        layerStyle: "panel",
        pl: 7,
        _before: {
          bg: "colorScheme.solid",
          h: "calc(100% - {sizes.5})",
          left: "3",
          position: "absolute",
          rounded: "full",
          top: "50%",
          transform: "translateY(-50%)",
          w: 1,
        },
      },
    },
    plain: {
      description: {
        color: "fg.muted",
      },
      icon: {
        color: "colorScheme.solid",
      },
      root: {
        layerStyle: "panel",
      },
    },
    solid: {
      icon: {
        textStyle: "solid",
      },
      root: {
        layerStyle: "solid",
      },
    },
    subtle: {
      icon: {
        textStyle: "subtle",
      },
      root: {
        layerStyle: "subtle",
      },
    },
    surface: {
      icon: {
        textStyle: "surface",
      },
      root: {
        layerStyle: "surface",
      },
    },
  },

  compounds: [
    {
      css: {
        icon: {
          color: ["colorScheme.600", "colorScheme.400"],
        },
      },
      colorScheme: ["secondary", "gray"],
      variant: "plain",
    },
  ],

  defaultProps: {
    variant: "plain",
  },
})

export type AlertStyle = typeof alertStyle

"use client"

import type { HTMLStyledProps, ThemeProps } from "@yamada-ui/react"
import type { IconProps } from "../icon"
import type { Loading } from "../loading"
import type { StatusScheme } from "../status"
import type { AlertStyle } from "./alert.style"
import { useMemo } from "react"
import { createSlotComponent, styled } from "@yamada-ui/react"
import {
  CircleCheckBigIcon,
  InfoIcon,
  OctagonAlertIcon,
  TriangleAlertIcon,
} from "../icon"
import { useLoadingComponent } from "../loading"
import { alertStyle } from "./alert.style"

const icons = {
  error: OctagonAlertIcon,
  info: InfoIcon,
  success: CircleCheckBigIcon,
  warning: TriangleAlertIcon,
} as const

interface ComponentContext {
  status: StatusScheme
}

export interface AlertRootProps
  extends HTMLStyledProps,
    ThemeProps<AlertStyle> {
  /**
   * The status of the alert.
   *
   * @default 'info'
   */
  status?: StatusScheme
}

const {
  ComponentContext,
  PropsContext: AlertPropsContext,
  useComponentContext,
  usePropsContext: useAlertPropsContext,
  withContext,
  withProvider,
} = createSlotComponent<AlertRootProps, AlertStyle, ComponentContext>(
  "alert",
  alertStyle,
)

export { AlertPropsContext, useAlertPropsContext }

/**
 * `Alert` is a component that conveys information to the user.
 *
 * @see https://yamada-ui.com/components/alert
 */
export const AlertRoot = withProvider<"div", AlertRootProps>(
  ({ status, colorScheme = status, ...props }) => {
    const context = useMemo(() => ({ status: status! }), [status])

    return (
      <ComponentContext value={context}>
        <styled.div colorScheme={colorScheme} role="alert" {...props} />
      </ComponentContext>
    )
  },
  "root",
)({ status: "info" })

export interface AlertIconProps extends IconProps {}

export const AlertIcon = withContext<"svg", AlertIconProps>(
  ({ as, ...rest }) => {
    const { status } = useComponentContext()
    const Icon = as || icons[status]

    return <Icon {...rest} />
  },
  "icon",
)()

export interface AlertLoadingProps extends Loading.Props {
  /**
   * The loading scheme.
   *
   * @default 'oval'
   */
  loadingScheme?: Loading.Scheme
}

export const AlertLoading = withContext<"svg", AlertLoadingProps>(
  ({ loadingScheme = "oval", ...rest }) => {
    const Component = useLoadingComponent(loadingScheme)

    return <Component {...rest} />
  },
  ["icon", "loading"],
)()

export interface AlertTitleProps extends HTMLStyledProps<"p"> {}

export const AlertTitle = withContext<"p", AlertTitleProps>("p", "title")()

export interface AlertDescriptionProps extends HTMLStyledProps<"span"> {}

export const AlertDescription = withContext<"span", AlertDescriptionProps>(
  "span",
  "description",
)()
