import { defineComponentStyle } from "@yamada-ui/react"

export const aspectRatioStyle = defineComponentStyle({
  base: {
    "& > *:not(style)": {
      alignItems: "center",
      bottom: "0",
      display: "flex",
      h: "100%",
      justifyContent: "center",
      left: "0",
      overflow: "hidden",
      position: "absolute",
      right: "0",
      top: "0",
      w: "100%",
    },
    "& > img, & > video": {
      objectFit: "cover",
    },
    position: "relative",
  },
})

export type AspectRatioStyle = typeof aspectRatioStyle

"use client"

import type { HTMLStyledProps, StyleValue } from "@yamada-ui/react"
import type { AspectRatioStyle } from "./aspect-ratio.style"
import { Children } from "react"
import { createComponent, replaceObject } from "@yamada-ui/react"
import { aspectRatioStyle } from "./aspect-ratio.style"

export interface AspectRatioProps extends HTMLStyledProps {
  /**
   * The aspect ratio of the Box.
   *
   * @default '4 / 3'
   */
  ratio?: StyleValue<number>
}

const {
  PropsContext: AspectRatioPropsContext,
  usePropsContext: useAspectRatioPropsContext,
  withContext,
} = createComponent<AspectRatioProps, AspectRatioStyle>(
  "aspect-ratio",
  aspectRatioStyle,
)

export { AspectRatioPropsContext, useAspectRatioPropsContext }

/**
 * `AspectRatio` is a component for embedding things like videos and maps while maintaining the aspect ratio.
 *
 * @see https://yamada-ui.com/docs/components/aspect-ratio
 */
export const AspectRatio = withContext("div")(
  undefined,
  ({ children, ratio = 4 / 3, ...rest }) => {
    const child = Children.only(children)

    return {
      ...rest,
      children: child,
      _before: {
        display: "block",
        h: 0,
        pb: replaceObject(ratio, (r) => `${(1 / r) * 100}%`),
      },
    }
  },
)
