"use client"

import type { HTMLStyledProps, ThemeProps } from "@yamada-ui/react"
import type { AvatarStyle } from "./avatar.style"
import type { UseAvatarGroupProps } from "./use-avatar-group"
import { useMemo } from "react"
import { styled } from "@yamada-ui/react"
import {
  Avatar,
  AvatarPropsContext,
  component,
  useRootComponentProps,
} from "./avatar"
import { useAvatarGroup } from "./use-avatar-group"

export interface AvatarGroupRootProps
  extends HTMLStyledProps, ThemeProps<AvatarStyle>, UseAvatarGroupProps {}

/**
 * `Avatar` is a component that displays a profile picture or an icon with initials representing a user.
 *
 * @see https://yamada-ui.com/docs/components/avatar
 */
export const AvatarGroupRoot = component<"div", AvatarGroupRootProps>(
  (props) => {
    const [, { colorScheme, size, variant, shape, ...rest }] =
      useRootComponentProps(props, "group", {
        transferProps: ["variant", "colorScheme", "size", "shape"],
      })
    const { children, excess, getRootProps } = useAvatarGroup(rest)
    const context = useMemo(
      () => ({ colorScheme, size, variant, shape }),
      [variant, size, colorScheme, shape],
    )

    return (
      <AvatarPropsContext value={context}>
        <styled.div {...getRootProps()}>
          {excess > 0 ? <Avatar fallback={`+${excess}`} /> : null}
          {children}
        </styled.div>
      </AvatarPropsContext>
    )
  },
  "group",
)()

import { defineComponentSlotStyle } from "@yamada-ui/react"

export const avatarStyle = defineComponentSlotStyle({
  base: {
    fallback: {
      alignItems: "center",
      boxSize: "100%",
      display: "inline-flex",
      fontWeight: "medium",
      justifyContent: "center",
      textTransform: "uppercase",
    },
    group: {
      alignItems: "center",
      display: "flex",
      flexDirection: "row-reverse",
      justifyContent: "flex-end",
      _notFirstChild: {
        me: "-2",
      },
    },
    image: {
      boxSize: "100%",
      objectFit: "cover",
    },
    root: {
      flexShrink: "0",
      overflow: "hidden",
      position: "relative",
      userSelect: "none",
      verticalAlign: "top",
    },
  },

  props: {
    /**
     * The shape of the component
     *
     * @default 'circle'
     */
    shape: {
      circle: {
        root: { rounded: "full" },
      },
      rounded: {
        root: { rounded: "l2" },
      },
      square: {
        root: { rounded: "0" },
      },
    },
  },

  variants: {
    outline: {
      group: {
        _child: {
          _loaded: {
            borderColor: "bg !important",
            borderWidth: "1px",
          },
        },
      },
      root: {
        layerStyle: "outline",
        _loaded: {
          bg: "transparent",
          borderColor: "transparent",
        },
      },
    },
    solid: {
      group: {
        _child: {
          borderColor: "bg",
          borderWidth: "1px",
        },
      },
      root: {
        layerStyle: "solid",
        _loaded: {
          bg: "transparent",
        },
      },
    },
    subtle: {
      group: {
        _child: {
          borderColor: "bg",
          borderWidth: "1px",
        },
      },
      root: {
        layerStyle: "subtle",
        _loaded: {
          bg: "transparent",
        },
      },
    },
    surface: {
      root: {
        layerStyle: "surface",
        _loaded: {
          bg: "transparent",
          borderColor: "transparent",
        },
      },
    },
  },

  sizes: {
    xs: {
      fallback: {
        fontSize: "xs",
        lineHeight: "{sizes.8}",
      },
      root: {
        boxSize: "8",
      },
    },
    sm: {
      fallback: {
        fontSize: "sm",
        lineHeight: "{sizes.9}",
      },
      root: {
        boxSize: "9",
      },
    },
    md: {
      fallback: {
        fontSize: "md",
        lineHeight: "{sizes.10}",
      },
      root: {
        boxSize: "10",
      },
    },
    lg: {
      fallback: {
        fontSize: "md",
        lineHeight: "{sizes.11}",
      },
      root: {
        boxSize: "11",
      },
    },
    xl: {
      fallback: {
        fontSize: "lg",
        lineHeight: "{sizes.12}",
      },
      root: {
        boxSize: "12",
      },
    },
  },

  defaultProps: {
    size: "md",
    variant: "solid",
    shape: "circle",
  },
})

export type AvatarStyle = typeof avatarStyle

"use client"

import type { ReactElement } from "react"
import type { HTMLStyledProps, ThemeProps } from "@yamada-ui/react"
import type { AvatarStyle } from "./avatar.style"
import type { UseAvatarProps } from "./use-avatar"
import { createSlotComponent, styled } from "@yamada-ui/react"
import { UserRoundIcon } from "../icon"
import { avatarStyle } from "./avatar.style"
import { useAvatar } from "./use-avatar"

export interface AvatarProps
  extends HTMLStyledProps, ThemeProps<AvatarStyle>, UseAvatarProps {
  /**
   * The avatar icon to use.
   */
  icon?: ReactElement
  /**
   * The props to pass to the fallback component.
   */
  fallbackProps?: AvatarFallbackProps
  /**
   * The props to pass to the image component.
   */
  imageProps?: AvatarImageProps
}

const {
  component,
  PropsContext: AvatarPropsContext,
  usePropsContext: useAvatarPropsContext,
  withContext,
  withProvider,
  useRootComponentProps,
} = createSlotComponent<AvatarProps, AvatarStyle>("avatar", avatarStyle)

export {
  AvatarPropsContext,
  component,
  useAvatarPropsContext,
  useRootComponentProps,
}

/**
 * `Avatar` is a component that displays a profile picture or an icon with initials representing a user.
 *
 * @see https://yamada-ui.com/docs/components/avatar
 */
export const Avatar = withProvider<"div", AvatarProps>(
  ({ fallbackProps, imageProps, ...rest }) => {
    const { getFallbackProps, getImageProps, getRootProps } = useAvatar(rest)

    return (
      <styled.div {...getRootProps()}>
        <AvatarFallback {...getFallbackProps(fallbackProps)} />
        <AvatarImage {...getImageProps(imageProps)} />
      </styled.div>
    )
  },
  "root",
)(({ icon, ...rest }) => ({
  icon: icon ?? <UserRoundIcon fontSize="1.2em" />,
  ...rest,
}))

interface AvatarImageProps extends HTMLStyledProps<"img"> {}

const AvatarImage = withContext<"img", AvatarImageProps>("img", "image")()

interface AvatarFallbackProps extends HTMLStyledProps {}

const AvatarFallback = withContext<"div", AvatarFallbackProps>(
  "div",
  "fallback",
)()

import type { HTMLProps, PropGetter } from "@yamada-ui/react"
import { useCallback, useMemo } from "react"
import { isUndefined, useValidChildren } from "@yamada-ui/react"

export interface UseAvatarGroupProps extends HTMLProps {
  /**
   * The maximum number of visible avatars.
   */
  max?: number
  /**
   * Whether to reverse the order of the avatars.
   */
  reverse?: boolean
}

export const useAvatarGroup = ({
  children,
  max,
  reverse,
  ...rest
}: UseAvatarGroupProps = {}) => {
  const validChildren = useValidChildren(children)
  const excess = !isUndefined(max) ? validChildren.length - max : 0

  const getRootProps: PropGetter = useCallback(
    (props) => ({ ...rest, ...props, role: "group" }),
    [rest],
  )

  const cloneChildren = useMemo(() => {
    const omittedChildren = !isUndefined(max)
      ? validChildren.slice(0, max)
      : [...validChildren]

    return reverse ? omittedChildren.reverse() : omittedChildren
  }, [max, validChildren, reverse])

  return {
    children: cloneChildren,
    excess,
    getRootProps,
  }
}

export type UseAvatarGroupReturn = ReturnType<typeof useAvatarGroup>

"use client"

import type { ReactElement, ReactNode } from "react"
import type { HTMLProps, PropGetter } from "@yamada-ui/react"
import { useCallback, useEffect, useRef, useState } from "react"
import { useI18n } from "../../providers/i18n-provider"
import { dataAttr, handlerAll, mergeRefs } from "@yamada-ui/react"

const defaultFormat = (name: string) => {
  const names = name.trim().split(" ")
  const firstName = names[0] ?? ""
  const lastName = names.length > 1 ? names[names.length - 1] : ""

  return firstName && lastName
    ? `${firstName.charAt(0)}${lastName.charAt(0)}`
    : firstName.charAt(0)
}

export interface UseAvatarProps extends HTMLProps {
  /**
   * The name of the person in the avatar.
   *
   * - If `src` has loaded, the name will be used as the `alt` attribute of the `img`
   * - If `src` is not loaded, the name will be used to create the initials
   */
  name?: string
  /**
   * The image url of the avatar.
   */
  src?: HTMLProps<"img">["src"]
  /**
   * List of sources to use for different screen resolutions.
   */
  srcSet?: HTMLProps<"img">["srcSet"]
  /**
   * The `HTMLImageElement` property `alt`.
   */
  alt?: HTMLProps<"img">["alt"]
  /**
   * The `HTMLImageElement` property `crossOrigin`.
   */
  crossOrigin?: HTMLProps<"img">["crossOrigin"]
  /**
   * The fallback text to display if the image is not provided.
   */
  fallback?: ReactNode
  /**
   * Function to get the initials to display.
   */
  format?: (name: string) => string
  /**
   * The avatar icon to use.
   */
  icon?: ReactElement
  /**
   * Defines loading strategy.
   */
  loading?: HTMLProps<"img">["loading"]
  /**
   * Defining which referrer is sent when fetching the resource.
   *
   * @default 'no-referrer'
   */
  referrerPolicy?: HTMLProps<"img">["referrerPolicy"]
}

export const useAvatar = ({
  name,
  src,
  srcSet,
  alt,
  crossOrigin,
  fallback: fallbackMessage,
  format = defaultFormat,
  icon,
  loading,
  referrerPolicy = "no-referrer",
  ...rest
}: UseAvatarProps = {}) => {
  const imageRef = useRef<HTMLImageElement>(null)
  const initials = name ? format(name) : undefined
  const [loaded, setLoaded] = useState<boolean>(false)
  const fallback = !src || !loaded
  const { t } = useI18n("avatar")

  useEffect(() => {
    if (!imageRef.current) return

    if (!!imageRef.current.src && imageRef.current.complete) setLoaded(true)
  }, [])

  const getGroupProps: PropGetter = useCallback((props) => ({ ...props }), [])

  const getRootProps: PropGetter = useCallback(
    (props) => ({
      ...rest,
      ...props,
      "data-fallback": dataAttr(!!fallbackMessage),
      "data-loaded": dataAttr(loaded),
    }),
    [loaded, fallbackMessage, rest],
  )

  const getImageProps: PropGetter<"img"> = useCallback(
    ({ ref, onLoad, ...props } = {}) => ({
      ...props,
      ref: mergeRefs(ref, imageRef),
      src,
      srcSet,
      alt: name || alt,
      crossOrigin,
      draggable: false,
      hidden: fallback,
      loading,
      referrerPolicy,
      onLoad: handlerAll(onLoad, () => setLoaded(true)),
    }),
    [src, srcSet, alt, crossOrigin, loading, referrerPolicy, fallback, name],
  )

  const getFallbackProps: PropGetter = useCallback(
    (props) => ({
      ...props,
      "aria-label": !fallbackMessage
        ? name || alt || t("Avatar Icon")
        : undefined,
      children: fallbackMessage || initials || icon,
      hidden: !fallback,
      role: "img",
    }),
    [name, initials, fallback, icon, fallbackMessage, alt, t],
  )

  return {
    name,
    loaded,
    getFallbackProps,
    getGroupProps,
    getImageProps,
    getRootProps,
  }
}

export type UseAvatarReturn = ReturnType<typeof useAvatar>
