import { defineComponentSlotStyle } from "@yamada-ui/react"

export const breadcrumbStyle = defineComponentSlotStyle({
  base: {
    ellipsis: {
      color: "fg.muted",
    },
    item: {
      alignItems: "center",
      display: "inline-flex",
    },
    link: {
      rounded: "l1",
      textDecoration: "none",
      transitionDuration: "moderate",
      transitionProperty: "common",
    },
    list: {
      alignItems: "center",
      display: "flex",
      wordBreak: "break-word",
    },
    root: {},
    separator: {
      color: "fg.muted",
    },
  },

  variants: {
    plain: {
      link: {
        _notCurrent: {
          color: "fg.muted",
          _hover: {
            color: "fg",
          },
        },
      },
    },
    underline: {
      link: {
        _notCurrent: {
          _hover: {
            textDecoration: "underline",
          },
        },
      },
    },
  },

  sizes: {
    sm: {
      list: {
        fontSize: "sm",
        gap: "1",
      },
    },
    md: {
      list: {
        fontSize: "md",
        gap: "1.5",
      },
    },
    lg: {
      list: {
        fontSize: "lg",
        gap: "2",
      },
    },
  },

  defaultProps: {
    size: "md",
    variant: "plain",
  },
})

export type BreadcrumbStyle = typeof breadcrumbStyle

"use client"

import type { ReactElement } from "react"
import type { HTMLStyledProps, PropGetter, ThemeProps } from "@yamada-ui/react"
import type { BreadcrumbStyle } from "./breadcrumb.style"
import type { UseBreadcrumbProps } from "./use-breadcrumb"
import { Fragment, useMemo } from "react"
import { createSlotComponent, styled } from "@yamada-ui/react"
import { ChevronRightIcon, EllipsisIcon } from "../icon"
import { breadcrumbStyle } from "./breadcrumb.style"
import { useBreadcrumb } from "./use-breadcrumb"

interface ComponentContext {
  getEllipsisProps: PropGetter<"svg">
  getLinkProps: PropGetter<"a", { currentPage?: boolean }>
}

export interface BreadcrumbRootProps
  extends
    HTMLStyledProps<"nav">,
    ThemeProps<BreadcrumbStyle>,
    Omit<UseBreadcrumbProps, "endBoundaries" | "startBoundaries"> {
  /**
   * Number of elements visible on the end(right) edges.
   *
   * @default 0
   */
  endBoundaries?: number
  /**
   * The visual separator between each breadcrumb item.
   *
   * @default '/'
   */
  separator?: ReactElement | string
  /**
   * Number of elements visible on the start(left) edges.
   *
   * @default 0
   */
  startBoundaries?: number
  /**
   * Props for item element.
   */
  itemProps?: BreadcrumbItemProps
  /**
   * Props for ol element.
   */
  listProps?: HTMLStyledProps<"ol">
  /**
   * Props for separator element.
   */
  separatorProps?: BreadcrumbSeparatorProps
}

const {
  ComponentContext,
  PropsContext: BreadcrumbPropsContext,
  useComponentContext,
  usePropsContext: useBreadcrumbPropsContext,
  withContext,
  withProvider,
} = createSlotComponent<BreadcrumbRootProps, BreadcrumbStyle, ComponentContext>(
  "breadcrumb",
  breadcrumbStyle,
)

export { BreadcrumbPropsContext, useBreadcrumbPropsContext }

/**
 * `Breadcrumb` is a component that helps users understand the hierarchy of a website.
 *
 * @see https://yamada-ui.com/docs/components/breadcrumb
 */
export const BreadcrumbRoot = withProvider<"nav", BreadcrumbRootProps>(
  ({
    endBoundaries,
    gap,
    separator,
    startBoundaries,
    itemProps,
    listProps,
    separatorProps,
    ...rest
  }) => {
    const {
      children,
      getEllipsisProps,
      getLinkProps,
      getListProps,
      getRootProps,
    } = useBreadcrumb({
      ellipsis: <BreadcrumbEllipsis />,
      endBoundaries,
      link: <BreadcrumbLink />,
      startBoundaries,
      ...rest,
    })

    const context = useMemo(
      () => ({ getEllipsisProps, getLinkProps }),
      [getEllipsisProps, getLinkProps],
    )

    return (
      <ComponentContext value={context}>
        <styled.nav {...getRootProps()}>
          <BreadcrumbList {...getListProps({ gap, ...listProps })}>
            {children.map((child, index) => {
              const last = index === children.length - 1

              return (
                <Fragment key={index}>
                  <BreadcrumbItem {...itemProps}>{child}</BreadcrumbItem>

                  {!last ? (
                    <BreadcrumbSeparator {...separatorProps}>
                      {separator}
                    </BreadcrumbSeparator>
                  ) : null}
                </Fragment>
              )
            })}
          </BreadcrumbList>
        </styled.nav>
      </ComponentContext>
    )
  },
  "root",
)()

interface BreadcrumbListProps extends HTMLStyledProps<"ol"> {}

const BreadcrumbList = withContext<"ol", BreadcrumbListProps>("ol", "list")()

interface BreadcrumbItemProps extends HTMLStyledProps<"li"> {}

const BreadcrumbItem = withContext<"li", BreadcrumbItemProps>("li", "item")()

interface BreadcrumbSeparatorProps extends HTMLStyledProps<"li"> {}

const BreadcrumbSeparator = withContext<"li", BreadcrumbSeparatorProps>(
  BreadcrumbItem,
  "separator",
)(({ children, ...rest }) => ({
  children: children ?? <ChevronRightIcon />,
  ...rest,
}))

export interface BreadcrumbLinkProps extends HTMLStyledProps<"a"> {
  /**
   * If `true`, change to span element.
   *
   * @default false
   */
  currentPage?: boolean
}

export const BreadcrumbLink = withContext<"a", BreadcrumbLinkProps>(
  "a",
  "link",
)(undefined, ({ currentPage, ...rest }) => {
  const { getLinkProps } = useComponentContext()

  return {
    as: !currentPage ? "a" : "span",
    ...getLinkProps({ currentPage, ...rest }),
  }
})

export interface BreadcrumbEllipsisProps extends HTMLStyledProps<"svg"> {}

export const BreadcrumbEllipsis = withContext<"svg", BreadcrumbEllipsisProps>(
  EllipsisIcon,
  "ellipsis",
)(undefined, (props) => {
  const { getEllipsisProps } = useComponentContext()

  return { ...getEllipsisProps(props) }
})

export {
  BreadcrumbEllipsis as Ellipsis,
  BreadcrumbLink as Link,
  BreadcrumbPropsContext as PropsContext,
  BreadcrumbRoot as Root,
  useBreadcrumbPropsContext as usePropsContext,
} from "./breadcrumb"
export type {
  BreadcrumbEllipsisProps as EllipsisProps,
  BreadcrumbLinkProps as LinkProps,
  BreadcrumbRootProps as RootProps,
} from "./breadcrumb"
export type { BreadcrumbItem as Item } from "./use-breadcrumb"

"use client"

import type { ReactElement, ReactNode } from "react"
import type {
  HTMLProps,
  PropGetter,
  ReactNodeOrFunction,
} from "@yamada-ui/react"
import { cloneElement, useCallback, useMemo } from "react"
import { useI18n } from "../../providers/i18n-provider"
import { runIfFn, useValidChildren } from "@yamada-ui/react"

export interface BreadcrumbItem extends HTMLProps<"a"> {
  currentPage?: boolean
  label?: ReactNode
}

export interface UseBreadcrumbProps extends HTMLProps<"nav"> {
  /**
   * The icon to be used in the ellipsis.
   */
  ellipsis?: ReactNodeOrFunction<{ items: BreadcrumbItem[] }>
  /**
   * Number of elements visible on the end(right) edges.
   *
   * @default 0
   */
  endBoundaries?: number
  /**
   * If provided, generate breadcrumb items based on items.
   */
  items?: BreadcrumbItem[]
  /**
   * The component to be used for the link.
   */
  link?: ReactElement<any>
  /**
   * Number of elements visible on the start(left) edges.
   *
   * @default 0
   */
  startBoundaries?: number
}

export const useBreadcrumb = ({
  children,
  ellipsis,
  endBoundaries = 0,
  items = [],
  link,
  startBoundaries = 0,
  ...rest
}: UseBreadcrumbProps = {}) => {
  const validChildren = useValidChildren(children)
  const length = validChildren.length || items.length
  const { t } = useI18n("breadcrumb")

  const hasBoundaries =
    startBoundaries + endBoundaries > 0 &&
    length > startBoundaries + endBoundaries

  const renderLink = useCallback(
    (items: BreadcrumbItem[]) => {
      if (!link) return []

      return items.map(({ label, ...rest }) =>
        cloneElement<any>(link, { ...link.props, children: label, ...rest }),
      )
    },
    [link],
  )

  const renderEllipsis = useCallback(
    (items: BreadcrumbItem[]) => {
      if (!ellipsis) return null

      return runIfFn(ellipsis, { items })
    },
    [ellipsis],
  )

  const splitItems = useCallback(
    (items: BreadcrumbItem[]) => {
      const length = items.length
      const firstItems = items.slice(0, startBoundaries)
      const ellipsisItems = items.slice(startBoundaries, length - endBoundaries)
      const lastItems = items.slice(length - endBoundaries)

      return [firstItems, ellipsisItems, lastItems] as const
    },
    [startBoundaries, endBoundaries],
  )

  const cloneChildren = useMemo(() => {
    const hasChildren = !!validChildren.length

    if (hasChildren) {
      return validChildren
    } else if (hasBoundaries) {
      const [firstItems, ellipsisItems, lastItems] = splitItems(items)

      return [
        ...renderLink(firstItems),
        renderEllipsis(ellipsisItems),
        ...renderLink(lastItems),
      ]
    } else {
      return renderLink(items)
    }
  }, [
    validChildren,
    items,
    hasBoundaries,
    renderEllipsis,
    renderLink,
    splitItems,
  ])

  const getRootProps: PropGetter<"nav"> = useCallback(
    (props) => ({
      "aria-label": t("Breadcrumb"),
      ...rest,
      ...props,
    }),
    [rest, t],
  )

  const getListProps: PropGetter<"ol"> = useCallback(
    (props) => ({ ...props }),
    [],
  )

  const getLinkProps: PropGetter<"a", { currentPage?: boolean }> = useCallback(
    ({ href, currentPage, ...props } = {}) => ({
      href: !currentPage ? href : undefined,
      "aria-current": currentPage ? "page" : undefined,
      ...props,
    }),
    [],
  )

  const getEllipsisProps: PropGetter<"svg"> = useCallback(
    (props) => ({
      "aria-label": t("Ellipsis"),
      role: "presentation",
      ...props,
    }),
    [t],
  )

  return {
    children: cloneChildren,
    getEllipsisProps,
    getLinkProps,
    getListProps,
    getRootProps,
  }
}

export type UseBreadcrumbReturn = ReturnType<typeof useBreadcrumb>
