import { defineComponentSlotStyle, focusRingStyle } from "@yamada-ui/react"
import { checkboxStyle } from "../checkbox"

export const checkboxCardStyle = defineComponentSlotStyle({
  base: {
    addon: {
      borderColor: "{--addon-border-color}",
      borderTopWidth: "1px",
      fontSize: "{description-size}",
      mt: "{space-y}",
      pt: "{space-y}",
      px: "{space-x}",
    },
    description: { color: "fg.muted", fontSize: "{description-size}" },
    group: { w: "full" },
    indicator: {
      ...checkboxStyle.base?.indicator,
      "input:focus-visible + &": {
        _checked: focusRingStyle.outline,
        _notChecked: {
          borderColor: "{focus-ring-color}",
        },
      },
      position: "absolute",
      top: "calc({space-y} + ({label-size} * 1.5 - {label-size}) / 2)",
    },
    root: {
      ...checkboxStyle.base?.root,
      "&:has(input:focus-visible)": focusRingStyle.outline,
      "--addon-border-color": "{root-border-color}",
      "--root-border-color": "colors.border",
      alignItems: "stretch",
      borderColor: "{--root-border-color}",
      borderWidth: "1px",
      flexDirection: "column",
      focusRingColor: "{focus-border-color}",
      gap: "1",
      position: "relative",
      px: "{space-x}",
      py: "{space-y}",
      rounded: "l2",
      w: "full",
      _invalid: {
        borderColor: "{error-border-color}",
        focusRingColor: "{error-border-color}",
      },
    },
  },

  props: {
    /**
     * The justify indicator of the component
     *
     * @default 'start'
     */
    justify: {
      end: {
        addon: {
          me: "calc({bleed-size} * -1)",
          ms: "calc({space-x} * -1)",
        },
        indicator: { right: "{space-x}" },
        root: {
          "&:has([data-indicator])": {
            "--bleed-size": "calc({indicator-size} + ({space-x} * 2))",
          },
          "--bleed-size": "{space-x}",
          pe: "{bleed-size}",
        },
      },
      start: {
        addon: {
          me: "calc({space-x} * -1)",
          ms: "calc({bleed-size} * -1)",
        },
        indicator: { left: "{space-x}" },
        root: {
          "&:has([data-indicator])": {
            "--bleed-size": "calc({indicator-size} + ({space-x} * 2))",
          },
          "--bleed-size": "{space-x}",
          ps: "{bleed-size}",
        },
      },
    },
    /**
     * The shape of the component
     *
     * @default 'circle'
     */
    shape: { ...checkboxStyle.props?.shape },
  },

  variants: {
    base: {
      indicator: {
        color: "colorScheme.contrast",
        _checked: { bg: "colorScheme.solid" },
        _indeterminate: { bg: "colorScheme.solid" },
      },
      root: {
        _checked: { "--indicator-border-color": "colorScheme.solid" },
        _indeterminate: { "--indicator-border-color": "colorScheme.solid" },
      },
    },
    outline: {
      root: {
        _checked: {
          "--root-border-color": "colorScheme.outline",
        },
      },
    },
    subtle: {
      root: {
        _checked: {
          "--addon-border-color": "colorScheme.muted",
          "--root-border-color": "transparent",
          bg: "colorScheme.subtle",
        },
      },
    },
    surface: {
      root: {
        _checked: {
          "--root-border-color": "colorScheme.muted",
          bg: "colorScheme.subtle",
        },
      },
    },
  },

  sizes: {
    sm: {
      indicator: checkboxStyle.sizes?.sm.indicator,
      root: {
        ...checkboxStyle.sizes?.sm.root,
        "--description-size": "fontSizes.xs",
        "--space-x": "spaces.3",
        "--space-y": "spaces.2",
      },
    },
    md: {
      indicator: checkboxStyle.sizes?.md.indicator,
      root: {
        ...checkboxStyle.sizes?.md.root,
        "--description-size": "fontSizes.sm",
        "--space-x": "spaces.4",
        "--space-y": "spaces.3",
      },
    },
    lg: {
      indicator: checkboxStyle.sizes?.lg.indicator,
      root: {
        ...checkboxStyle.sizes?.lg.root,
        "--description-size": "fontSizes.md",
        "--space-x": "spaces.5",
        "--space-y": "spaces.4",
      },
    },
    xl: {
      indicator: checkboxStyle.sizes?.lg.indicator,
      root: {
        ...checkboxStyle.sizes?.lg.root,
        "--description-size": "fontSizes.md",
        "--space-x": "spaces.6",
        "--space-y": "spaces.5",
      },
    },
  },

  defaultProps: {
    size: "md",
    variant: "surface",
    justify: "start",
    shape: "rounded",
  },
})

export type CheckboxCardStyle = typeof checkboxCardStyle

"use client"

import type { ReactElement, ReactNode } from "react"
import type {
  GenericsComponent,
  HTMLStyledProps,
  ThemeProps,
  Merge,
} from "@yamada-ui/react"
import type { UseCheckboxProps } from "../checkbox"
import type { UseInputBorderProps } from "../input"
import type { CheckboxCardStyle } from "./checkbox-card.style"
import { useMemo } from "react"
import { createSlotComponent, styled } from "@yamada-ui/react"
import { useCheckbox } from "../checkbox"
import { CheckIcon, MinusIcon } from "../icon"
import { useInputBorder } from "../input"
import { checkboxCardStyle } from "./checkbox-card.style"

export interface CheckboxCardRootProps<Y extends string = string>
  extends Merge<HTMLStyledProps<"label">, UseCheckboxProps<Y>>,
    ThemeProps<CheckboxCardStyle>,
    UseInputBorderProps {
  /**
   * The addon of the checkbox card.
   */
  addon?: ReactNode
  /**
   * The icon to display in the checkbox when it is checked.
   */
  checkedIcon?: ReactNode
  /**
   * The description of the checkbox card.
   */
  description?: ReactNode
  /**
   * The icon to display in the checkbox when it is indeterminate.
   */
  indeterminateIcon?: ReactNode
  /**
   * The label of the checkbox card.
   */
  label?: ReactNode
  /**
   * If `true`, the indicator will be displayed.
   *
   * @default true
   */
  withIndicator?: boolean
  /**
   * Props for the addon component.
   */
  addonProps?: CheckboxCardAddonProps
  /**
   * Props for the description component.
   */
  descriptionProps?: CheckboxCardDescriptionProps
  /**
   * Props for the indicator component.
   */
  indicatorProps?: CheckboxCardIndicatorProps
  /**
   * Props for the input element.
   */
  inputProps?: HTMLStyledProps<"input">
  /**
   * Props for the label component.
   */
  labelProps?: CheckboxCardLabelProps
  /**
   * Props for the label element.
   */
  rootProps?: HTMLStyledProps<"label">
}

const {
  component,
  PropsContext: CheckboxCardPropsContext,
  usePropsContext: useCheckboxCardPropsContext,
  withContext,
  withProvider,
  useRootComponentProps,
} = createSlotComponent<CheckboxCardRootProps, CheckboxCardStyle>(
  "checkbox-card",
  checkboxCardStyle,
)

export {
  CheckboxCardPropsContext,
  component,
  useCheckboxCardPropsContext,
  useRootComponentProps,
}

/**
 * `CheckboxCard` is a component used for allowing users to select multiple values from multiple options.
 *
 * @see https://yamada-ui.com/docs/components/checkbox-card
 */
export const CheckboxCardRoot = withProvider<"label", CheckboxCardRootProps>(
  ({
    addon,
    checkedIcon,
    children,
    description,
    errorBorderColor,
    focusBorderColor,
    indeterminateIcon,
    label,
    withIndicator = true,
    addonProps,
    descriptionProps,
    indicatorProps,
    inputProps,
    labelProps,
    rootProps,
    ...rest
  }) => {
    const {
      checked,
      indeterminate,
      getIndicatorProps,
      getInputProps,
      getRootProps,
    } = useCheckbox(rest)
    const varProps = useInputBorder({ errorBorderColor, focusBorderColor })
    const icon = useMemo(() => {
      if (indeterminate) {
        return indeterminateIcon || <MinusIcon />
      } else if (checked) {
        return checkedIcon || <CheckIcon />
      } else {
        return null
      }
    }, [indeterminate, indeterminateIcon, checked, checkedIcon])
    const computedChildren = useMemo(() => {
      if (children) return children

      return (
        <>
          {label ? (
            <CheckboxCardLabel {...labelProps}>{label}</CheckboxCardLabel>
          ) : null}
          {description ? (
            <CheckboxCardDescription {...descriptionProps}>
              {description}
            </CheckboxCardDescription>
          ) : null}
          {addon ? (
            <CheckboxCardAddon {...addonProps}>{addon}</CheckboxCardAddon>
          ) : null}
        </>
      )
    }, [
      addon,
      addonProps,
      children,
      description,
      descriptionProps,
      label,
      labelProps,
    ])

    return (
      <styled.label {...getRootProps({ ...varProps, ...rootProps })}>
        <styled.input {...getInputProps(inputProps)} />
        {withIndicator ? (
          <CheckboxCardIndicator {...getIndicatorProps(indicatorProps)}>
            {icon}
          </CheckboxCardIndicator>
        ) : null}

        {computedChildren}
      </styled.label>
    )
  },
  "root",
)() as GenericsComponent<{
  <Y extends string = string>(props: CheckboxCardRootProps<Y>): ReactElement
}>

interface CheckboxCardIndicatorProps extends HTMLStyledProps {}

const CheckboxCardIndicator = withContext<"div", CheckboxCardIndicatorProps>(
  "div",
  "indicator",
)({ "data-indicator": "" })

export interface CheckboxCardLabelProps extends HTMLStyledProps {}

export const CheckboxCardLabel = withContext<"span", CheckboxCardLabelProps>(
  "span",
  "label",
)()

export interface CheckboxCardDescriptionProps extends HTMLStyledProps {}

export const CheckboxCardDescription = withContext<
  "span",
  CheckboxCardDescriptionProps
>("span", "description")()

export interface CheckboxCardAddonProps extends HTMLStyledProps {}

export const CheckboxCardAddon = withContext<"span", CheckboxCardAddonProps>(
  "span",
  "addon",
)()
