import { defineComponentStyle } from "@yamada-ui/react"

export const codeStyle = defineComponentStyle({
  base: {
    alignItems: "center",
    display: "inline-flex",
    fontFamily: "mono",
    rounded: "l1",
  },

  variants: {
    outline: {
      layerStyle: "outline",
    },
    solid: {
      layerStyle: "solid",
    },
    subtle: {
      layerStyle: "subtle",
    },
    surface: {
      layerStyle: "surface",
    },
  },

  sizes: {
    xs: {
      fontSize: "2xs",
      lineHeight: "{sizes.4}",
      minH: "4",
      px: "1",
    },
    sm: {
      fontSize: "xs",
      lineHeight: "{sizes.5}",
      minH: "5",
      px: "1.5",
    },
    md: {
      fontSize: "sm",
      lineHeight: "{sizes.6}",
      minH: "6",
      px: "2",
    },
    lg: {
      fontSize: "sm",
      lineHeight: "{sizes.7}",
      minH: "7",
      px: "2.5",
    },
  },

  defaultProps: {
    size: "sm",
    variant: "subtle",
  },
})

export type CodeStyle = typeof codeStyle

"use client"

import type { HTMLStyledProps, ThemeProps } from "@yamada-ui/react"
import type { CodeStyle } from "./code.style"
import { createComponent } from "@yamada-ui/react"
import { codeStyle } from "./code.style"

export interface CodeProps
  extends HTMLStyledProps<"code">,
    ThemeProps<CodeStyle> {}

const {
  PropsContext: CodePropsContext,
  usePropsContext: useCodePropsContext,
  withContext,
} = createComponent<CodeProps, CodeStyle>("code", codeStyle)

export { CodePropsContext, useCodePropsContext }

/**
 * `Code` is a component that represents a code block. By default, it renders a `code` element.
 *
 * @see https://yamada-ui.com/components/code
 */
export const Code = withContext("code")()
