import { defineComponentSlotStyle } from "@yamada-ui/react"
import { modalStyle } from "../modal"

export const drawerStyle = defineComponentSlotStyle({
  base: {
    ...modalStyle.base,
    body: {
      ...modalStyle.base?.body,
      overflow: "auto",
    },
    content: {
      ...modalStyle.base?.content,
      position: "fixed",
      rounded: "l4",
    },
    dragBar: {
      bg: "bg.subtle",
      position: "absolute",
      rounded: "full",
    },
  },

  props: {
    /**
     * The placement of the drawer.
     *
     * @default 'inline-end'
     */
    placement: {
      "block-end": {
        content: { maxW: "full", roundedBottom: "0" },
        dragBar: {
          left: "50%",
          minH: "2",
          top: "md",
          transform: "translateX(-50%)",
          w: "3xs",
        },
      },
      "block-start": {
        content: { maxW: "full", roundedTop: "0" },
        dragBar: {
          bottom: "md",
          left: "50%",
          minH: "2",
          transform: "translateX(-50%)",
          w: "3xs",
        },
      },
      "inline-end": {
        content: { roundedRight: "0" },
        dragBar: {
          h: "3xs",
          left: "md",
          minW: "2",
          top: "50%",
          transform: "translateY(-50%)",
        },
      },
      "inline-start": {
        content: { roundedLeft: "0" },
        dragBar: {
          h: "3xs",
          minW: "2",
          right: "md",
          top: "50%",
          transform: "translateY(-50%)",
        },
      },
    },
  },

  sizes: {
    xs: { content: { maxW: "xs" } },
    sm: { content: { maxW: "sm" } },
    md: { content: { maxW: "md" } },
    lg: { content: { maxW: "lg" } },
    xl: { content: { maxW: "xl" } },
    "2xl": { content: { maxW: "2xl" } },
    "3xl": { content: { maxW: "3xl" } },
    "4xl": { content: { maxW: "4xl" } },
    "5xl": { content: { maxW: "5xl" } },
    "6xl": { content: { maxW: "6xl" } },
    full: { content: { minH: "dvh", minW: "vw", rounded: "0" } },
  },

  compounds: [
    {
      css: {
        content: {
          pb: "md",
          _after: {
            bg: "bg.panel",
            display: "block",
            h: "dvh",
            left: "0",
            position: "absolute",
            right: "0",
            top: "calc(-100dvh + 1px)",
            w: "full",
          },
        },
      },
      closeOnDrag: true,
      placement: "block-start",
      withDragBar: true,
    },
    {
      css: {
        content: {
          pt: "md",
          _after: {
            bg: "bg.panel",
            bottom: "calc(-100dvh + 1px)",
            display: "block",
            h: "dvh",
            left: "0",
            position: "absolute",
            right: "0",
            w: "full",
          },
        },
      },
      closeOnDrag: true,
      placement: "block-end",
      withDragBar: true,
    },
    {
      css: {
        content: {
          ps: "md",
          _after: {
            bg: "bg.panel",
            bottom: "0",
            display: "block",
            h: "dvh",
            position: "absolute",
            right: "calc(-100% + 1px)",
            top: "0",
            w: "full",
          },
        },
      },
      closeOnDrag: true,
      placement: "inline-end",
      withDragBar: true,
    },
    {
      css: {
        content: {
          pe: "md",
          _after: {
            bg: "bg.panel",
            bottom: "0",
            display: "block",
            h: "dvh",
            left: "calc(-100% + 1px)",
            position: "absolute",
            top: "0",
            w: "full",
          },
        },
      },
      closeOnDrag: true,
      placement: "inline-start",
      withDragBar: true,
    },
  ],

  defaultProps: {
    size: "lg",
    placement: "inline-end",
  },
})

export type DrawerStyle = typeof drawerStyle

"use client"

import type { FC, PropsWithChildren, ReactNode } from "react"
import type { HTMLProps, HTMLStyledProps, ThemeProps } from "@yamada-ui/react"
import type { ButtonProps } from "../button"
import type { CloseButtonProps } from "../close-button"
import type { FocusLockProps } from "../focus-lock"
import type { HTMLMotionProps, MotionTransitionProps } from "../motion"
import type { PortalProps } from "../portal"
import type { SlideProps } from "../slide"
import type { DrawerStyle } from "./drawer.style"
import type { UseDrawerProps, UseDrawerReturn } from "./use-drawer"
import { AnimatePresence } from "motion/react"
import { useMemo } from "react"
import { RemoveScroll } from "react-remove-scroll"
import {
  createSlotComponent,
  styled,
  cast,
  isArray,
  useSplitChildren,
  wrapOrPassProps,
} from "@yamada-ui/react"
import { useValue } from "../../hooks/use-value"
import { Button } from "../button"
import { CloseButton } from "../close-button"
import { fadeVariants } from "../fade"
import { FocusLock } from "../focus-lock"
import { motion } from "../motion"
import { Portal } from "../portal"
import { Slide } from "../slide"
import { drawerStyle } from "./drawer.style"
import { useDrawer } from "./use-drawer"

interface ComponentContext
  extends Omit<UseDrawerReturn, "getRootProps">,
    Pick<
      DrawerRootProps,
      "duration" | "placement" | "withCloseButton" | "withDragBar"
    > {}

export interface DrawerRootProps
  extends ThemeProps<DrawerStyle>,
    Omit<UseDrawerProps, "placement" | "title">,
    Pick<
      FocusLockProps,
      | "autoFocus"
      | "finalFocusRef"
      | "initialFocusRef"
      | "lockFocusAcrossFrames"
      | "restoreFocus"
    >,
    ShorthandDrawerContentProps {
  /**
   * Handle zoom or pinch gestures on iOS devices when scroll locking is enabled.
   *
   * @default false.
   */
  allowPinchZoom?: boolean
  /**
   * If `true`, scrolling will be disabled on the `body` when the drawer opens.
   *
   * @default true
   */
  blockScrollOnMount?: boolean
  /**
   * The animation duration.
   */
  duration?: MotionTransitionProps["duration"]
  /**
   * The modal trigger to use.
   */
  trigger?: ReactNode
  /**
   * If `true`, display the drawer close button.
   *
   * @default true
   */
  withCloseButton?: boolean
  /**
   * If `true`, display the drag bar when `closeOnDrag` is `true`.
   *
   * @default true
   */
  withDragBar?: boolean
  /**
   * If `true`, display the drawer overlay.
   *
   * @default true
   */
  withOverlay?: boolean
  /**
   * Props to be forwarded to the portal component.
   */
  portalProps?: Omit<PortalProps, "children">
  /**
   * Callback function to run side effects after the drawer has closed.
   */
  onCloseComplete?: () => void
}

const {
  ComponentContext,
  PropsContext: DrawerPropsContext,
  useComponentContext,
  usePropsContext: useDrawerPropsContext,
  withContext,
  withProvider,
} = createSlotComponent<DrawerRootProps, DrawerStyle, ComponentContext>(
  "drawer",
  drawerStyle,
)

export { DrawerPropsContext, useDrawerPropsContext }

/**
 * `Drawer` is a component for a panel that appears from the edge of the screen.
 *
 * @see https://yamada-ui.com/docs/components/drawer
 */
export const DrawerRoot = withProvider<"div", DrawerRootProps, "placement">(
  ({
    allowPinchZoom = false,
    autoFocus,
    blockScrollOnMount = true,
    body,
    cancel,
    children,
    closeOnDrag,
    dragConstraints,
    dragElastic,
    dragOffset,
    dragVelocity,
    duration = { enter: 0.4, exit: 0.3 },
    finalFocusRef,
    footer,
    header,
    initialFocusRef,
    lockFocusAcrossFrames = true,
    middle,
    placement: placementProp,
    restoreFocus,
    success,
    title,
    trigger,
    withCloseButton = !closeOnDrag,
    withDragBar = true,
    withOverlay = true,
    portalProps,
    onCancel,
    onCloseComplete,
    onMiddle,
    onSuccess,
    ...props
  }) => {
    const placement = useValue(placementProp)
    const [omittedChildren, openTrigger, customOverlay] = useSplitChildren(
      children,
      DrawerOpenTrigger,
      DrawerOverlay,
    )
    const hasChildren = isArray(omittedChildren) && !!omittedChildren.length
    const { open, getRootProps, ...rest } = useDrawer({
      closeOnDrag,
      dragConstraints,
      dragElastic,
      dragOffset,
      dragVelocity,
      placement,
      ...props,
    })
    const customOpenTrigger = trigger ? (
      <DrawerOpenTrigger>{trigger}</DrawerOpenTrigger>
    ) : null

    const context = useMemo(
      () => ({
        duration,
        open,
        placement,
        withCloseButton,
        withDragBar,
        ...rest,
      }),
      [duration, open, withDragBar, placement, withCloseButton, rest],
    )

    return (
      <ComponentContext value={context}>
        {openTrigger ?? customOpenTrigger}

        <AnimatePresence onExitComplete={onCloseComplete}>
          {open ? (
            <Portal {...portalProps}>
              <FocusLock
                autoFocus={autoFocus}
                finalFocusRef={finalFocusRef}
                initialFocusRef={initialFocusRef}
                lockFocusAcrossFrames={lockFocusAcrossFrames}
                restoreFocus={restoreFocus}
              >
                <RemoveScroll
                  allowPinchZoom={allowPinchZoom}
                  enabled={blockScrollOnMount}
                  forwardProps
                >
                  <styled.div {...getRootProps()}>
                    {customOverlay ?? (withOverlay ? <DrawerOverlay /> : null)}

                    {hasChildren ? (
                      omittedChildren
                    ) : (
                      <ShorthandDrawerContent
                        body={body}
                        cancel={cancel}
                        footer={footer}
                        header={header}
                        middle={middle}
                        success={success}
                        title={title}
                        onCancel={onCancel}
                        onMiddle={onMiddle}
                        onSuccess={onSuccess}
                      />
                    )}
                  </styled.div>
                </RemoveScroll>
              </FocusLock>
            </Portal>
          ) : null}
        </AnimatePresence>
      </ComponentContext>
    )
  },
  "root",
  { transferProps: ["placement"] },
)()

export interface DrawerOpenTriggerProps extends HTMLStyledProps<"button"> {}

export const DrawerOpenTrigger = withContext<"button", DrawerOpenTriggerProps>(
  "button",
  { name: "OpenTrigger", slot: ["trigger", "open"] },
)(undefined, (props) => {
  const { getOpenTriggerProps } = useComponentContext()

  return { asChild: true, ...getOpenTriggerProps(props) }
})

export interface DrawerCloseTriggerProps extends HTMLStyledProps<"button"> {}

export const DrawerCloseTrigger = withContext<
  "button",
  DrawerCloseTriggerProps
>("button", { name: "CloseTrigger", slot: ["trigger", "close"] })(
  undefined,
  (props) => {
    const { getCloseTriggerProps } = useComponentContext()

    return { asChild: true, ...getCloseTriggerProps(props) }
  },
)

export interface DrawerCloseButtonProps extends CloseButtonProps {}

export const DrawerCloseButton = withContext<"button", DrawerCloseButtonProps>(
  CloseButton,
  "closeButton",
)(undefined, (props) => {
  const { getCloseButtonProps } = useComponentContext()

  return { ...getCloseButtonProps(props) }
})

export interface DrawerOverlayProps extends HTMLMotionProps {}

export const DrawerOverlay = withContext<"div", DrawerOverlayProps>((props) => {
  const { duration, getOverlayProps } = useComponentContext()

  return (
    <motion.div
      animate="enter"
      custom={{ duration }}
      exit="exit"
      initial="exit"
      variants={fadeVariants}
      {...cast<HTMLMotionProps>(getOverlayProps(cast<HTMLProps>(props)))}
    />
  )
}, "overlay")()

export interface DrawerContentProps
  extends Omit<HTMLMotionProps<"section">, "children">,
    PropsWithChildren {}

export const DrawerContent = withContext<"div", DrawerContentProps>(
  ({ children, ...rest }) => {
    const {
      closeOnDrag,
      duration,
      open,
      placement,
      withCloseButton,
      withDragBar,
      getContentProps,
    } = useComponentContext()
    const [omittedChildren, customCloseButton, customDragBar] =
      useSplitChildren(children, DrawerCloseButton, DrawerDragBar)

    return (
      <Slide
        as="section"
        duration={duration}
        open={open}
        placement={placement}
        {...(getContentProps(rest) as SlideProps)}
      >
        {customCloseButton ?? (withCloseButton ? <DrawerCloseButton /> : null)}
        {customDragBar ??
          (withDragBar && closeOnDrag ? <DrawerDragBar /> : null)}

        {omittedChildren}
      </Slide>
    )
  },
  "content",
)()

interface ShorthandDrawerContentProps {
  /**
   * The drawer body to use.
   */
  body?: DrawerBodyProps | ReactNode
  /**
   * The drawer cancel button to use.
   */
  cancel?: ButtonProps | ReactNode
  /**
   * The drawer footer to use.
   */
  footer?: DrawerFooterProps | ReactNode
  /**
   * The drawer header to use.
   */
  header?: DrawerHeaderProps | ReactNode
  /**
   * The drawer middle button to use.
   */
  middle?: ButtonProps | ReactNode
  /**
   * The drawer success button to use.
   */
  success?: ButtonProps | ReactNode
  /**
   * The drawer title to use.
   */
  title?: DrawerTitleProps | ReactNode
  /**
   * The callback invoked when cancel button clicked.
   */
  onCancel?: (onClose: () => void) => void
  /**
   * The callback invoked when middle button clicked.
   */
  onMiddle?: (onClose: () => void) => void
  /**
   * The callback invoked when success button clicked.
   */
  onSuccess?: (onClose: () => void) => void
}

export const ShorthandDrawerContent: FC<ShorthandDrawerContentProps> = ({
  body,
  cancel,
  footer,
  header,
  middle,
  success,
  title,
  onCancel,
  onMiddle,
  onSuccess,
}) => {
  const { onClose } = useComponentContext()
  const customHeader = wrapOrPassProps(DrawerHeader, header)
  const customTitle = wrapOrPassProps(DrawerTitle, title)
  const customBody = wrapOrPassProps(DrawerBody, body)
  const customFooter = wrapOrPassProps(DrawerFooter, footer)
  const customCancel = wrapOrPassProps(Button, cancel, {
    colorScheme: "mono",
    variant: "ghost",
    onClick: () => (onCancel ? onCancel(onClose) : onClose()),
  })
  const customMiddle = wrapOrPassProps(Button, middle, {
    colorScheme: "secondary",
    onClick: () => (onMiddle ? onMiddle(onClose) : onClose()),
  })
  const customSuccess = wrapOrPassProps(Button, success, {
    colorScheme: "primary",
    onClick: () => (onSuccess ? onSuccess(onClose) : onClose()),
  })

  return (
    <DrawerContent>
      {customHeader ??
        (customTitle ? <DrawerHeader>{customTitle}</DrawerHeader> : null)}
      {customBody}
      {customFooter ??
        (customCancel || customMiddle || customSuccess ? (
          <DrawerFooter>
            {customCancel}
            {customMiddle}
            {customSuccess}
          </DrawerFooter>
        ) : null)}
    </DrawerContent>
  )
}

export interface DrawerDragBarProps extends HTMLStyledProps {}

export const DrawerDragBar = withContext<"div", DrawerDragBarProps>(
  "div",
  "dragBar",
)(undefined, (props) => {
  const { getDragBarProps } = useComponentContext()

  return { ...getDragBarProps(props) }
})

export interface DrawerHeaderProps extends HTMLStyledProps<"header"> {}

export const DrawerHeader = withContext<"header", DrawerHeaderProps>(
  "header",
  "header",
)(undefined, (props) => {
  const { getHeaderProps } = useComponentContext()

  return { ...getHeaderProps(props) }
})

export interface DrawerTitleProps extends HTMLStyledProps<"h2"> {}

export const DrawerTitle = withContext<"h2", DrawerTitleProps>("h2", "title")(
  undefined,
  (props) => {
    const { getTitleProps } = useComponentContext()

    return { ...getTitleProps(props) }
  },
)

export interface DrawerBodyProps extends HTMLStyledProps {}

export const DrawerBody = withContext<"div", DrawerBodyProps>("div", "body")(
  undefined,
  (props) => {
    const { getBodyProps } = useComponentContext()

    return { ...getBodyProps(props) }
  },
)

export interface DrawerFooterProps extends HTMLStyledProps<"footer"> {}

export const DrawerFooter = withContext<"footer", DrawerFooterProps>(
  "footer",
  "footer",
)(undefined, (props) => {
  const { getFooterProps } = useComponentContext()

  return { ...getFooterProps(props) }
})

"use client"

import type { HTMLProps, PropGetter, SimplePlacement } from "@yamada-ui/react"
import type { UseModalProps } from "../modal"
import type { HTMLMotionProps } from "../motion"
import { useCallback, useMemo } from "react"
import { cast, handlerAll } from "@yamada-ui/react"
import { useModal } from "../modal"

type DragEndEventHandler = Required<HTMLMotionProps>["onDragEnd"]

export interface UseDrawerProps extends UseModalProps {
  /**
   * If `true`, then the drawer will close on drag.
   *
   * @default false
   */
  closeOnDrag?: boolean
  /**
   * Applies constraints on the permitted draggable area.
   *
   * @default 0
   */
  dragConstraints?: number
  /**
   * The degree of movement allowed outside constraints. 0 = no movement, 1 = full movement.
   *
   * @default 0.1
   */
  dragElastic?: number
  /**
   * Offset from being dragged to closing.
   *
   * @default 80
   */
  dragOffset?: number
  /**
   * Velocity of the drag that triggers close.
   *
   * @default 100
   */
  dragVelocity?: number
  /**
   * The placement of the drawer.
   *
   * @default 'inline-end'
   */
  placement?: SimplePlacement
}

export const useDrawer = ({
  closeOnDrag = false,
  closeOnEsc,
  closeOnOverlay,
  dragConstraints = 0,
  dragElastic = 0.1,
  dragOffset = 80,
  dragVelocity = 100,
  placement = "inline-end",
  onEsc,
  ...rest
}: UseDrawerProps = {}) => {
  const {
    getContentProps: getModalContentProps,
    onClose,
    ...modalProps
  } = useModal({
    closeOnEsc,
    closeOnOverlay,
    onEsc,
    ...rest,
  })

  const drag = useMemo<HTMLMotionProps["drag"]>(() => {
    if (!closeOnDrag) return false

    switch (placement) {
      case "block-start":
      case "block-end":
        return "y"
      case "inline-start":
      case "inline-end":
        return "x"
    }
  }, [placement, closeOnDrag])

  const getDragRestriction = useCallback(
    (value: number) => {
      switch (placement) {
        case "block-start":
          return { bottom: value }
        case "block-end":
          return { top: value }
        case "inline-start":
          return { right: value }
        case "inline-end":
          return { left: value }
      }
    },
    [placement],
  )

  const onDragEnd: DragEndEventHandler = useCallback(
    (_, info) => {
      switch (placement) {
        case "block-start":
          if (
            info.velocity.y <= dragVelocity * -1 ||
            info.offset.y <= dragOffset * -1
          )
            onClose()
          break

        case "block-end":
          if (info.velocity.y >= dragVelocity || info.offset.y >= dragOffset)
            onClose()
          break

        case "inline-start":
          if (
            info.velocity.x <= dragVelocity * -1 ||
            info.offset.x <= dragOffset * -1
          )
            onClose()
          break

        case "inline-end":
          if (info.velocity.x >= dragVelocity || info.offset.x >= dragOffset)
            onClose()
          break
      }
    },
    [placement, dragVelocity, dragOffset, onClose],
  )

  const getContentProps: PropGetter<HTMLMotionProps<"section">> = useCallback(
    (props = {}) => ({
      drag,
      dragConstraints: getDragRestriction(dragConstraints),
      dragElastic: getDragRestriction(dragElastic),
      dragMomentum: false,
      dragSnapToOrigin: true,
      ...cast<HTMLMotionProps<"section">>(
        getModalContentProps(cast<HTMLProps<"section">>(props)),
      ),
      onDragEnd: handlerAll(props.onDragEnd, onDragEnd),
    }),
    [
      drag,
      getDragRestriction,
      dragConstraints,
      dragElastic,
      onDragEnd,
      getModalContentProps,
    ],
  )

  const getDragBarProps: PropGetter = useCallback(
    (props) => ({
      ...props,
    }),
    [],
  )

  return {
    ...modalProps,
    closeOnDrag,
    getContentProps,
    getDragBarProps,
    onClose,
  }
}

export type UseDrawerReturn = ReturnType<typeof useDrawer>
