import { defineComponentStyle } from "@yamada-ui/react"

export const floatStyle = defineComponentStyle({
  base: {
    alignItems: "center",
    boxSize: "fit-content",
    display: "inline-flex",
    justifyContent: "center",
    position: "absolute",
  },

  props: {
    /**
     * The placement of the float.
     *
     * @default 'start-end'
     */
    placement: {
      center: {},
      "center-center": {},
      "center-end": {},
      "center-start": {},
      end: {},
      "end-center": {},
      "end-end": {},
      "end-start": {},
      start: {},
      "start-center": {},
      "start-end": {},
      "start-start": {},
    },
  },

  compounds: [
    {
      css: {
        insetBlockEnd: "auto",
        insetBlockStart: "{offset-block, 0px}",
        translateY: "-50%",
      },
      placement: /^start/,
    },
    {
      css: {
        insetBlockEnd: "{offset-block, 0px}",
        insetBlockStart: "auto",
        translateY: "50%",
      },
      placement: /^end/,
    },
    {
      css: {
        insetBlock: "50%",
        translateY: "-50%",
      },
      placement: /^center/,
    },
    {
      css: {
        insetInlineEnd: "auto",
        insetInlineStart: "{offset-inline, 0px}",
        translateX: "-50%",
      },
      placement: /start$/,
    },
    {
      css: {
        insetInlineEnd: "{offset-inline, 0px}",
        insetInlineStart: "auto",
        translateX: "50%",
      },
      placement: /end$/,
    },
    {
      css: {
        insetInline: "50%",
        translateX: "-50%",
      },
      placement: /center$/,
    },
  ],

  defaultProps: {
    placement: "start-end",
  },
})

export type FloatStyle = typeof floatStyle

"use client"

import type { CSSProps, HTMLStyledProps, ThemeProps } from "@yamada-ui/react"
import type { FloatStyle } from "./float.style"
import { createComponent, varAttr, isArray } from "@yamada-ui/react"
import { floatStyle } from "./float.style"

export interface FloatProps
  extends Omit<HTMLStyledProps, "offset">,
    ThemeProps<FloatStyle> {
  /**
   * Changes position offset.
   */
  offset?: CSSProps["inset"]
}

const {
  PropsContext: FloatPropsContext,
  usePropsContext: useFloatPropsContext,
  withContext,
} = createComponent<FloatProps, FloatStyle>("float", floatStyle)

export { FloatPropsContext, useFloatPropsContext }

/**
 * `Float` is a component used to anchor an element to the edge of a container.
 *
 * @see https://yamada-ui.com/docs/components/float
 */
export const Float = withContext("div")(undefined, ({ offset, ...rest }) => {
  const [offsetBlock, offsetInline] = isArray(offset)
    ? offset
    : [offset, offset]

  return {
    "--offset-block": varAttr(offsetBlock, "spaces"),
    "--offset-inline": varAttr(offsetInline, "spaces"),
    ...rest,
  }
})
