import { defineComponentStyle } from "@yamada-ui/react"

export const formatByteStyle = defineComponentStyle({})

export type FormatByteStyle = typeof formatByteStyle

"use client"

import type { HTMLStyledProps, ThemeProps } from "@yamada-ui/react"
import type { FormatByteStyle } from "./format-byte.style"
import { createComponent, styled } from "@yamada-ui/react"
import { formatByteStyle } from "./format-byte.style"
import { useFormatByte } from "./use-format-byte"

export interface FormatByteProps
  extends HTMLStyledProps,
    ThemeProps<FormatByteStyle> {
  /**
   * The byte size to format
   */
  value: number
  /**
   * The locale string to use for formatting.
   *
   * @default 'en-US'
   */
  locale?: string
  /**
   * The unit granularity to display
   */
  unit?: "bit" | "byte"
  /**
   * The unit display
   */
  unitDisplay?: "long" | "narrow" | "short"
}

const {
  PropsContext: FormatBytePropsContext,
  usePropsContext: useFormatBytePropsContext,
  withContext,
} = createComponent<FormatByteProps, FormatByteStyle>(
  "format-byte",
  formatByteStyle,
)

export { FormatBytePropsContext, useFormatBytePropsContext }

/**
 * `FormatByte` is used to format bytes to a human-readable format.
 *
 * @see https://yamada-ui.com/components/format-byte
 */
export const FormatByte = withContext(
  ({ locale, unit, unitDisplay, value, ...rest }) => {
    const text = useFormatByte(value, { locale, unit, unitDisplay })

    return <styled.span {...rest}>{text}</styled.span>
  },
)()

"use client"

import { useI18n } from "../../providers/i18n-provider"
import { useFormatNumber } from "../format-number"

const bitPrefixes = ["", "kilo", "mega", "giga", "tera"]
const bytePrefixes = ["", "kilo", "mega", "giga", "tera", "peta"]

export interface FormatBytesOptions {
  locale?: string
  unit?: "bit" | "byte"
  unitDisplay?: "long" | "narrow" | "short"
}

/**
 * `useFormatByte` is a custom hook that returns the formatted byte.
 *
 * @see https://yamada-ui.com/hooks/use-format-byte
 */
export const useFormatByte = (
  bytes: number,
  { locale, unit = "byte", unitDisplay = "short" }: FormatBytesOptions = {},
) => {
  const { locale: defaultLocale } = useI18n()

  locale ??= defaultLocale

  const sanitizedBytes = Number.isNaN(bytes) ? 0 : bytes
  const prefix = unit === "bit" ? bitPrefixes : bytePrefixes
  const index = Math.max(
    0,
    Math.min(Math.floor(Math.log10(sanitizedBytes) / 3), prefix.length - 1),
  )
  const value =
    sanitizedBytes === 0
      ? 0
      : Number.parseFloat((sanitizedBytes / 10 ** (3 * index)).toPrecision(3))

  return useFormatNumber(value, {
    style: "unit",
    locale,
    unit: prefix[index] + unit,
    unitDisplay,
  })
}

export type UseFormatByteReturn = ReturnType<typeof useFormatByte>
