import { defineComponentSlotStyle } from "@yamada-ui/react"

export const nativeTableStyle = defineComponentSlotStyle({
  base: {
    caption: {
      "&[data-placement='end']": {
        mt: "sm",
      },
      "&[data-placement='start']": {
        mb: "sm",
      },
      color: "fg.muted",
      fontSize: "xs",
      fontWeight: "medium",
      textAlign: "center",
    },
    col: {},
    colgroup: {},
    root: {
      borderCollapse: "collapse",
      fontVariantNumeric: "lining-nums tabular-nums",
      h: "full",
      w: "full",
    },
    scrollArea: {
      display: "block",
      maxW: "full",
      overflow: "auto",
      WebkitOverflowScrolling: "touch",
      whiteSpace: "nowrap",
    },
    tbody: {},
    td: {
      verticalAlign: "top",
      _numeric: {
        textAlign: "end",
      },
    },
    tfoot: {},
    th: {
      fontWeight: "medium",
      verticalAlign: "middle",
      _numeric: {
        textAlign: "end",
      },
    },
    thead: {},
    tr: {
      _disabled: {
        layerStyle: "disabled",
      },
    },
  },

  props: {
    /**
     * If `true`, highlight the row when the table row is hovered.
     *
     * @default false
     */
    highlightOnHover: {
      true: {
        tbody: {
          "& tr": {
            cursor: "pointer",
            transitionDuration: "moderate",
            transitionProperty: "common",
            _hover: {
              bg: "colorScheme.bg",
            },
          },
        },
      },
    },
    /**
     * If `true`, highlight the row when the table row is selected.
     *
     * @default false
     */
    highlightOnSelected: {
      true: {
        tbody: {
          "& tr": {
            _selected: {
              bg: "colorScheme.bg",
            },
          },
        },
      },
    },
    /**
     * If `true`, display the sticky footer.
     *
     * @default false
     */
    stickyFooter: {
      true: {
        root: {
          borderCollapse: "separate",
        },
        tfoot: {
          "& tr": {
            bottom: "0",
            position: "sticky",
            zIndex: "yamcha",
          },
        },
      },
    },
    /**
     * If `true`, display the sticky header.
     *
     * @default false
     */
    stickyHeader: {
      true: {
        root: {
          borderCollapse: "separate",
        },
        thead: {
          "& tr": {
            position: "sticky",
            top: "0",
            zIndex: "yamcha",
          },
        },
      },
    },
    /**
     * If `true`, display striped rows.
     *
     * @default false
     */
    striped: {
      true: {},
    },
    /**
     * If `true`, display the outer border of the table.
     *
     * @default false
     */
    withBorder: {
      true: {
        root: { borderWidth: "1px" },
      },
    },
    /**
     * If `true`, display line on the columns of the table.
     *
     * @default false
     */
    withColumnBorders: {
      true: {
        tr: {
          _notLastChild: { borderRightWidth: "1px" },
        },
      },
    },
  },

  variants: {
    line: {
      tbody: {
        "& tr": {
          _last: {
            "& th, td": {
              borderBottomWidth: 0,
            },
          },
        },
      },
      td: {
        borderBottomWidth: "1px",
      },
      tfoot: {
        "& tr": {
          "& th": {
            borderBottomWidth: 0,
            borderTopWidth: "1px",
          },
        },
      },
      th: {
        borderBottomWidth: "1px",
      },
      tr: {
        bg: "bg",
      },
    },
    outline: {
      root: {
        borderWidth: "1px",
      },
      scrollArea: {
        borderWidth: "1px",
      },
      tbody: {
        "& tr": {
          _last: {
            "& th, td": {
              borderBottomWidth: 0,
            },
          },
        },
      },
      td: {
        borderBottomWidth: "1px",
      },
      tfoot: {
        "& tr": {
          "& th": {
            borderBottomWidth: 0,
            borderTopWidth: "1px",
          },
          bg: "colorScheme.bg",
        },
      },
      th: {
        borderBottomWidth: "1px",
      },
      thead: {
        "& tr": {
          bg: "colorScheme.bg",
        },
      },
    },
  },

  sizes: {
    sm: {
      root: {
        fontSize: "xs",
      },
      td: {
        px: "2",
        py: "1",
      },
      th: {
        px: "2",
        py: "1",
      },
    },
    md: {
      root: {
        fontSize: "sm",
      },
      td: {
        px: "3",
        py: "2",
      },
      th: {
        px: "3",
        py: "2",
      },
    },
    lg: {
      root: {
        fontSize: "md",
      },
      td: {
        px: "4",
        py: "3",
      },
      th: {
        px: "4",
        py: "3",
      },
    },
  },

  compounds: [
    {
      css: {
        tbody: {
          "& tr": {
            _odd: {
              bg: "colorScheme.bg",
            },
          },
        },
      },
      variant: "line",
      layer: "props",
      striped: true,
    },
    {
      css: {
        tbody: {
          "& tr": {
            _even: {
              bg: "colorScheme.bg",
            },
          },
        },
      },
      variant: "outline",
      layer: "props",
      striped: true,
    },
    {
      css: {
        root: {
          borderWidth: "0",
        },
      },
      variant: "outline",
      withScrollArea: true,
    },
  ],

  defaultProps: {
    size: "md",
    variant: "line",
  },
})

export type NativeTableStyle = typeof nativeTableStyle

"use client"

import type {
  CSSProps,
  HTMLStyledProps,
  SimpleDirection,
  ThemeProps,
} from "@yamada-ui/react"
import type { NativeTableStyle } from "./native-table.style"
import { createSlotComponent, styled } from "@yamada-ui/react"
import { nativeTableStyle } from "./native-table.style"

export interface NativeTableRootProps
  extends HTMLStyledProps<"table">,
    ThemeProps<NativeTableStyle> {
  /**
   * The CSS `table-layout` property.
   */
  layout?: CSSProps["tableLayout"]
  /**
   * Whether to enable the scroll area.
   *
   * @default false
   */
  withScrollArea?: boolean
  /**
   * The props for the scroll area.
   */
  scrollAreaProps?: NativeTableAreaProps
}

const {
  PropsContext: NativeTablePropsContext,
  usePropsContext: useNativeTablePropsContext,
  withContext,
  withProvider,
} = createSlotComponent<NativeTableRootProps, NativeTableStyle>(
  "native-table",
  nativeTableStyle,
)

export { NativeTablePropsContext, useNativeTablePropsContext }

/**
 * `NativeTable` is a component for efficiently organizing and displaying data.
 *
 * @see https://yamada-ui.com/components/native-table
 */
export const NativeTableRoot = withProvider(
  ({ withScrollArea, scrollAreaProps, ...rest }) => {
    if (withScrollArea) {
      return (
        <NativeTableScrollArea {...scrollAreaProps}>
          <styled.table {...rest} />
        </NativeTableScrollArea>
      )
    } else {
      return <styled.table {...rest} />
    }
  },
  "root",
)(undefined, ({ layout: tableLayout, ...rest }) => ({
  tableLayout,
  ...rest,
}))

interface NativeTableAreaProps
  extends HTMLStyledProps,
    ThemeProps<NativeTableStyle> {}

const NativeTableScrollArea = withContext<"div", NativeTableAreaProps>(
  "div",
  "scrollArea",
)()

export interface CaptionProps extends HTMLStyledProps<"caption"> {
  /**
   * The placement of the table caption.
   *
   * @default 'end'
   */
  placement?: SimpleDirection
}

export const Caption = withContext<"caption", CaptionProps>(
  "caption",
  "caption",
)(undefined, ({ placement = "end", ...rest }) => ({
  "data-placement": placement,
  captionSide: placement === "end" ? "bottom" : "top",
  ...rest,
}))

export interface TheadProps extends HTMLStyledProps<"thead"> {}

export const Thead = withContext<"thead", TheadProps>("thead", "thead")()

export interface TbodyProps extends HTMLStyledProps<"tbody"> {}

export const Tbody = withContext<"tbody", TbodyProps>("tbody", "tbody")()

export interface TfootProps extends HTMLStyledProps<"tfoot"> {}

export const Tfoot = withContext<"tfoot", TfootProps>("tfoot", "tfoot")()

export interface ColgroupProps extends HTMLStyledProps<"colgroup"> {}

export const Colgroup = withContext<"colgroup", ColgroupProps>(
  "colgroup",
  "colgroup",
)()

export interface ColProps extends HTMLStyledProps<"col"> {}

export const Col = withContext<"col", ColProps>("col", "col")()

export interface ThProps extends HTMLStyledProps<"th"> {
  /**
   * Aligns the cell content to the right.
   *
   * @default false
   */
  numeric?: boolean
}

export const Th = withContext<"th", ThProps>("th", "th")(
  undefined,
  ({ numeric, ...rest }) => ({
    "data-numeric": numeric,
    ...rest,
  }),
)

export interface TrProps extends HTMLStyledProps<"tr"> {}

export const Tr = withContext<"tr", TrProps>("tr", "tr")()

export interface TdProps extends HTMLStyledProps<"td"> {
  /**
   * Aligns the cell content to the right.
   *
   * @default false
   */
  numeric?: boolean
}

export const Td = withContext<"td", TdProps>("td", "td")(
  undefined,
  ({ numeric, ...rest }) => ({
    "data-numeric": numeric,
    ...rest,
  }),
)
