import { defineComponentSlotStyle } from "@yamada-ui/react"
import { buttonStyle } from "../button"
import {
  getInputHeightStyle,
  getInputPaddingStartResetStyle,
  inputStyle,
} from "../input"

export const numberInputStyle = defineComponentSlotStyle({
  base: {
    button: {
      ...buttonStyle.base,
      flex: "1",
      lineHeight: "1",
      rounded: "l1",
    },
    control: {
      display: "flex",
      flexDirection: "column",
    },
    decrement: {},
    field: inputStyle.base,
    increment: {},
    root: {},
  },

  variants: {
    base: {
      button: {
        layerStyle: "ghost",
        focusVisibleRing: "none",
        _hover: {
          layerStyle: "ghost.hover",
        },
        _focusVisible: {
          layerStyle: "ghost.hover",
        },
      },
    },

    filled: {
      field: inputStyle.variants?.filled,
    },
    flushed: {
      field: inputStyle.variants?.flushed,
      root: getInputPaddingStartResetStyle("& > input"),
    },
    outline: {
      field: inputStyle.variants?.outline,
    },
    plain: {
      field: inputStyle.variants?.plain,
    },
  },

  sizes: {
    xs: {
      control: {
        boxSize: `calc({--height} - {spaces.2})`,
        fontSize: inputStyle.sizes?.xs.fontSize,
      },
      field: inputStyle.sizes?.xs,
      root: getInputHeightStyle(inputStyle.sizes?.xs.minH, "& > input"),
    },
    sm: {
      control: {
        boxSize: `calc({--height} - {spaces.2})`,
        fontSize: inputStyle.sizes?.sm.fontSize,
      },
      field: inputStyle.sizes?.sm,
      root: getInputHeightStyle(inputStyle.sizes?.sm.minH, "& > input"),
    },
    md: {
      control: {
        boxSize: `calc({--height} - {spaces.2})`,
        fontSize: inputStyle.sizes?.md.fontSize,
      },
      field: inputStyle.sizes?.md,
      root: getInputHeightStyle(inputStyle.sizes?.md.minH, "& > input"),
    },
    lg: {
      control: {
        boxSize: `calc({--height} - {spaces.2.5})`,
        fontSize: inputStyle.sizes?.lg.fontSize,
      },
      field: inputStyle.sizes?.lg,
      root: getInputHeightStyle(inputStyle.sizes?.lg.minH, "& > input"),
    },
    xl: {
      control: {
        boxSize: `calc({--height} - {spaces.3})`,
        fontSize: inputStyle.sizes?.xl.fontSize,
      },
      field: inputStyle.sizes?.xl,
      root: getInputHeightStyle(inputStyle.sizes?.xl.minH, "& > input"),
    },
    "2xl": {
      control: {
        boxSize: `calc({--height} - {spaces.3})`,
        fontSize: inputStyle.sizes?.["2xl"].fontSize,
      },
      field: inputStyle.sizes?.["2xl"],
      root: getInputHeightStyle(inputStyle.sizes?.["2xl"]?.minH, "& > input"),
    },
  },

  defaultProps: {
    size: "md",
    variant: "outline",
  },
})

export type NumberInputStyle = typeof numberInputStyle

"use client"

import type {
  HTMLStyledProps,
  ThemeProps,
  WithoutThemeProps,
} from "@yamada-ui/react"
import type { InputProps } from "../input"
import type { NumberInputStyle } from "./number-input.style"
import type { UseNumberInputProps } from "./use-number-input"
import { createSlotComponent } from "@yamada-ui/react"
import { useGroupItemProps } from "../group"
import { ChevronDownIcon, ChevronUpIcon } from "../icon"
import { Input, InputGroup, useInputPropsContext } from "../input"
import { numberInputStyle } from "./number-input.style"
import { useNumberInput } from "./use-number-input"

export interface NumberInputProps
  extends
    Omit<
      WithoutThemeProps<InputProps, NumberInputStyle>,
      keyof UseNumberInputProps
    >,
    ThemeProps<NumberInputStyle>,
    UseNumberInputProps {
  /**
   * The props for the control element.
   */
  controlProps?: NumberInputControlProps
  /**
   * The props for the decrement button element.
   */
  decrementProps?: NumberInputDecrementButtonProps
  /**
   * The props for the end element.
   */
  elementProps?: InputGroup.ElementProps
  /**
   * The props for the increment button element.
   */
  incrementProps?: NumberInputIncrementButtonProps
  /**
   * The props for the root element.
   */
  rootProps?: InputGroup.RootProps
}

const {
  PropsContext: NumberInputPropsContext,
  usePropsContext: useNumberInputPropsContext,
  withContext,
  withProvider,
} = createSlotComponent<NumberInputProps, NumberInputStyle>(
  "number-input",
  numberInputStyle,
)

export { NumberInputPropsContext, useNumberInputPropsContext }

/**
 * `NumberInput` is a component used to obtain numeric input from the user.
 *
 * @see https://yamada-ui.com/docs/components/number-input
 */
export const NumberInput = withProvider<"input", NumberInputProps>(
  ({
    className,
    css,
    colorScheme,
    controlProps,
    decrementProps,
    elementProps,
    incrementProps,
    rootProps,
    ...props
  }) => {
    const [groupItemProps, rest] = useGroupItemProps(props)
    const { getDecrementProps, getIncrementProps, getInputProps } =
      useNumberInput(rest)

    return (
      <InputGroup.Root
        className={className}
        css={css}
        colorScheme={colorScheme}
        {...groupItemProps}
        {...rootProps}
      >
        <NumberInputField {...getInputProps()} />

        <InputGroup.Element clickable {...elementProps}>
          <NumberInputControl {...controlProps}>
            <NumberInputIncrementButton
              {...getIncrementProps(incrementProps)}
            />
            <NumberInputDecrementButton
              {...getDecrementProps(decrementProps)}
            />
          </NumberInputControl>
        </InputGroup.Element>
      </InputGroup.Root>
    )
  },
  "root",
)((props) => {
  const context = useInputPropsContext()

  return { ...context, ...props }
})

interface NumberInputFieldProps extends InputProps {}

const NumberInputField = withContext<"input", NumberInputFieldProps>(
  Input,
  "field",
)({ "data-group-propagate": "" })

interface NumberInputControlProps extends InputGroup.AddonProps {}

const NumberInputControl = withContext<"div", NumberInputControlProps>(
  "div",
  "control",
)()

type NumberInputIncrementButtonProps = HTMLStyledProps<"button">

const NumberInputIncrementButton = withContext<
  "button",
  NumberInputIncrementButtonProps
>("button", ["button", "increment"])(({ children, ...rest }) => ({
  children: children ?? <ChevronUpIcon />,
  ...rest,
}))

type NumberInputDecrementButtonProps = HTMLStyledProps<"button">

const NumberInputDecrementButton = withContext<
  "button",
  NumberInputDecrementButtonProps
>("button", ["button", "decrement"])(({ children, ...rest }) => ({
  children: children ?? <ChevronDownIcon />,
  ...rest,
}))

"use client"

import type { HTMLProps, PropGetter } from "@yamada-ui/react"
import type { UseCounterProps, UseCounterReturn } from "../../hooks/use-counter"
import type { UseSpinnerProps } from "./use-spinner"
import { useCallback, useRef } from "react"
import { useI18n } from "../../providers/i18n-provider"
import { handlerAll, mergeRefs, useAttributeObserver } from "@yamada-ui/react"
import { useSpinner } from "./use-spinner"

export interface UseNumberCounterProps
  extends
    HTMLProps<"button">,
    Pick<UseCounterProps, "keepWithinRange">,
    Pick<UseCounterReturn, "max" | "min">,
    UseSpinnerProps {}

export const useNumberCounter = ({
  decrement,
  disabled,
  increment,
  keepWithinRange,
  max,
  min,
  ...rest
}: UseNumberCounterProps) => {
  const incrementRef = useRef<HTMLButtonElement>(null)
  const decrementRef = useRef<HTMLButtonElement>(null)
  const { down, spinning, stop, up } = useSpinner({ decrement, increment })
  const { t } = useI18n("numberInput")

  useAttributeObserver(incrementRef, ["disabled"], spinning, stop)
  useAttributeObserver(decrementRef, ["disabled"], spinning, stop)

  const getButtonProps: PropGetter<"button"> = useCallback(
    (props = {}) => ({
      type: "button",
      disabled,
      tabIndex: -1,
      ...rest,
      ...props,
      onPointerLeave: handlerAll(props.onPointerLeave, stop),
      onPointerUp: handlerAll(props.onPointerUp, stop),
    }),
    [rest, disabled, stop],
  )

  const getIncrementProps: PropGetter<"button"> = useCallback(
    ({ ref, ...props } = {}) => {
      const trulyDisabled = disabled || (keepWithinRange && max)

      return {
        ref: mergeRefs(ref, incrementRef),
        "aria-label": t("Increase"),
        ...getButtonProps({ disabled: trulyDisabled, ...props }),
        onPointerDown: handlerAll(props.onPointerDown, (ev) => {
          if (ev.button !== 0 || trulyDisabled) return

          ev.preventDefault()
          up()
        }),
      }
    },
    [getButtonProps, disabled, keepWithinRange, max, up, t],
  )

  const getDecrementProps: PropGetter<"button"> = useCallback(
    ({ ref, ...props } = {}) => {
      const trulyDisabled = disabled || (keepWithinRange && min)

      return {
        ref: mergeRefs(ref, decrementRef),
        "aria-label": t("Decrease"),
        ...getButtonProps({ disabled: trulyDisabled, ...props }),
        onPointerDown: handlerAll(props.onPointerDown, (ev) => {
          if (ev.button !== 0 || trulyDisabled) return

          ev.preventDefault()
          down()
        }),
      }
    },
    [getButtonProps, disabled, keepWithinRange, min, down, t],
  )

  return { getDecrementProps, getIncrementProps }
}

export type UseNumberCounterReturn = ReturnType<typeof useNumberCounter>

"use client"

import type { ChangeEvent, FocusEvent, KeyboardEvent } from "react"
import type { HTMLProps, PropGetter } from "@yamada-ui/react"
import type { UseCounterProps } from "../../hooks/use-counter"
import type { FieldProps } from "../field"
import { useCallback, useMemo, useRef } from "react"
import { useCounter } from "../../hooks/use-counter"
import { useEventListener } from "../../hooks/use-event-listener"
import {
  ariaAttr,
  handlerAll,
  isComposing,
  mergeRefs,
  runKeyAction,
  useSafeLayoutEffect,
} from "@yamada-ui/react"
import { useFieldProps } from "../field"
import { useNumberCounter } from "./use-number-counter"

const defaultFormat = (value: number | string) => value.toString()

const defaultParse = (value: string) => value

const isDefaultValidCharacter = (char: string) => /^[Ee0-9+\-.]$/.test(char)

const isValidNumericKeyboardEvent = (
  { key, altKey, ctrlKey, metaKey }: KeyboardEvent,
  isValid: (key: string) => boolean,
) => {
  const modifierKey = ctrlKey || altKey || metaKey
  const singleCharacterKey = key.length === 1

  if (!singleCharacterKey || modifierKey) return true

  return isValid(key)
}

const getStepRatio = <Y extends KeyboardEvent | WheelEvent>({
  ctrlKey,
  metaKey,
  shiftKey,
}: Y) => {
  let ratio = 1

  if (metaKey || ctrlKey) ratio = 0.1

  if (shiftKey) ratio = 10

  return ratio
}

export interface UseNumberInputProps
  extends
    Omit<HTMLProps<"input">, keyof UseCounterProps>,
    UseCounterProps,
    FieldProps {
  /**
   * If `true`, the input's value will change based on mouse wheel.
   *
   * @default false
   */
  allowMouseWheel?: boolean
  /**
   * This controls the value update when you blur out of the input.
   * - If `true` and the value is greater than `max`, the value will be reset to `max`.
   * - Else, the value remains the same.
   *
   * @default true
   */
  clampValueOnBlur?: boolean
  /**
   * If `true`, the input will be focused as you increment or decrement the value with the button.
   *
   * @default true
   */
  focusInputOnChange?: boolean
  /**
   * If using a custom display format, this converts the default format to the custom format.
   */
  format?: (value: number | string) => string
  /**
   * This is used to format the value so that screen readers
   * can speak out a more human-friendly value.
   *
   * It is used to set the `aria-valuetext` property of the input.
   */
  getAriaValueText?: (value: number | string) => string | undefined
  /**
   * Whether the pressed key should be allowed in the input.
   * The default behavior is to allow DOM floating point characters defined by /^[Ee0-9+\-.]$/.
   */
  isValidCharacter?: (value: string) => boolean
  /**
   * If using a custom display format, this converts the custom format to a format `parseFloat` understands.
   */
  parse?: (value: string) => string
}

export const useNumberInput = (props: UseNumberInputProps = {}) => {
  const {
    props: {
      allowMouseWheel,
      clampValueOnBlur = true,
      defaultValue,
      disabled,
      focusInputOnChange = true,
      format = defaultFormat,
      getAriaValueText,
      isValidCharacter = isDefaultValidCharacter,
      keepWithinRange = true,
      max: maxValue = Number.MAX_SAFE_INTEGER,
      min: minValue = Number.MIN_SAFE_INTEGER,
      parse = defaultParse,
      precision,
      readOnly,
      step = 1,
      value: valueProp,
      onChange: onChangeProp,
      ...rest
    },
    ariaProps,
    dataProps,
    eventProps,
  } = useFieldProps(props)
  const interactive = !(readOnly || disabled)
  const inputRef = useRef<HTMLInputElement>(null)
  const {
    cast,
    max,
    min,
    out,
    setValue,
    update,
    value,
    valueAsNumber,
    ...counter
  } = useCounter({
    defaultValue,
    keepWithinRange,
    max: maxValue,
    min: minValue,
    precision,
    step,
    value: valueProp,
    onChange: onChangeProp,
  })
  const selectionRef = useRef<null | {
    end: null | number
    start: null | number
  }>(null)
  const valueText = useMemo(() => {
    let text = getAriaValueText?.(value)

    if (text != null) return text

    text = value.toString()

    return !text ? undefined : text
  }, [value, getAriaValueText])

  const sanitize = useCallback(
    (value: string) => value.split("").filter(isValidCharacter).join(""),
    [isValidCharacter],
  )

  const increment = useCallback(
    (value: number = step) => {
      if (!interactive) return

      counter.increment(value)

      if (!focusInputOnChange) return

      requestAnimationFrame(() => {
        inputRef.current?.focus()
      })
    },
    [interactive, counter, step, focusInputOnChange],
  )

  const decrement = useCallback(
    (value: number = step) => {
      if (!interactive) return

      counter.decrement(value)

      if (!focusInputOnChange) return

      requestAnimationFrame(() => {
        inputRef.current?.focus()
      })
    },
    [interactive, counter, step, focusInputOnChange],
  )

  const onChange = useCallback(
    (ev: ChangeEvent<HTMLInputElement>) => {
      if (isComposing(ev)) return

      const { selectionEnd, selectionStart, value } = ev.currentTarget

      update(sanitize(parse(value)))

      selectionRef.current = { end: selectionEnd, start: selectionStart }
    },
    [parse, sanitize, update],
  )

  const onFocus = useCallback((ev: FocusEvent<HTMLInputElement>) => {
    if (!selectionRef.current) return

    const { end, start } = selectionRef.current
    const { selectionStart, value } = ev.currentTarget

    ev.currentTarget.selectionStart = start ?? value.length
    ev.currentTarget.selectionEnd = end ?? selectionStart
  }, [])

  const onBlur = useCallback(() => {
    if (!clampValueOnBlur) return

    let nextValue = value as number | string

    if (value === "") return

    const valueStartsWithE = /^[eE]/.test(value.toString())

    if (valueStartsWithE) {
      setValue("")
    } else {
      if (valueAsNumber < minValue) nextValue = minValue

      if (valueAsNumber > maxValue) nextValue = maxValue

      cast(nextValue)
    }
  }, [
    cast,
    clampValueOnBlur,
    maxValue,
    minValue,
    setValue,
    value,
    valueAsNumber,
  ])

  const onKeyDown = useCallback(
    (ev: KeyboardEvent<HTMLInputElement>) => {
      if (isComposing(ev)) return

      if (!isValidNumericKeyboardEvent(ev, isValidCharacter))
        ev.preventDefault()

      const stepValue = getStepRatio(ev) * step

      runKeyAction(ev, {
        ArrowDown: () => decrement(stepValue),
        ArrowUp: () => increment(stepValue),
        End: () => update(maxValue),
        Home: () => update(minValue),
      })
    },
    [decrement, increment, isValidCharacter, maxValue, minValue, step, update],
  )

  const { getDecrementProps, getIncrementProps } = useNumberCounter({
    "aria-disabled": ariaAttr(!interactive),
    decrement,
    disabled,
    increment,
    keepWithinRange,
    max,
    min,
    ...dataProps,
  })

  useSafeLayoutEffect(() => {
    if (!inputRef.current) return

    const notInSync = inputRef.current.value != value

    if (!notInSync) return

    setValue(sanitize(parse(inputRef.current.value)))
  }, [parse, sanitize])

  useEventListener(
    inputRef.current,
    "wheel",
    (ev) => {
      const ownerDocument = inputRef.current?.ownerDocument ?? document
      const focused = ownerDocument.activeElement === inputRef.current

      if (!allowMouseWheel || !focused) return

      ev.preventDefault()

      const stepValue = getStepRatio(ev) * step
      const direction = Math.sign(ev.deltaY)

      if (direction === -1) {
        increment(stepValue)
      } else if (direction === 1) {
        decrement(stepValue)
      }
    },
    { passive: false },
  )

  const getInputProps: PropGetter<"input"> = useCallback(
    ({ ref, ...props } = {}) => ({
      ...ariaProps,
      ...dataProps,
      type: "text",
      "aria-invalid": ariaAttr(ariaProps["aria-invalid"] ?? out),
      "aria-valuemax": maxValue,
      "aria-valuemin": minValue,
      "aria-valuenow": Number.isNaN(valueAsNumber) ? undefined : valueAsNumber,
      "aria-valuetext": valueText,
      autoComplete: "off",
      autoCorrect: "off",
      disabled,
      inputMode: "decimal",
      max: maxValue,
      min: minValue,
      pattern: "[0-9]*(.[0-9]+)?",
      readOnly,
      role: "spinbutton",
      step,
      value: format(value),
      ...rest,
      ...props,
      ref: mergeRefs(ref, rest.ref, inputRef),
      onBlur: handlerAll(eventProps.onBlur, props.onBlur, onBlur),
      onChange: handlerAll(props.onChange, onChange),
      onFocus: handlerAll(eventProps.onFocus, props.onFocus, onFocus),
      onKeyDown: handlerAll(rest.onKeyDown, props.onKeyDown, onKeyDown),
    }),
    [
      format,
      out,
      value,
      valueText,
      ariaProps,
      dataProps,
      eventProps,
      maxValue,
      minValue,
      valueAsNumber,
      disabled,
      readOnly,
      step,
      rest,
      onKeyDown,
      onBlur,
      onFocus,
      onChange,
    ],
  )

  return { getDecrementProps, getIncrementProps, getInputProps }
}

export type UseNumberInputReturn = ReturnType<typeof useNumberInput>

"use client"

import { useCallback, useRef, useState } from "react"
import { useInterval } from "../../hooks/use-interval"
import { useUnmountEffect } from "@yamada-ui/react"

const INTERVAL = 50
const DELAY = 300

export interface UseSpinnerProps {
  decrement: () => void
  increment: () => void
}

export const useSpinner = ({ decrement, increment }: UseSpinnerProps) => {
  const [spinning, setSpinning] = useState(false)
  const [action, setAction] = useState<"decrement" | "increment" | null>(null)
  const [once, setOnce] = useState(true)
  const timeoutRef = useRef<any>(null)

  useInterval(
    () => {
      if (action === "increment") increment()

      if (action === "decrement") decrement()
    },
    spinning ? INTERVAL : null,
  )

  const up = useCallback(() => {
    if (once) increment()

    timeoutRef.current = setTimeout(() => {
      setOnce(false)
      setSpinning(true)
      setAction("increment")
    }, DELAY)
  }, [increment, once])

  const down = useCallback(() => {
    if (once) decrement()

    timeoutRef.current = setTimeout(() => {
      setOnce(false)
      setSpinning(true)
      setAction("decrement")
    }, DELAY)
  }, [decrement, once])

  const removeTimeout = useCallback(() => clearTimeout(timeoutRef.current), [])

  const stop = useCallback(() => {
    setOnce(true)
    setSpinning(false)
    removeTimeout()
  }, [removeTimeout])

  useUnmountEffect(removeTimeout)

  return { down, spinning, stop, up }
}

export type UseSpinnerReturn = ReturnType<typeof useSpinner>
