import { defineComponentSlotStyle, focusRingStyle } from "@yamada-ui/react"
import { radioStyle } from "../radio"

export const radioCardStyle = defineComponentSlotStyle({
  base: {
    addon: {
      borderColor: "{--addon-border-color}",
      borderTopWidth: "1px",
      fontSize: "{description-size}",
      mt: "{space-y}",
      pt: "{space-y}",
      px: "{space-x}",
    },
    description: { color: "fg.muted", fontSize: "{description-size}" },
    group: { w: "full" },
    indicator: {
      ...radioStyle.base?.indicator,
      "input:focus-visible + &": {
        borderColor: "{focus-ring-color}",
      },
      position: "absolute",
      top: "calc({space-y} + ({label-size} * 1.5 - {label-size}) / 2)",
    },
    root: {
      ...radioStyle.base?.root,
      "&:has(input:focus-visible)": focusRingStyle.outline,
      "--addon-border-color": "{root-border-color}",
      "--root-border-color": "colors.border",
      alignItems: "stretch",
      borderColor: "{--root-border-color}",
      borderWidth: "1px",
      flexDirection: "column",
      focusRingColor: "{focus-border-color}",
      gap: "1",
      position: "relative",
      px: "{space-x}",
      py: "{space-y}",
      rounded: "l2",
      w: "full",
      _invalid: {
        borderColor: "{error-border-color}",
        focusRingColor: "{error-border-color}",
      },
    },
  },

  props: {
    /**
     * The justify indicator of the component
     *
     * @default 'start'
     */
    justify: {
      end: {
        addon: {
          me: "calc({bleed-size} * -1)",
          ms: "calc({space-x} * -1)",
        },
        indicator: { right: "{space-x}" },
        root: {
          "&:has([data-indicator])": {
            "--bleed-size": "calc({indicator-size} + ({space-x} * 2))",
          },
          "--bleed-size": "{space-x}",
          pe: "{bleed-size}",
        },
      },
      start: {
        addon: {
          me: "calc({space-x} * -1)",
          ms: "calc({bleed-size} * -1)",
        },
        indicator: { left: "{space-x}" },
        root: {
          "&:has([data-indicator])": {
            "--bleed-size": "calc({indicator-size} + ({space-x} * 2))",
          },
          "--bleed-size": "{space-x}",
          ps: "{bleed-size}",
        },
      },
    },
    /**
     * The shape of the component
     *
     * @default 'circle'
     */
    shape: { ...radioStyle.props?.shape },
  },

  variants: {
    base: { indicator: radioStyle.variants?.base.indicator },
    outline: {
      root: {
        _checked: {
          "--indicator-border-color": "colorScheme.outline",
          "--root-border-color": "colorScheme.outline",
        },
      },
    },
    subtle: {
      indicator: { _checked: { bg: "bg" } },
      root: {
        _checked: {
          "--addon-border-color": "colorScheme.muted",
          "--indicator-border-color": "colorScheme.emphasized",
          "--root-border-color": "transparent",
          bg: "colorScheme.subtle",
        },
      },
    },
    surface: {
      indicator: { _checked: { bg: "bg" } },
      root: {
        _checked: {
          "--indicator-border-color": "colorScheme.emphasized",
          "--root-border-color": "colorScheme.muted",
          bg: "colorScheme.subtle",
        },
      },
    },
  },

  sizes: {
    sm: {
      indicator: radioStyle.sizes?.sm.indicator,
      root: {
        ...radioStyle.sizes?.sm.root,
        "--description-size": "fontSizes.xs",
        "--space-x": "spaces.3",
        "--space-y": "spaces.2",
      },
    },
    md: {
      indicator: radioStyle.sizes?.md.indicator,
      root: {
        ...radioStyle.sizes?.md.root,
        "--description-size": "fontSizes.sm",
        "--space-x": "spaces.4",
        "--space-y": "spaces.3",
      },
    },
    lg: {
      indicator: radioStyle.sizes?.lg.indicator,
      root: {
        ...radioStyle.sizes?.lg.root,
        "--description-size": "fontSizes.md",
        "--space-x": "spaces.5",
        "--space-y": "spaces.4",
      },
    },
    xl: {
      indicator: radioStyle.sizes?.lg.indicator,
      root: {
        ...radioStyle.sizes?.lg.root,
        "--description-size": "fontSizes.md",
        "--space-x": "spaces.6",
        "--space-y": "spaces.5",
      },
    },
  },

  defaultProps: {
    size: "md",
    variant: "surface",
    justify: "start",
    shape: "circle",
  },
})

export type RadioCardStyle = typeof radioCardStyle

"use client"

import type { ReactElement, ReactNode } from "react"
import type {
  GenericsComponent,
  HTMLStyledProps,
  ThemeProps,
  Merge,
} from "@yamada-ui/react"
import type { UseInputBorderProps } from "../input"
import type { UseRadioProps } from "../radio"
import type { RadioCardStyle } from "./radio-card.style"
import { useMemo } from "react"
import { createSlotComponent, styled } from "@yamada-ui/react"
import { useInputBorder } from "../input"
import { useRadio } from "../radio"
import { radioCardStyle } from "./radio-card.style"

export interface RadioCardRootProps<Y extends string = string>
  extends Merge<HTMLStyledProps<"label">, UseRadioProps<Y>>,
    ThemeProps<RadioCardStyle>,
    UseInputBorderProps {
  /**
   * The addon of the radio card.
   */
  addon?: ReactNode
  /**
   * The description of the radio card.
   */
  description?: ReactNode
  /**
   * The label of the radio card.
   */
  label?: ReactNode
  /**
   * If `true`, the indicator will be displayed.
   *
   * @default true
   */
  withIndicator?: boolean
  /**
   * Props for the addon component.
   */
  addonProps?: RadioCardAddonProps
  /**
   * Props for the description component.
   */
  descriptionProps?: RadioCardDescriptionProps
  /**
   * Props for the indicator component.
   */
  indicatorProps?: RadioCardIndicatorProps
  /**
   * Props for the input element.
   */
  inputProps?: HTMLStyledProps<"input">
  /**
   * Props for the label component.
   */
  labelProps?: RadioCardLabelProps
  /**
   * Props for the label element.
   */
  rootProps?: HTMLStyledProps<"label">
}

const {
  component,
  PropsContext: RadioCardPropsContext,
  usePropsContext: useRadioCardPropsContext,
  withContext,
  withProvider,
  useRootComponentProps,
} = createSlotComponent<RadioCardRootProps, RadioCardStyle>(
  "radio-card",
  radioCardStyle,
)

export {
  component,
  RadioCardPropsContext,
  useRadioCardPropsContext,
  useRootComponentProps,
}

/**
 * `RadioCard` is a component used for allowing users to select one option from multiple choices.
 *
 * @see https://yamada-ui.com/components/radio-card
 */
export const RadioCardRoot = withProvider<"label", RadioCardRootProps>(
  ({
    addon,
    children,
    description,
    errorBorderColor,
    focusBorderColor,
    label,
    withIndicator = true,
    addonProps,
    descriptionProps,
    indicatorProps,
    inputProps,
    labelProps,
    rootProps,
    ...rest
  }) => {
    const { getIndicatorProps, getInputProps, getRootProps } = useRadio(rest)
    const varProps = useInputBorder({ errorBorderColor, focusBorderColor })
    const computedChildren = useMemo(() => {
      if (children) return children

      return (
        <>
          {label ? (
            <RadioCardLabel {...labelProps}>{label}</RadioCardLabel>
          ) : null}
          {description ? (
            <RadioCardDescription {...descriptionProps}>
              {description}
            </RadioCardDescription>
          ) : null}
          {addon ? (
            <RadioCardAddon {...addonProps}>{addon}</RadioCardAddon>
          ) : null}
        </>
      )
    }, [
      addon,
      addonProps,
      children,
      description,
      descriptionProps,
      label,
      labelProps,
    ])

    return (
      <styled.label {...getRootProps({ ...varProps, ...rootProps })}>
        <styled.input {...getInputProps(inputProps)} />
        {withIndicator ? (
          <RadioCardIndicator {...getIndicatorProps(indicatorProps)} />
        ) : null}

        {computedChildren}
      </styled.label>
    )
  },
  "root",
)() as GenericsComponent<{
  <Y extends string = string>(props: RadioCardRootProps<Y>): ReactElement
}>

interface RadioCardIndicatorProps extends HTMLStyledProps {}

const RadioCardIndicator = withContext<"div", RadioCardIndicatorProps>(
  "div",
  "indicator",
)({ "data-indicator": "" })

export interface RadioCardLabelProps extends HTMLStyledProps {}

export const RadioCardLabel = withContext<"span", RadioCardLabelProps>(
  "span",
  "label",
)()

export interface RadioCardDescriptionProps extends HTMLStyledProps {}

export const RadioCardDescription = withContext<
  "span",
  RadioCardDescriptionProps
>("span", "description")()

export interface RadioCardAddonProps extends HTMLStyledProps {}

export const RadioCardAddon = withContext<"span", RadioCardAddonProps>(
  "span",
  "addon",
)()
