export {
  ResizableItem as Item,
  ResizablePropsContext as PropsContext,
  ResizableRoot as Root,
  ResizableTrigger as Trigger,
  useResizablePropsContext as usePropsContext,
} from "./resizable"
export type {
  ResizableItemProps as ItemProps,
  ResizableRootProps as RootProps,
  ResizableTriggerProps as TriggerProps,
} from "./resizable"
export {
  ResizableContext as Context,
  useResizableContext as useContext,
} from "./use-resizable"
export type {
  ResizableGroupControl as GroupControl,
  ResizableItemControl as ItemControl,
  ResizableStorage as Storage,
} from "./use-resizable"

import { defineComponentSlotStyle } from "@yamada-ui/react"

export const resizableStyle = defineComponentSlotStyle({
  base: {
    icon: {
      alignItems: "center",
      display: "flex",
      justifyContent: "center",
      left: "50%",
      position: "absolute",
      top: "50%",
      translateX: "-50%",
      translateY: "-50%",
    },
    item: {
      boxSize: "full",
    },
    root: {
      boxSize: "full",
    },
    trigger: {
      position: "relative",
      transitionDuration: "moderate",
      transitionProperty: "common",
      _after: {
        transitionDuration: "moderate",
        transitionProperty: "common",
      },
    },
  },

  variants: {
    border: {
      icon: {
        bg: "colorScheme.muted",
        color: "colorScheme.fg",
        py: "1",
        rounded: "l1",
      },
      trigger: {
        bg: "colorScheme.muted",
        focusVisibleRing: "none",
        _after: {
          position: "absolute",
        },
        _hover: {
          bg: "colorScheme.emphasized",
        },
        _focusVisible: {
          bg: "colorScheme.emphasized",
        },
      },
    },
    plain: {
      trigger: {
        focusVisibleRing: "none",
        _after: {
          position: "absolute",
        },
      },
    },
    spacer: {
      icon: {
        color: "colorScheme.fg",
        transitionDuration: "moderate",
        transitionProperty: "common",
        _active: {
          color: "transparent",
        },
      },
      trigger: {
        focusVisibleRing: "none",
        p: "1",
        _after: {
          bg: "transparent",
          display: "block",
          rounded: "full",
        },
        _hover: {
          _after: {
            bg: "colorScheme.bg",
          },
          _icon: {
            color: "transparent",
          },
        },
        _focusVisible: {
          _after: {
            bg: "colorScheme.bg",
          },
          _icon: {
            color: "transparent",
          },
        },
      },
    },
  },

  props: {
    /**
     * The orientation of the resizable.
     *
     * @default 'horizontal'
     */
    orientation: {
      horizontal: {
        icon: {
          transform: "translate(-50%, -50%) rotate(0deg)",
        },
      },
      vertical: {
        icon: {
          transform: "translate(-50%, -50%) rotate(90deg)",
        },
      },
    },
  },

  compounds: [
    {
      css: {
        trigger: {
          h: "px",
          _after: {
            h: "2",
            left: "0",
            right: "0",
            transform: "translateY(-50%)",
          },
        },
      },
      variant: "border",
      orientation: "vertical",
    },
    {
      css: {
        trigger: {
          w: "px",
          _after: {
            bottom: "0",
            top: "0",
            transform: "translateX(-50%)",
            w: "2",
          },
        },
      },
      variant: "border",
      orientation: "horizontal",
    },
    {
      css: {
        trigger: {
          _after: { h: "2", w: "full" },
        },
      },
      variant: "spacer",
      orientation: "vertical",
    },
    {
      css: {
        trigger: {
          _after: { h: "full", w: "2" },
        },
      },
      variant: "spacer",
      orientation: "horizontal",
    },
    {
      css: {
        trigger: {
          _after: {
            h: "2",
            left: "0",
            right: "0",
            transform: "translateY(-50%)",
          },
        },
      },
      variant: "plain",
      orientation: "vertical",
    },
    {
      css: {
        trigger: {
          _after: {
            bottom: "0",
            top: "0",
            transform: "translateX(-50%)",
            w: "2",
          },
        },
      },
      variant: "plain",
      orientation: "horizontal",
    },
  ],

  defaultProps: {
    variant: "border",
    orientation: "horizontal",
  },
})

export type ResizableStyle = typeof resizableStyle

"use client"

import type { ForwardedRef, ReactElement } from "react"
import type {
  HTMLStyledProps,
  HTMLStyledPropsWithoutAs,
  ThemeProps,
} from "@yamada-ui/react"
import type { ResizableStyle } from "./resizable.style"
import type {
  UseResizableItemProps,
  UseResizableProps,
  UseResizableTriggerProps,
} from "./use-resizable"
import { useMemo } from "react"
import { Panel, PanelGroup, PanelResizeHandle } from "react-resizable-panels"
import { createSlotComponent, styled } from "@yamada-ui/react"
import { useValue } from "../../hooks/use-value"
import { resizableStyle } from "./resizable.style"
import {
  ResizableContext,
  useResizable,
  useResizableItem,
  useResizableTrigger,
} from "./use-resizable"

/**
 * `Resizable` is accessible resizable panel groups and layouts with keyboard support.
 *
 * @see https://yamada-ui.com/docs/components/resizable
 */
export interface ResizableRootProps
  extends HTMLStyledProps,
    ThemeProps<ResizableStyle>,
    Omit<UseResizableProps, "orientation"> {
  /**
   * Ref for resizable root element.
   */
  rootRef?: ForwardedRef<HTMLDivElement>
}

const {
  PropsContext: ResizablePropsContext,
  usePropsContext: useResizablePropsContext,
  withContext,
  withProvider,
} = createSlotComponent<ResizableRootProps, ResizableStyle>(
  "resizable",
  resizableStyle,
)

export { ResizablePropsContext, useResizablePropsContext }

export const ResizableRoot = withProvider<
  "div",
  ResizableRootProps,
  "orientation"
>(
  ({ children, orientation: orientationProp, rootRef, ...rest }) => {
    const computedOrientation = useValue(orientationProp)
    const { controlRef, disabled, orientation, getGroupProps, getRootProps } =
      useResizable({ orientation: computedOrientation, ...rest })

    const context = useMemo(
      () => ({ controlRef, disabled, orientation }),
      [disabled, orientation, controlRef],
    )

    return (
      <ResizableContext value={context}>
        <styled.div {...getRootProps({ ref: rootRef })}>
          <PanelGroup {...getGroupProps()}>{children}</PanelGroup>
        </styled.div>
      </ResizableContext>
    )
  },
  "root",
  { transferProps: ["orientation"] },
)()

const StyledPanel = styled(Panel, { forwardProps: ["order"] })

export interface ResizableItemProps
  extends Omit<HTMLStyledPropsWithoutAs, "order">,
    UseResizableItemProps {}

export const ResizableItem = withContext<"div", ResizableItemProps>((props) => {
  const { getItemProps } = useResizableItem(props)

  return <StyledPanel {...getItemProps()} />
}, "item")()

export interface ResizableTriggerProps
  extends HTMLStyledPropsWithoutAs,
    UseResizableTriggerProps {
  /**
   * The resizable trigger icon to use.
   */
  icon?: ReactElement
  /**
   * Props for resizable trigger icon component.
   */
  iconProps?: HTMLStyledProps
}

export const ResizableTrigger = withContext<"div", ResizableTriggerProps>(
  ({ children, icon, iconProps, ...rest }) => {
    const { getIconProps, getTriggerProps } = useResizableTrigger(rest)

    return (
      <styled.div as={PanelResizeHandle} {...getTriggerProps()}>
        {icon ? (
          <ResizableIcon {...getIconProps(iconProps)}>{icon}</ResizableIcon>
        ) : null}

        {children}
      </styled.div>
    )
  },
  "trigger",
)()

interface ResizableIconProps extends HTMLStyledProps {}

const ResizableIcon = withContext<"div", ResizableIconProps>("div", "icon")()

"use client"

import type { MouseEvent, MouseEventHandler, RefObject } from "react"
import type {
  ImperativePanelGroupHandle,
  ImperativePanelHandle,
  PanelGroupOnLayout,
  PanelGroupProps,
  PanelGroupStorage,
  PanelProps,
  PanelResizeHandleProps,
} from "react-resizable-panels"
import type {
  HTMLProps,
  HTMLRefAttributes,
  Orientation,
  PropGetter,
} from "@yamada-ui/react"
import { useCallback, useId, useLayoutEffect, useRef, useState } from "react"
import {
  getPanelElement,
  getPanelGroupElement,
  getResizeHandleElement,
} from "react-resizable-panels"
import {
  assignRef,
  createContext,
  dataAttr,
  fnAll,
  handlerAll,
  mergeRefs,
} from "@yamada-ui/react"

interface ResizableContext
  extends Omit<UseResizableReturn, "getGroupProps" | "getRootProps"> {}

const [ResizableContext, useResizableContext] = createContext<ResizableContext>(
  {
    name: "ResizableContext",
  },
)

export { ResizableContext, useResizableContext }

export interface ResizableStorage extends PanelGroupStorage {}
export interface ResizableGroupControl extends ImperativePanelGroupHandle {}
export interface ResizableItemControl extends ImperativePanelHandle {}

interface ResizableGroupProps
  extends Omit<Partial<PanelGroupProps>, "tagName"> {
  /**
   * Ref of the resizable group callback.
   */
  ref?: RefObject<ResizableGroupControl>
  /**
   * The HTML element to render.
   */
  as?: keyof HTMLElementTagNameMap
}
interface ResizableItemProps extends Omit<PanelProps, "tagName"> {
  /**
   * Ref of the resizable item callback.
   */
  ref?: RefObject<ResizableItemControl>
  /**
   * The HTML element to render.
   */
  as?: keyof HTMLElementTagNameMap
}
interface ResizableTriggerProps
  extends Omit<PanelResizeHandleProps, "tagName">,
    HTMLRefAttributes {
  /**
   * The HTML element to render.
   */
  as?: keyof HTMLElementTagNameMap
}

export interface UseResizableProps extends HTMLProps {
  /**
   * Ref of the resizable item callback.
   */
  controlRef?: RefObject<null | ResizableGroupControl>
  /**
   * If `true`, the resizable trigger will be disabled.
   */
  disabled?: boolean
  /**
   * Unit to resize by keyboard operation.
   *
   * @default 10
   */
  keyboardStep?: number
  /**
   * The orientation of the resizable.
   *
   * @default 'horizontal'
   */
  orientation?: Orientation
  /**
   * A callback that gets and sets a value in custom storage.
   */
  storage?: PanelGroupStorage
  /**
   * Key of value saved in storage.
   * By default, it is saved to `local storage`.
   */
  storageKey?: string
  /**
   * Props for resizable component.
   */
  groupProps?: ResizableGroupProps
  /**
   * The callback invoked when resizable items are resized.
   */
  onLayout?: PanelGroupOnLayout
}

export const useResizable = ({
  id,
  ref,
  controlRef: controlRefProp,
  disabled,
  keyboardStep,
  orientation = "horizontal",
  storage,
  storageKey,
  groupProps = {},
  onLayout,
  ...rest
}: UseResizableProps = {}) => {
  const controlRef = useRef<ResizableGroupControl>(null)
  const uuid = useId()

  id ??= uuid

  const getRootProps: PropGetter = useCallback(
    (props = {}) => ({ ...rest, ...props }),
    [rest],
  )

  const getGroupProps: PropGetter<
    ResizableGroupProps,
    undefined,
    PanelGroupProps
  > = useCallback(
    ({ ref, ...props } = {}) => ({
      id,
      autoSaveId: storageKey,
      direction: orientation,
      keyboardResizeBy: keyboardStep,
      storage,
      ...groupProps,
      ...props,
      ref: mergeRefs(ref, controlRefProp, controlRef),
      tagName: props.as ?? groupProps.as,
      onLayout: fnAll(props.onLayout, groupProps.onLayout, onLayout),
    }),
    [
      id,
      orientation,
      groupProps,
      controlRefProp,
      storageKey,
      keyboardStep,
      onLayout,
      storage,
    ],
  )

  useLayoutEffect(() => {
    const el = getPanelGroupElement(id) as HTMLDivElement | null

    assignRef(ref, el)
  }, [ref, id])

  return {
    controlRef,
    disabled,
    orientation,
    getGroupProps,
    getRootProps,
  }
}

export type UseResizableReturn = ReturnType<typeof useResizable>

export interface UseResizableItemProps
  extends HTMLProps,
    Pick<ResizableItemProps, "as"> {
  /**
   * The collapsed size of the resizable item.
   */
  collapsedSize?: number
  /**
   * If `true`, the resizable item can be collapsed.
   *
   * @default false
   */
  collapsible?: boolean
  /**
   * Ref of the resizable item callback.
   */
  controlRef?: RefObject<null | ResizableItemControl>
  /**
   * The initial size of the resizable item.
   */
  defaultSize?: number
  /**
   * The maximum allowed value of the resizable item.
   */
  maxSize?: number
  /**
   * The minimum allowed value of the resizable item.
   */
  minSize?: number
  /**
   * Order for the resizable item.
   */
  order?: number
  /**
   * The callback invoked when resizable item are collapsed.
   */
  onCollapse?: () => void
  /**
   * The callback invoked when resizable item are expanded.
   */
  onExpand?: () => void
  /**
   * The callback invoked when resizable item are resized.
   */
  onResize?: (size: number, prevSize: number | undefined) => void
}

export const useResizableItem = ({
  id,
  ref,
  as,
  controlRef,
  onCollapse,
  onExpand,
  onResize,
  ...rest
}: UseResizableItemProps) => {
  const uuid = useId()

  id ??= uuid

  const getItemProps: PropGetter<ResizableItemProps, undefined, PanelProps> =
    useCallback(
      ({ ref, ...props } = {}) => ({
        id,
        ref: mergeRefs(ref, controlRef),
        tagName: props.as ?? as,
        ...props,
        ...rest,
        onCollapse: fnAll(props.onCollapse, onCollapse),
        onExpand: fnAll(props.onExpand, onExpand),
        onResize: fnAll(props.onResize, onResize),
      }),
      [as, controlRef, id, onCollapse, onExpand, onResize, rest],
    )

  useLayoutEffect(() => {
    const el = getPanelElement(id) as HTMLDivElement | null

    assignRef(ref, el)
  }, [ref, id])

  return {
    getItemProps,
  }
}

export type UseResizableItemReturn = ReturnType<typeof useResizableItem>

export interface UseResizableTriggerProps
  extends HTMLProps,
    Pick<ResizableTriggerProps, "as"> {
  /**
   * If `true`, the resizable trigger will be disabled.
   *
   * @default false
   */
  disabled?: boolean
  /**
   * The callback invoked when resizable trigger are dragged.
   */
  onDragging?: (dragging: boolean) => void
}

export const useResizableTrigger = ({
  id,
  ref,
  as,
  disabled,
  onDragging,
  ...rest
}: UseResizableTriggerProps) => {
  const uuid = useId()
  const {
    controlRef,
    disabled: groupDisabled,
    orientation,
  } = useResizableContext()
  const [active, setActive] = useState<boolean>(false)
  const trulyDisabled = disabled || groupDisabled

  id ??= uuid

  const onDoubleClick = useCallback(
    (ev: MouseEvent<HTMLDivElement>) => {
      ev.preventDefault()

      const layout = controlRef.current?.getLayout()

      if (!layout) return

      const count = layout.length
      const size = 100 / count
      const nextLayout = layout.map(() => size)

      controlRef.current?.setLayout(nextLayout)
    },
    [controlRef],
  )

  const getTriggerProps: PropGetter<
    ResizableTriggerProps,
    undefined,
    PanelResizeHandleProps
  > = useCallback(
    (props = {}) =>
      ({
        id,
        "aria-orientation": orientation,
        "data-active": dataAttr(active),
        "data-disabled": dataAttr(trulyDisabled),
        disabled: trulyDisabled,
        tabIndex: trulyDisabled ? -1 : 0,
        tagName: props.as ?? as,
        ...rest,
        ...props,
        onDoubleClick: handlerAll(
          props.onDoubleClick as MouseEventHandler<HTMLDivElement> | undefined,
          rest.onDoubleClick,
          onDoubleClick,
        ),
        onDragging: fnAll(props.onDragging, onDragging, setActive),
      }) as unknown as PanelResizeHandleProps,
    [
      id,
      as,
      orientation,
      trulyDisabled,
      rest,
      onDoubleClick,
      onDragging,
      active,
    ],
  )

  const getIconProps: PropGetter = useCallback(
    (props = {}) => ({
      "data-active": dataAttr(active),
      "data-icon": "",
      ...props,
    }),
    [active],
  )

  useLayoutEffect(() => {
    const el = getResizeHandleElement(id) as HTMLDivElement | null

    assignRef(ref, el)
  }, [ref, id])

  return {
    getIconProps,
    getTriggerProps,
  }
}

export type UseResizableTriggerReturn = ReturnType<typeof useResizableTrigger>
