import { defineComponentStyle } from "@yamada-ui/react"

export const separatorStyle = defineComponentStyle({
  base: {
    borderColor: "border",
  },

  props: {
    /**
     * The orientation of the divider.
     *
     * @default 'horizontal'
     */
    orientation: {
      horizontal: {
        borderWidth: "0 0 1px 0",
        w: "full",
      },
      vertical: {
        borderWidth: "0 0 0 1px",
        h: "full",
      },
    },
  },

  variants: {
    dashed: {
      borderStyle: "dashed",
    },
    dotted: {
      borderStyle: "dotted",
    },
    solid: {
      borderStyle: "solid",
    },
  },

  defaultProps: { variant: "solid", orientation: "horizontal" },
})

export type SeparatorStyle = typeof separatorStyle

"use client"

import type { HTMLStyledProps, ThemeProps } from "@yamada-ui/react"
import type { SeparatorStyle } from "./separator.style"
import { createComponent } from "@yamada-ui/react"
import { separatorStyle } from "./separator.style"

export interface SeparatorProps
  extends HTMLStyledProps<"hr">,
    ThemeProps<SeparatorStyle> {}

const {
  PropsContext: SeparatorPropsContext,
  usePropsContext: useSeparatorPropsContext,
  withContext,
} = createComponent<SeparatorProps, SeparatorStyle>("separator", separatorStyle)

export { SeparatorPropsContext, useSeparatorPropsContext }

/**
 * `Separator` is a component that represents a division between elements.
 *
 * @see https://yamada-ui.com/components/separator
 */
export const Separator = withContext("hr")()
