import { defineComponentSlotStyle, focusRingStyle } from "@yamada-ui/react"

export const switchStyle = defineComponentSlotStyle({
  base: {
    label: {
      userSelect: "none",
    },
    root: {
      alignItems: "center",
      cursor: "pointer",
      display: "inline-flex",
      gap: "2",
      _readOnly: {
        layerStyle: "readOnly",
        pointerEvents: "none",
      },
      _disabled: {
        layerStyle: "disabled",
      },
    },
    thumb: {
      alignItems: "center",
      bg: "white",
      display: "inline-flex",
      justifyContent: "center",
      position: "absolute",
      rounded: "inherit",
      transitionDuration: "fast",
      transitionProperty: "background, transform",
      _checked: {
        transform: "translateX(100%)",
      },
    },
    track: {
      "--focus-ring-color": "{colorScheme.outline}",
      alignItems: "center",
      bg: "bg.muted",
      boxSizing: "content-box",
      display: "inline-flex",
      flexShrink: 0,
      position: "relative",
      rounded: "full",
      transitionDuration: "fast",
      transitionProperty: "background",
      _peerFocusVisible: {
        ...focusRingStyle.outline,
      },
    },
  },

  variants: {
    thin: {
      thumb: {
        boxShadow: "2xl",
        _checked: {
          bg: "colorScheme.solid",
        },
      },
      track: {
        "--focus-ring-offset": "4px",
        _checked: {
          bg: ["colorScheme.muted", "colorScheme.subtle"],
        },
      },
    },
    thick: {
      thumb: {
        _checked: {
          bg: "colorScheme.contrast",
        },
      },
      track: {
        p: "1",
        _checked: {
          bg: "colorScheme.solid",
        },
      },
    },
  },

  sizes: {
    sm: {
      label: { fontSize: "sm" },
      thumb: { boxSize: "3" },
      track: { w: "6" },
    },
    md: {
      label: { fontSize: "md" },
      thumb: { boxSize: "4" },
      track: { w: "8" },
    },
    lg: {
      label: { fontSize: "lg" },
      thumb: { boxSize: "5" },
      track: { w: "10" },
    },
  },

  props: {
    /**
     * Change switch label from right to left.
     *
     * @default false
     */
    reverse: {
      false: {
        root: { flexDirection: "row" },
      },
      true: {
        root: { flexDirection: "row-reverse" },
      },
    },
    /**
     * The shape of the switch.
     *
     * @default circle
     */
    shape: {
      circle: {
        track: { rounded: "full" },
      },
      rounded: {
        thumb: { rounded: "l1" },
        track: { rounded: "l2" },
      },
      square: {
        track: { rounded: "0" },
      },
    },
  },

  compounds: [
    {
      css: {
        track: { h: "3" },
      },
      size: "sm",
      variant: "thick",
    },
    {
      css: {
        track: { h: "4" },
      },
      size: "md",
      variant: "thick",
    },
    {
      css: {
        track: { h: "5" },
      },
      size: "lg",
      variant: "thick",
    },
    {
      css: {
        track: { h: "2" },
      },
      size: "sm",
      variant: "thin",
    },
    {
      css: {
        track: { h: "3" },
      },
      size: "md",
      variant: "thin",
    },
    {
      css: {
        track: { h: "4" },
      },
      size: "lg",
      variant: "thin",
    },
  ],

  defaultProps: {
    size: "md",
    variant: "thick",
    shape: "circle",
  },
})

export type SwitchStyle = typeof switchStyle

"use client"

import type { ReactNode } from "react"
import type { HTMLStyledProps, ThemeProps, Merge } from "@yamada-ui/react"
import type { SwitchStyle } from "./switch.style"
import type { UseSwitchProps } from "./use-switch"
import { createSlotComponent, styled, isObject } from "@yamada-ui/react"
import { switchStyle } from "./switch.style"
import { useSwitch } from "./use-switch"

export interface SwitchProps
  extends Merge<HTMLStyledProps<"label">, UseSwitchProps>,
    ThemeProps<SwitchStyle> {
  /**
   * The switch icon to use.
   */
  icon?: ReactNode | { off: ReactNode; on: ReactNode }
  /**
   * Props for switch input element.
   */
  inputProps?: HTMLStyledProps<"input">
  /**
   * Props for switch label element.
   */
  labelProps?: HTMLStyledProps<"span">
}

const {
  PropsContext: SwitchPropsContext,
  usePropsContext: useSwitchPropsContext,
  withContext,
  withProvider,
} = createSlotComponent<SwitchProps, SwitchStyle>("switch", switchStyle)

export { SwitchPropsContext, useSwitchPropsContext }

/**
 * `Switch` is a component used to toggle between on and off states.
 *
 * @see https://yamada-ui.com/docs/components/switch
 */
export const Switch = withProvider<"input", SwitchProps>(
  ({ children, icon, inputProps, labelProps, ...props }) => {
    const {
      checked,
      getInputProps,
      getLabelProps,
      getRootProps,
      getThumbProps,
      getTrackProps,
    } = useSwitch(props)

    return (
      <styled.label {...getRootProps()}>
        <styled.input data-peer {...getInputProps(inputProps)} />

        <SwitchTrack {...getTrackProps()}>
          <SwitchThumb {...getThumbProps()}>
            {icon && isObject(icon) && "on" in icon
              ? checked
                ? icon.on
                : icon.off
              : icon}
          </SwitchThumb>
        </SwitchTrack>

        {children ? (
          <SwitchLabel {...getLabelProps(labelProps)}>{children}</SwitchLabel>
        ) : null}
      </styled.label>
    )
  },
  "root",
)()

interface SwitchThumbProps extends HTMLStyledProps {}

const SwitchThumb = withContext<"div", SwitchThumbProps>("div", "thumb")()

interface SwitchTrackProps extends HTMLStyledProps {}

const SwitchTrack = withContext<"div", SwitchTrackProps>("div", "track")()

interface SwitchLabelProps extends HTMLStyledProps<"span"> {}

const SwitchLabel = withContext<"span", SwitchLabelProps>("span", "label")()

"use client"

import type {
  ChangeEvent,
  ChangeEventHandler,
  FocusEventHandler,
  KeyboardEvent,
} from "react"
import type { HTMLProps, HTMLRefAttributes, PropGetter } from "@yamada-ui/react"
import type { FieldProps } from "../field"
import { useCallback, useId, useRef } from "react"
import { useControllableState } from "../../hooks/use-controllable-state"
import {
  dataAttr,
  handlerAll,
  mergeRefs,
  visuallyHiddenAttributes,
} from "@yamada-ui/react"
import { useFieldProps } from "../field"

export interface UseSwitchProps<Y extends number | string = string>
  extends Omit<HTMLProps<"label">, "onBlur" | "onChange" | "onFocus" | "ref">,
    HTMLRefAttributes<"input">,
    FieldProps {
  /**
   * id assigned to input.
   */
  id?: string
  /**
   * The HTML `name` attribute used for forms.
   */
  name?: string
  /**
   * If `true`, the switch will be checked.
   *
   * @default false
   */
  checked?: boolean
  /**
   * If `true`, the switch will be checked when the Enter key is pressed.
   *
   * @default true
   */
  checkOnEnter?: boolean
  /**
   * If `true`, the switch will be initially checked.
   *
   * @default false
   */
  defaultChecked?: boolean
  /**
   * The tab-index property of the underlying input element.
   */
  tabIndex?: number
  /**
   * The value to be used in the switch input.
   */
  value?: Y
  /**
   * The callback invoked when the switch is blurred.
   */
  onBlur?: FocusEventHandler<HTMLInputElement>
  /**
   * The callback invoked when the checked state changes.
   */
  onChange?: ChangeEventHandler<HTMLInputElement>
  /**
   * The callback invoked when the switch is focused.
   */
  onFocus?: FocusEventHandler<HTMLInputElement>
}

export const useSwitch = <Y extends number | string = string>(
  props: UseSwitchProps<Y> = {},
) => {
  const uuid = useId()
  const {
    props: {
      id = uuid,
      ref,
      name,
      checked: checkedProp,
      checkOnEnter = true,
      defaultChecked = false,
      disabled,
      readOnly,
      required,
      value,
      onChange: onChangeProp,
      ...rest
    },
    ariaProps,
    dataProps,
    eventProps,
  } = useFieldProps(props)
  const interactive = !(readOnly || disabled)
  const inputRef = useRef<HTMLInputElement>(null)
  const [checked, setChecked] = useControllableState({
    defaultValue: defaultChecked,
    value: checkedProp,
  })

  const onChange = useCallback(
    (ev: ChangeEvent<HTMLInputElement>) => {
      if (!interactive) return ev.preventDefault()

      setChecked(ev.target.checked)
      onChangeProp?.(ev)
    },
    [onChangeProp, setChecked, interactive],
  )

  const onKeyDown = useCallback(
    (ev: KeyboardEvent<HTMLInputElement>) => {
      if (interactive && checkOnEnter && ev.key === "Enter")
        inputRef.current?.click()
    },
    [interactive, checkOnEnter],
  )

  const getRootProps: PropGetter<"label"> = useCallback(
    (props = {}) => ({
      ...dataProps,
      htmlFor: id,
      "data-checked": dataAttr(checked),
      ...rest,
      ...props,
    }),
    [checked, rest, id, dataProps],
  )

  const getTrackProps: PropGetter = useCallback(
    (props = {}) => ({
      "data-checked": dataAttr(checked),
      ...dataProps,
      ...props,
    }),
    [checked, dataProps],
  )

  const getThumbProps: PropGetter = useCallback(
    (props = {}) => ({
      "data-checked": dataAttr(checked),
      ...dataProps,
      ...props,
    }),
    [checked, dataProps],
  )

  const getInputProps: PropGetter<"input"> = useCallback(
    (props = {}) => ({
      ...ariaProps,
      ...dataProps,
      id,
      type: "checkbox",
      name,
      style: visuallyHiddenAttributes.style,
      checked,
      disabled,
      readOnly,
      required,
      role: "switch",
      tabIndex: interactive ? 0 : -1,
      value,
      ...props,
      ref: mergeRefs(inputRef, props.ref, ref),
      onBlur: handlerAll(eventProps.onBlur, props.onBlur),
      onChange: handlerAll(props.onChange, onChange),
      onFocus: handlerAll(eventProps.onFocus, props.onFocus),
      onKeyDown: handlerAll(props.onKeyDown, onKeyDown),
    }),
    [
      ariaProps,
      dataProps,
      id,
      name,
      checked,
      disabled,
      readOnly,
      required,
      interactive,
      value,
      ref,
      eventProps,
      onChange,
      onKeyDown,
    ],
  )

  const getLabelProps: PropGetter<"span"> = useCallback(
    (props = {}) => ({
      ...dataProps,
      "data-checked": dataAttr(checked),
      ...props,
    }),
    [dataProps, checked],
  )

  return {
    checked,
    getInputProps,
    getLabelProps,
    getRootProps,
    getThumbProps,
    getTrackProps,
  }
}

export type UseSwitchReturn = ReturnType<typeof useSwitch>
