"use client"

import type { ReactElement } from "react"
import type {
  GenericsComponent,
  ThemeProps,
  WithoutThemeProps,
} from "@yamada-ui/react"
import type { GroupProps } from "../group"
import type { ToggleProps } from "./toggle"
import type { ToggleStyle } from "./toggle.style"
import type { UseToggleGroupProps } from "./use-toggle-group"
import { useMemo } from "react"
import { Group } from "../group"
import { component, TogglePropsContext, useRootComponentProps } from "./toggle"
import { ToggleGroupContext, useToggleGroup } from "./use-toggle-group"

/**
 * `ToggleGroup` is a set of two-state buttons that can be toggled on or off.
 *
 * @see https://yamada-ui.com/docs/components/toggle
 */
export interface ToggleGroupRootProps<Y extends string | string[] = string>
  extends Omit<
      WithoutThemeProps<GroupProps, ToggleStyle>,
      "defaultValue" | "onChange" | "value"
    >,
    UseToggleGroupProps<Y>,
    ThemeProps<ToggleStyle> {}

export const ToggleGroupRoot = component<"div", ToggleGroupRootProps>(
  (props: ToggleGroupRootProps) => {
    const [, { colorScheme, size, variant, attached, ...rest }] =
      useRootComponentProps(props, "group", {
        transferProps: ["size", "variant", "colorScheme"],
      })
    const {
      disabled,
      readOnly,
      value,
      getGroupProps,
      onChange,
      onChangeMapRef,
    } = useToggleGroup(rest)
    const context = useMemo<ToggleProps>(
      () => ({
        colorScheme,
        size,
        variant,
        focusVisibleRing: attached ? "inside" : undefined,
      }),
      [variant, size, colorScheme, attached],
    )
    const groupContext = useMemo(
      () => ({
        disabled,
        readOnly,
        value,
        onChange,
        onChangeMapRef,
      }),
      [value, disabled, readOnly, onChange, onChangeMapRef],
    )

    return (
      <TogglePropsContext value={context}>
        <ToggleGroupContext value={groupContext}>
          <Group attached={attached} {...getGroupProps()} />
        </ToggleGroupContext>
      </TogglePropsContext>
    )
  },
  "group",
)() as GenericsComponent<{
  <Y extends string | string[] = string>(
    props: ToggleGroupRootProps<Y>,
  ): ReactElement
}>

import { defineComponentSlotStyle } from "@yamada-ui/react"
import { iconButtonStyle } from "../button"

export const toggleStyle = defineComponentSlotStyle({
  base: {
    group: {},
    root: {
      "--error-border-color": "colors.border.error",
      gap: "2",
    },
  },

  props: {
    /**
     * If true, the toggle button is full rounded. Else, it'll be slightly round.
     *
     * @default false
     */
    fullRounded: {
      true: {
        root: {
          rounded: "full",
        },
      },
    },
  },

  variants: {
    ghost: {
      root: {
        _checked: {
          layerStyle: "solid",
          _invalid: {
            borderColor: "{error-border-color}",
            borderWidth: "1px",
          },
        },
        _notChecked: {
          layerStyle: "ghost",
          _hover: {
            layerStyle: "ghost.hover",
          },
          _invalid: {
            borderColor: "{error-border-color}",
            borderWidth: "1px",
          },
        },
      },
    },
    outline: {
      root: {
        _checked: {
          layerStyle: "solid",
          borderColor: "colorScheme.solid",
          borderWidth: "1px",
          _invalid: {
            borderColor: "{error-border-color}",
          },
        },
        _notChecked: {
          layerStyle: "outline",
          _hover: {
            layerStyle: "outline.hover",
          },
          _invalid: {
            borderColor: "{error-border-color}",
          },
        },
      },
    },
    solid: {
      root: {
        _checked: {
          layerStyle: "subtle",
          _invalid: {
            borderColor: "{error-border-color}",
            borderWidth: "1px",
          },
        },
        _notChecked: {
          layerStyle: "solid",
          _hover: {
            layerStyle: "solid.hover",
          },
          _invalid: {
            borderColor: "{error-border-color}",
            borderWidth: "1px",
          },
        },
      },
    },
    subtle: {
      root: {
        _checked: {
          layerStyle: "solid",
          _invalid: {
            borderColor: "{error-border-color}",
            borderWidth: "1px",
          },
        },
        _notChecked: {
          layerStyle: "subtle",
          _hover: {
            layerStyle: "subtle.hover",
          },
          _invalid: {
            borderColor: "{error-border-color}",
            borderWidth: "1px",
          },
        },
      },
    },
    surface: {
      root: {
        _checked: {
          layerStyle: "solid",
          _invalid: {
            borderColor: "{error-border-color}",
          },
        },
        _notChecked: {
          layerStyle: "surface",
          _hover: {
            layerStyle: "surface.hover",
          },
          _invalid: {
            borderColor: "{error-border-color}",
          },
        },
      },
    },
  },

  sizes: {
    xs: {
      root: {
        ...iconButtonStyle.sizes?.xs,
        gap: "1",
      },
    },
    sm: {
      root: {
        ...iconButtonStyle.sizes?.sm,
        gap: "2",
      },
    },
    md: {
      root: {
        ...iconButtonStyle.sizes?.md,
        gap: "2",
      },
    },
    lg: {
      root: {
        ...iconButtonStyle.sizes?.lg,
        gap: "2.5",
      },
    },
    xl: {
      root: {
        ...iconButtonStyle.sizes?.xl,
        gap: "3",
      },
    },
    "2xl": {
      root: {
        ...iconButtonStyle.sizes?.["2xl"],
        gap: "3",
      },
    },
  },

  defaultProps: {
    size: "md",
    variant: "ghost",
  },
})

export type ToggleStyle = typeof toggleStyle

"use client"

import type { ReactElement } from "react"
import type {
  GenericsComponent,
  ThemeProps,
  WithoutThemeProps,
} from "@yamada-ui/react"
import type { IconButtonProps } from "../button"
import type { UseInputBorderProps } from "../input"
import type { ToggleStyle } from "./toggle.style"
import type { UseToggleProps } from "./use-toggle"
import { createSlotComponent, styled } from "@yamada-ui/react"
import { IconButton } from "../button"
import { useInputBorder } from "../input"
import { Portal } from "../portal"
import { toggleStyle } from "./toggle.style"
import { useToggle } from "./use-toggle"

export interface ToggleProps<Y extends string = string>
  extends Omit<
      WithoutThemeProps<IconButtonProps, ToggleStyle>,
      "aria-label" | "onChange" | "ref" | "value"
    >,
    UseToggleProps<Y>,
    Pick<UseInputBorderProps, "errorBorderColor">,
    ThemeProps<ToggleStyle> {}

const {
  component,
  PropsContext: TogglePropsContext,
  usePropsContext: useTogglePropsContext,
  withProvider,
  useRootComponentProps,
} = createSlotComponent<ToggleProps, ToggleStyle>("toggle", toggleStyle)

export {
  component,
  TogglePropsContext,
  useRootComponentProps,
  useTogglePropsContext,
}

/**
 * `Toggle` is a two-state button that can be either on or off.
 *
 * @see https://yamada-ui.com/docs/components/toggle
 */
export const Toggle = withProvider<"button", ToggleProps>(
  ({ errorBorderColor, icon, ...rest }: ToggleProps) => {
    const { getButtonProps, getInputProps } = useToggle(rest)
    const varProps = useInputBorder({ errorBorderColor })

    return (
      <>
        <Portal>
          <styled.input {...getInputProps()} />
        </Portal>

        <IconButton icon={icon} {...varProps} {...getButtonProps()} />
      </>
    )
  },
  "root",
)() as GenericsComponent<{
  <Y extends string = string>(props: ToggleProps<Y>): ReactElement
}>

"use client"

import type { RefObject } from "react"
import type { HTMLProps, PropGetter } from "@yamada-ui/react"
import { useCallback, useRef } from "react"
import { useControllableState } from "../../hooks/use-controllable-state"
import {
  createContext,
  dataAttr,
  isArray,
  isUndefined,
  toArray,
  useUpdateEffect,
} from "@yamada-ui/react"

interface ToggleGroupContext {
  onChangeMapRef: RefObject<Map<string, (value: boolean) => void>>
  disabled?: boolean
  readOnly?: boolean
  value?: string | string[]
  onChange?: <Y extends string = string>(value: undefined | Y) => void
}

const [ToggleGroupContext, useToggleGroupContext] =
  createContext<ToggleGroupContext>({
    name: "ToggleGroupContext",
    strict: false,
  })

export { ToggleGroupContext, useToggleGroupContext }

export interface UseToggleGroupProps<Y extends string | string[] = string>
  extends Omit<HTMLProps, "defaultValue" | "onChange" | "value"> {
  /**
   * The initial value of the toggle button group.
   */
  defaultValue?: Y extends any[] ? Y : undefined | Y
  /**
   * If `true`, all wrapped toggle button will be disabled.
   *
   * @default false
   */
  disabled?: boolean
  /**
   * If `true`, all wrapped toggle button will be readonly.
   *
   * @default false
   */
  readOnly?: boolean
  /**
   * The value of the toggle button group.
   */
  value?: Y extends any[] ? Y : undefined | Y
  /**
   * The callback fired when any children toggle button is selected or unselected.
   */
  onChange?: (value: Y extends any[] ? Y : undefined | Y) => void
}

export const useToggleGroup = <Y extends string | string[] = string>({
  defaultValue,
  disabled,
  readOnly,
  value: valueProp,
  onChange: onChangeProp,
  ...rest
}: UseToggleGroupProps<Y>) => {
  type Value = Y extends any[] ? Y : undefined | Y

  const onChangeMapRef = useRef<Map<string, (value: boolean) => void>>(
    new Map(),
  )
  const [value, setValue] = useControllableState<Value>({
    defaultValue,
    value: valueProp,
    onChange: onChangeProp,
  })

  const onReset = useCallback((targetValue?: string[]) => {
    onChangeMapRef.current.forEach((onChange, value) => {
      if (targetValue?.includes(value)) {
        onChange(true)
      } else {
        onChange(false)
      }
    })
  }, [])

  const onChange = useCallback(
    <M extends string = Y extends any[] ? Y[number] : Y>(
      value: M | undefined,
    ) => {
      if (isUndefined(value)) return

      setValue((prev) => {
        if (isArray(prev)) {
          const included = prev.includes(value)

          if (included) {
            const nextValue = prev.filter((prevValue) => prevValue !== value)

            onReset(nextValue)

            return nextValue as Value
          } else {
            const nextValue = [...prev, value]

            onReset(nextValue)

            return nextValue as Value
          }
        } else {
          if (value === (prev as string | undefined)) {
            onReset()

            return undefined as Value
          } else {
            onReset([value])

            return value as unknown as Value
          }
        }
      })
    },
    [setValue, onReset],
  )

  useUpdateEffect(() => {
    onReset(!isUndefined(valueProp) ? toArray(valueProp) : undefined)

    setValue(valueProp as Value)
  }, [valueProp])

  const getGroupProps: PropGetter = useCallback(
    (props) => ({
      "data-disabled": dataAttr(disabled),
      "data-readonly": dataAttr(readOnly),
      role: "group",
      ...rest,
      ...props,
    }),
    [disabled, readOnly, rest],
  )

  return { disabled, readOnly, value, getGroupProps, onChange, onChangeMapRef }
}

export type UseToggleGroupReturn = ReturnType<typeof useToggleGroup>
